css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("boards.fightingamphibians.org") {

body{ background-image: url('http://imgur.com/OcIwG.jpg') !important;
     background-attachment: fixed !important;
     background: -moz-linear-gradient(450deg,rgba(0,0,0,.3), rgba(1000,400,1000,.65)); /* Add !important before the ; to use this gradient */
     color: #000 !important;
     font-family: Sans !important;
     font-size: 14px !important;
     height: 100% !important;
     margin: 0 auto !important;
     padding: 0;
}
form {
     width: 1100px !important;
     margin: 0 auto !important;
     padding: 25px 5px 5px;
}
div.logo {
     visibility: hidden;
     position: absolute;
     left: -700px;
     right: 0;
}

span.r1{font-size:30px;text-decoration:none;font-weight:bold;}.highlight{background:#fff!important;width:1100px!important;margin-right:auto!important;-moz-border-radius:17px!important;-webkit-border-radius:17px!important;color:#37DF6D!important;padding:10px!important;}td ul, td li{background:transparent!important;color:#fff!important;}ul,li,li.tab{background:#fff!important;border:0!important;color:#08B;padding:0px;-moz-border-radius:10px!important;-webkit-border-radius:10px!important;}h1,h2,h3{background:transparent!important;color:#fff!important;font-weight:bold!important;font-size:15px!important;}div.content{background:#fff!important;-moz-border-radius:17px!important;-webkit-border-radius:17px!important;padding:10px!important;}span.plus{background:#fff!important}span.newssub{background:transparent!important;width:75%!important;color:#08B!important;font-weight:bold!important;text-shadow: #07A 0 0 1px !important;}

div.navbar {
     font: 10px verdana !important;
     border-bottom: 1px solid #555;
     top: 0;
     position: fixed;
     width: 100% !important;
     padding: 4px !important;
     background: #222 !important;
     color: #89A !important;
     text-align: center !important;
}
td.reply, div#backwash_tooltip {
     -moz-border-radius: 15px !important;
     -webkit-border-radius: 15px !important;
     color: #000 !important;
     padding: 10px !important;
     border: none !important;
}
td.reply {
     width: 1100px !important;
     border: 0 !important;
     color: #000 !important;
     background: rgba(255, 255, 255, 0.9) !important;
}
blockquote {
     width: auto !important;
     margin-right: auto !important;
     -moz-border-radius: 17px !important;
     -webkit-border-radius: 17px !important;
     color: #000 !important;
     padding: 10px !important;
     border: none !important;
     background: rgba(255, 255, 255, 0.9) !important;
}
td blockquote {
     background-color: transparent !important;
}
.unkfunc {
     color: #32CD32 !important;
}
.postername {
     color: #08B !important;
     font-weight: 800 !important;
     font-size: 14px !important;
     background: none !important;
}
.postertrip {
     background: none !important;
     color: #8B0 !important;
}
.omittedposts, .abbr {
     color: #08B !important;
     font-weight: bold !important;
     font-size: 14px !important;
}
span[style="color: rgb(255, 255, 255);"] {
     font-weight: bold !important;
     text-shadow: 0 0 5px rgb(204, 87, 17) !important;
}
.filetitle, .replytitle {
     color: #08B !important;
     background: none !important;
}
img {
     -moz-border-top-right-radius: 3px !important;
     -moz-border-bottom-left-radius: 3px !important;
     -moz-border-top-left-radius: 5px !important;
     -moz-border-bottom-right-radius: 5px !important;
     -moz-box-shadow: 0 0 20px #000 !important;
     -webkit-border-top-right-radius: 3px !important;
     -webkit-border-bottom-left-radius: 3px !important;
     -webkit-border-top-left-radius: 5px !important;
     -webkit-border-bottom-right-radius: 5px !important;
     -webkit-box-shadow: 0 0 20px #000 !important;
}
img:hover {
     -moz-box-shadow: 0 0 30px #000 !important;
     -webkit-box-shadow: 0 0 30px #000 !important;
}
.postblock, .pages {
     background-color: #FFF !important;
     opacity: 0.7 !important;
     -moz-border-radius: 5px !important;
     -webkit-border-radius: 5px !important;
     color: #000 !important;
     font-weight: bold !important;
     border: none !important;
}
input, textarea {
     background-color: #FFF !important;
     color: #000 !important;
     border: 1px solid #000;
}
form hr {
     display: block !important;
}
hr {
     display: none !important;
     height: 1px !important;
     border: none !important;
     background: inherit !important;
}
th, .doubledash {
     display: none !important;
}
* {
     font: 14px verdana !important;
}
table {
     border: 0 !important;
}
td {
     color: #FFF !important;
     border: 0 !important;
}
a:link, a:active, a:visited {
     background-color: transparent !important;
     text-decoration: none !important;
     color: #08B !important;
     text-shadow: #07A 0 0 3px !important;
     font-weight: bold !important;
}
a:hover {
     text-decoration: none !important;
     color: #08B !important;
     font-weight: bold !important;
     text-shadow: #07A 0 0 20px !important;
}
}
