css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userscripts.org") {

/*page-wide: use the whole screen*/
div#footer-content,div.container,div#nav {
margin:auto!important;
width:99%!important;
padding:0px!important}


/*header stuff*/
div#site-logo,div#userbox {margin:4px !important;padding:0px!important;}
h1,p.subtitle,p.subtitle+div.pagination {margin:0px 0px 4px!important;padding:0px!important;}
h1,p.subtitle {display:block;float:left;}
h1 {clear:left;}
/*shift this toward the center*/
p.subtitle {position:relative;left:30%;}
/*put pagination boxes at top and bottom of page in same horizontal location*/
div.pagination{
display:block;
clear:right;
float:right;
margin:0px!important;
padding:0px!important;}



/*search stuff*/
form#script_search {
margin:0px!important;
padding:0px!important;} 

input#script_q {
margin:2px!important;
padding:2px!important;}



/*body stuff*/
div#root {padding-top:0px!important}
th,th.la {padding:0px 2px!important;margin:0px!important;}
th.la>a,th>a {padding:0px!important;margin:0px!important;text-decoration:none!important;}
td {padding:0.4em 0.2em!important;}
p.desc{margin-top:0.1em!important;}
td.inv+td.inv {text-align:right!important}
td.inv+td.inv+td.inv+td.inv+td.inv {text-align:center!important;}



/*footer stuff*/
div#footer, div.col {padding:0px!important;}
div#footer div.col {width:50%!important;}
div.la p,p.credit, p#policies {margin:0.25em 0px !important;}

/*that's all*/
}
