css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.godlikeproductions.com") {

body, html, tbody, table, td, tr, ul, li{
  background:black !important;
  border:0px none !important;
  }

td[align=right] table{
  background:red !important;
  display:none;
  }

td a {
  line-height:20px;
  display:inline-block;
  padding-left:20px;
  padding-right:20px;
  margin-bottom:0px;
  }

td a:visited{
 
}

html body table tbody tr td table tbody tr td b{
  color:green !important;
}

td.sfro a,
td.sfr a {
  border:1px dotted transparent;
  color:#00FF0D !important;
  }

td.sfro a:hover,
td.sfr a:hover {
  background:green !important;
  color:black !important;
  }

td.vfr img, td.vfro img{
  margin-bottom:8px !important;
}

td.vfr, td.vfro{
  color:#BA2FB6 !important;
}

td.sfro a:visited,
td.sfr a:visited{
 color:#4C9152 !important;
}

td.ifr{
  vertical-align:top;
}

td.ifro,
td.sfro,
td.hfro,
td.rfro,
td.vifro,
td.vfro,
td.pfro,
td.mfro,
td.ifr,
td.sfr,
td.hfr,
td.rfr,
td.vifr,
td.vfr,
td.pfr,
td.mfr{
 border-bottom:1px dotted silver !important;
 padding-bottom:0px !important;
 padding-top:10px !important;
}

span.mlinks a{
  border:0px !important;
  margin:0px !important;
  padding:0px !important;
  }

html body table tbody tr td a img{
  display:none;
  }

.Panel{
  width:300px;
  border:0px solid !important;
  border-right:1px dotted silver !important;
  }

.Panel h3{
  background:#639069 !important;
  }

iframe{
  display:none;
  }

a{
  color:red !important;
  text-decoration:underline !important;
  }

td.rightpanel table tbody + tr{
  display:none;
  }

td.sh img,
td.hh img,
td.rh img,
td.vih img,
td.vh img,
td.ph img,
td.mh img{
display:block !important;
margin:0px !important;
padding:0px !important;
  }

td.sh a,
td.hh a,
td.rh a,
td.vih a,
td.vh a,
td.ph a,
td.mh a{
margin:0px !important;
padding:0px !important;
  }

td.ih,
td.sh,
td.hh,
td.rh,
td.vih,
td.vh,
td.ph,
td.mh{
  background:#22602A !important;
  padding:2px !important;
  padding-left:20px !important;
  color:#999 !important;
  font-weight:bold;
  }

html body table tbody tr td div{
display:none;
}

html body table tbody tr td.leftpanel div{
  display:block;
}

table.content tbody tr td.rightpanel table tbody tr td table tbody tr td table{
  padding:0px !important;
  width:105%;
  margin-left:-50px !important;
  height:10px !important;
}

table.content tbody tr td.rightpanel table tbody tr td table tbody tr td table a{
  color:#A737C3!important;
  text-decoration:none !important;
}

html body table tbody tr td table{
  
}

table tbody tr td table.content tbody tr td.rightpanel table tbody tr td a{

}

html body table tbody tr td table.content tbody tr td.rightpanel table tbody tr td font{

}

html body table tbody tr td table.content tbody tr td.rightpanel table tbody tr td font a{
padding:0px !important;
margin:0px !important;
border:0 !important;
}

html body table tbody tr td table.content tbody tr td.rightpanel table tbody tr td table tbody tr td{
  background:green;
}

span.mlinks, span.mlinks {
color:silver !important;
}

span.mlinks, span.mlinks a{
background:black !important;
margin:0px !important;
margin-left:20px !important;
padding:0px !important;
padding-bottom:5px !important;
display:inline-block !important;
opacity:.5;
}

span.mlinks:hover{
  opacity:1;
}

td.rightpanel b a{
 color:yellow !important;
}

td.leftpanel .Panel{
  background:#204B18 !important;
  padding-top:7px;
}

.leftpanel .Panel ul{
  padding-top:10px !important;
  }

.leftpanel .Panel ul li{
  background:#204B18 !important;
  padding-top:2px !important;
  padding-bottom:10px !important;
  margin-top:1px !important;
  margin-bottom:10px !important;
  border-top:1px solid green !important;
  border-bottom:1px solid !important;
  }

.leftpanel .Panel ul li a{
  color:black !important;
  text-decoration:none !important;
}

*{
  font-family:courier;
  font-size:13px !important;
  color:grey !important; 
  }

td.messagecontent table tbody tr td,
td.replycontent table tbody tr td{
  font-size:16px !important;
  color:green !important;
}

td.messagecontent table tbody tr td font b,
td.replycontent table tbody tr td font b{
  color:#33E015 !important;
}

td.messagecontent,
td.messageauthor,
td.replyauthor,
td.replycontent{
padding-top:40px !important;
padding-bottom:40px !important;
margin-bottom:40px !important;
margin-top:40px !important;
border-bottom:1px dotted #efefef !important;
}

td.replyauthor a{
  color:red !important;
  text-align:left !important;
  padding:0px;
  margin-bottom:10px;
}

td.replyauthor b a u,
td.messageauthor b a u{
  color:green !important;
  text-decoration:none !important;
  font-size:20px !important;
}

tr td.msgtitle h1{
  font-size:20px !important;
  font-weight:normal !important;
  color:red !important;
}

.quotei{
  background:yellow !important;
}


}
