css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* GENERAL */
#sidebar-box{
-moz-appearance: none !important;
-moz-border-image: url("data:image/png;base64,")
13 13 15 / 14px 13px 15px repeat stretch !important;
-moz-window-shadow: none !important;
background: none !important;
color: #ffffff !important;
opacity: 0.95 !important;
}

#sidebar-splitter{display:none}

#sidebar-box{position:fixed!important;top:60px;bottom:0px}
#sidebar-header{width:100%;background-color:transparent!important;-moz-appearance:none!important}

#sidebar{height:95%;background-color:transparent!important;width:100%!important}
#bookmarksPanel *,#history-panel *{background-color:transparent!important}
#bookmarksPanel ,#history-panel{background-color:transparent!important}
#bookmarksPanel ,#history-panel{color:grey!important}
#bookmarksPanel *,#history-panel *{color:grey!important}
