css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ve3d.ign.com") {
	#site_liquid_content_back {
		background: #D6D9DE !important;
	}

	.popupmenu ul li:hover ul,
	.popupmenu ul a:hover ul{
		background:#D6D9DE !important;
	}

	.news_title {
		color: #CF7E0C !important;
	}

	h1.article_title {
		color: #CF7E0C !important;
	}

	h1.media_title {
		color: #CF7E0C !important;
	}

	#news_box_body a.news_title:link, #news_box_body a.news_title:visited {
		color: #CF7E0C !important;
	}
	#news_box_body a.news_title:hover, #news_box_body a.news_title:active {
		color: #CF7E0C !important;
	}

	#news_footer a:hover, #news_footer a:active {
		color: #CF7E0C !important;
	}

	#article_box_body a.news_title:link, #article_box_body a.news_title:visited {
		color: #CF7E0C !important;
	}

	#article_box_body a.news_title:hover, #article_box_body a.news_title:active {
		color: #CF7E0C !important;
	}

	#article_box_body a.article_archive_title:link, #article_box_body a.article_archive_title:visited {
		color: #CF7E0C !important;
	}

	#article_box_body a.article_archive_title:hover, #article_box_body a.article_archive_title:active {
		color: #CF7E0C !important;
	}

	#media_box_body a.news_title:link, #media_box_body a.news_title:visited {
		color: #CF7E0C !important;
	}

	#media_box_body a.news_title:hover, #media_box_body a.news_title:active {
		color: #CF7E0C !important;
	}

	#site_full_width_title {
		color: #CF7E0C !important;
	}

	/* news_background.jpg */
	#news_box_body, #article_box_body, #media_box_body {
		background-image:url(http://ve3dmedia.ign.com/files/ve_skins/slate/news_background.jpg) !important;
	}

	/* news_1stpost_background.jpg */
	.news_1stpost, #media_top_header {
		background:url(http://ve3dmedia.ign.com/files/ve_skins/slate/news_1stpost_background.jpg) no-repeat !important;
	}

	.news_post{
		background-color: #D6D9DE !important;
	}

	.news_post_alt{
		background-color: #C3C7D0 !important;
	}

	.news_date {
		color: #616466 !important;
	}

	.news_author {
		color: #616466 !important;
	}

	#news_footer {
		color: #616466 !important;
	}

	#news_footer a:link, #news_footer a:visited {
		color: #616466 !important;
	}

	.article_post {
		background-color: #D6D9DE !important;
	}

	.article_post_alt {
		background-color: #C3C7D0 !important;
	}

	.article_date {
		color: #616466 !important;
	}

	.article_author {
		color: #616466 !important;
	}

	.article_footer {
		color: #616466 !important;
	}

	.comment_post {
		background-color: #D6D9DE !important;
	}

	.comment_post_alt {
		background-color: #C3C7D0 !important;
	}

	.comment_date {
		color: #616466 !important;
	}

	.media_post {
		background-color: #D6D9DE !important;
	}

	.media_date {
		color: #616466 !important;
	}

	.media_author {
		color: #616466 !important;
	}

	.media_footer {
		color: #616466 !important;
	}

	.table_content_row {
		background-color: #C3C7D0 !important;
	}

	.table_content_row_alt {
		background-color: #D6D9DE !important;
	}

	/* news_separator.gif */
	.news_post_separator, .article_post_separator, .comments_separator, .media_post_separator {
		background:url(http://ve3dmedia.ign.com/files/ve_skins/slate/news_separator.gif) no-repeat !important;
	}

	/* comment_separator.jpg */
	#comment_display_styles {
		background:url(http://ve3dmedia.ign.com/files/ve_skins/slate/comment_separator.jpg) no-repeat !important;
	}

	/* comment_box_background.jpg */
	.comment_box {
		background:url(http://ve3dmedia.ign.com/files/ve_skins/slate/comment_box_background.jpg) no-repeat !important;
	}

	/* fullwidth_background.jpg */
	#site_full_width {
		background-image:url(http://ve3dmedia.ign.com/files/ve_skins/slate/fullwidth_background.jpg) !important;
	}

	/* fullwidth_swoosh_background.jpg */
	#site_full_width_body{
		background-image:url(http://ve3dmedia.ign.com/files/ve_skins/slate/fullwidth_swoosh_background.jpg) !important;
	}

	body img[src$="icon_leftarrow.jpg"] {
		width: 0 !important;
		height: 7px !important;
		padding-left: 10px !important;
		background-image:url(http://ve3dmedia.ign.com/files/ve_skins/slate/icon_leftarrow.jpg) !important;
	}

	body img[src$="icon_rightarrow.jpg"] {
		width: 0 !important;
		height: 7px !important;
		padding-left: 10px !important;
		background-image:url(http://ve3dmedia.ign.com/files/ve_skins/slate/icon_rightarrow.jpg) !important;
	}

	body img[src$="icon_uparrow.jpg"] {
		width: 0 !important;
		height: 9px !important;
		padding-left: 7px !important;
		background-image:url(http://ve3dmedia.ign.com/files/ve_skins/slate/icon_uparrow.jpg) !important;
	}

	.comment_post_indent {
		background-image:url(http://ve3dmedia.ign.com/files/ve_skins/slate/indent.jpg) !important;
	}

	#site_liquid_content_left {
		background: url(http://ve3dmedia.ign.com/files/ve_skins/slate/liquid_content_left.jpg) !important;
	}

	#site_liquid_content_right {
		background: url(http://ve3dmedia.ign.com/files/ve_skins/slate/liquid_content_right.jpg) !important;
	}

	body img[src$="liquid_content_left.jpg"] {
		width: 0 !important;
		height: 0 !important;
	}

	body img[src$="liquid_content_right.jpg"] {
		width: 0 !important;
		height: 0 !important;
	}
	
	#resultdiv font{
		color: #616466 !important;
	}
}
