css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ve3d.ign.com") {
	#site_liquid_content_back {
		background: #434955 !important;
		color: #ffffff !important;
	}

	.popupmenu ul li:hover ul,
	.popupmenu ul a:hover ul{
		background:#434955 !important;
	}

	.news_body {
		font: .7em verdana !important;
		color: #ffffff !important;
	}

	#news_wrapper a {
		color: #FFA500 !important;
	}

	#news_wrapper a:visited {
		color: #C08528 !important;
	}

	#article_box_body a {
		color: #FFA500 !important;
	}

	#article_box_body a:visited {
		color: #C08528 !important;
	}

	#site_full_width a {
		color: #FFA500 !important;
	}

	#site_full_width a:visited {
		color: #C08528 !important;
	}

	#site_liquid_content_back a {
		color: #FFA500 !important;
	}

	#site_liquid_content_back a:visited {
		color: #C08528 !important;
	}

	#pager {
		color: #FFA500 !important;
	}

	#media_pager {
		color: #FFA500 !important;
	}

	.news_title {
		color: #FFA500 !important;
	}

	h1.article_title {
		color: #FFA500 !important;
	}

	h1.media_title {
		color: #FFA500 !important;
	}

	#news_box_body a.news_title:link, #news_box_body a.news_title:visited {
		color: #FFA500 !important;
	}
	#news_box_body a.news_title:hover, #news_box_body a.news_title:active {
		color: #FFA500 !important;
	}

	#news_footer a:hover, #news_footer a:active {
		color: #FFA500 !important;
	}

	#article_box_body a.news_title:link, #article_box_body a.news_title:visited {
		color: #FFA500 !important;
	}

	#article_box_body a.news_title:hover, #article_box_body a.news_title:active {
		color: #FFA500 !important;
	}

	#article_box_body a.article_archive_title:link, #article_box_body a.article_archive_title:visited {
		color: #FFA500 !important;
	}

	#article_box_body a.article_archive_title:hover, #article_box_body a.article_archive_title:active {
		color: #FFA500 !important;
	}

	#media_box_body a.news_title:link, #media_box_body a.news_title:visited {
		color: #FFA500 !important;
	}

	#media_box_body a.news_title:hover, #media_box_body a.news_title:active {
		color: #FFA500 !important;
	}


	#Comment_bodytext {
		-moz-appearance: none !important;
		background: #272B36 !important;
		border: 1px solid #000000 !important;
		color: #ffffff !important;
	}

	.short_content_box_body {
		color:#ffffff  !important;
		border: 1px solid #717574  !important;
	}

	.minimum_content_box_body {
		color:#ffffff  !important;
		border: 1px solid #717574  !important;
	}

	/* news_background.jpg */
	#news_box_body, #article_box_body, #media_box_body {
		background-image:url(http://ve3dmedia.ign.com/files/ve_skins/classic/news_background.jpg) !important;
		color: #ffffff !important;
	}

	/* news_1stpost_background.jpg */
	.news_1stpost, #media_top_header {
		background:url(http://ve3dmedia.ign.com/files/ve_skins/classic/news_1stpost_background.jpg) no-repeat !important;
	}

	.news_post{
		background-color: #434955 !important;
	}

	.news_post_alt{
		background-color: #565A65 !important;
	}

	.article_post {
		background-color: #434955 !important;
	}

	.article_post_alt {
		background-color: #565A65 !important;
	}

	.comment_post {
		background-color: #434955 !important;
	}

	.comment_post_alt {
		background-color: #565A65 !important;
	}

	.media_post {
		background-color: #434955 !important;
	}

	.table_content_row {
		background-color: #565A65 !important;
		color: #ffffff !important;
	}

	.table_content_row_alt {
		background-color: #434955 !important;
		color: #ffffff !important;
	}

	.table_text_faded {
		color: #C1C5CC !important;
		color: #ffffff !important;
	}

	/* news_separator.gif */
	.news_post_separator, .article_post_separator, .comments_separator, .media_post_separator {
		background:url(http://ve3dmedia.ign.com/files/ve_skins/classic/news_separator.gif) no-repeat !important;
	}

	/* comment_separator.jpg */
	#comment_display_styles {
		background:url(http://ve3dmedia.ign.com/files/ve_skins/classic/comment_separator.jpg) no-repeat !important;
	}

	/* comment_box_background.jpg */
	.comment_box {
		background:url(http://ve3dmedia.ign.com/files/ve_skins/classic/comment_box_background.jpg) no-repeat !important;
	}

	/* fullwidth_background.jpg */
	#site_full_width {
		background-image:url(http://ve3dmedia.ign.com/files/ve_skins/classic/fullwidth_background.jpg) !important;
	}

	/* fullwidth_swoosh_background.jpg */
	#site_full_width_body{
		background-image:url(http://ve3dmedia.ign.com/files/ve_skins/classic/fullwidth_swoosh_background.jpg) !important;
	}

	body img[src$="icon_leftarrow.jpg"] {
		width: 0 !important;
		height: 7px !important;
		padding-left: 10px !important;
		background-image:url(http://ve3dmedia.ign.com/files/ve_skins/classic/icon_leftarrow.jpg) !important;
	}

	body img[src$="icon_rightarrow.jpg"] {
		width: 0 !important;
		height: 7px !important;
		padding-left: 10px !important;
		background-image:url(http://ve3dmedia.ign.com/files/ve_skins/classic/icon_rightarrow.jpg) !important;
	}

	body img[src$="icon_uparrow.jpg"] {
		width: 0 !important;
		height: 9px !important;
		padding-left: 7px !important;
		background-image:url(http://ve3dmedia.ign.com/files/ve_skins/classic/icon_uparrow.jpg) !important;
	}

	#site_liquid_content_left {
		background: url(http://ve3dmedia.ign.com/files/ve_skins/classic/liquid_content_left.jpg) !important;
	}

	#site_liquid_content_right {
		background: url(http://ve3dmedia.ign.com/files/ve_skins/classic/liquid_content_right.jpg) !important;
	}

	.comment_post_indent {
		background-image:url(http://ve3dmedia.ign.com/files/ve_skins/classic/indent.jpg) !important;
	}

	body img[src$="liquid_content_left.jpg"] {
		width: 0 !important;
		height: 0 !important;
	}

	body img[src$="liquid_content_right.jpg"] {
		width: 0 !important;
		height: 0 !important;
	}
}
