css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("saraba1st.com") {

  /* 去顶栏, 底栏 */
  body > * { display: none !important; }
  #wp { display: block !important; }
  
  /* 宽度 */
  #wp { width: 960px !important; }
  
  /* 字体 */
  *, a { font-size: 20px !important; }
  
  /* 隐藏热门新区 */
  div[class="bm bmw  flg cl"] { display: none; }
  
  /* 隐藏版块介绍 */
  #category_1 p { display: none; }
  
  /* 其它, 隐藏版块介绍版主最后回复人友情链接 */
  #pt, #chart, div[class="bm lk"] { display: none; }
  .fl_by cite { display: none !important; }
  #sp-fw-container { display: block !important; }

}

@-moz-document url-prefix("http://bbs.saraba1st.com/2b/forum-") {

  /* 隐藏版块说明和标签 */
  div[class="bm bml pbn"], #thread_types { display: none !important; }
  
  /* 隐藏子版块 */
  div[class="bm bmw fl flg"] { display: none !important; }
  
  /* 调整字体, 全缩小仅帖子标题大 */
  #threadlist * { font-size: 12px !important; }
  #threadlist .xst { font-size: 20px !important; }
  
  /* 控制作者回复最后回复栏宽度高度 */
  div.th { height: 30px !important; overflow: hidden !important; }
  .num { width: 0 !important; height: 0 !important; overflow: hidden !important; }
  .by { width: 80px !important;  }
  .by { height: 28px !important; overflow: hidden !important; text-align: center; }
  .by > * { width: 80px; height: 14px; overflow: hidden; }
  
  /* 隐藏预览最热新人帖精华附件标志 */
  .tdpre, tbody[id^="normalthread"] em, tbody[id^="stickthread"] em, img[alt="heatlevel"], img[alt="digest"], img[alt="attachment"], img[alt="新人帖"], img[alt="recommend"], img[alt="手机发帖"] { display: none !important; }
  
  /* 调整点击区域 */
  tbody[id^="normalthread"] th, tbody[id^="stickthread"] th { position: relative; padding: 0 !important; }
  .xst { display: block; width: 600px !important; height: 30px !important; padding: 8px 0 8px 30px; overflow: hidden !important; font-size: 20px !important; }

  /* 调整标志位置 */
  .tps, .xi1, img[alt="attach_img"] { position: absolute; }
  .tps { display: none; margin-top: -15px; margin-left: 35px; }  /* 页码, 鼠标悬停显示 */
  .tps, .tps a { color: #999 !important; } 
  tbody[id^="normalthread"]:hover .tps, tbody[id^="stickthread"]:hover .tps { display: block; }
  .xi1 { margin-top: -18px; margin-left: -30px; }  /* new */
  img[alt="attach_img"] { margin-top: -39px; margin-left: -30px; width: 25px; }  /* 图片 */
  .num > .xi2 { display: block; position: absolute; width: 35px; height: 15px; overflow: hidden; margin-left: -848px; margin-top: -7px; text-align: center; }  /* 回帖数 */
  
  /* 隐藏快速发帖 */
  #f_pst { display: none; }
  
}

@-moz-document url-prefix("http://bbs.saraba1st.com/2b/thread-") {
  
  /* 背景颜色和边框 */
  #postlist { border: none !important; }
  .pls { background-color: #F6F7EB !important; border: none !important; }
  .vwthd, .plc { background-color: #F6F7EB !important; }
  .pi, .pbn { border-bottom: none !important; }
  .plhin { border: 1px dashed #333 !important; border-width: 1px 0 0 0 !important; }
  
  /* 调整帖子尺寸各种字体 */
  div[class="po hin"], td[class="plc ptm pbn vwthd"] > .y, td[class="plc ptm pbn vwthd"] > .xg1, #fj { display: none !important; }
  td[class="pls ptn pbn"] *, .authi, .authi *, a[id^="postnum"], a[id^="postnum"] *, .favatar *, .sign { font-size: 12px !important; }
  .xw1 { font-size: 18px !important; }  /* 用户名 */
  a[id^="postnum"] { padding: 4px 0 0 0 !important; margin: 0 !important; }
  .pi { padding-top: 0 !important; }
  .pti { padding-top: 4px; }
  
  /* 隐藏个人信息 */
  .favatar > p, .favatar > dl, .favatar > ul, .favatar > div[class="tns xg2"] { display: none; }

  /* 隐藏其它 */
  #f_pst, div[class="pgs mtm mbm cl"], .pgbtn, td[class="plc plm"] { display: none; }
}
