css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.reddit.com") {
body {
background:#333 !important;
width:990px !important;
font-family:Helvetica, Arial, Verdana, sans-serif !important;
font-size:11px !important;
font-weight:400;
color:#eee !important;
margin:0 auto !important;
}

a:visited {
color:#03619f !important;
}

.infobar {
background:#121212 !important;
}

#header {
background:none !important;
border-bottom:0 !important;
height:145px !important;
}

#sr-header-area {
background:#1e1e1e !important;
color:#fff !important;
text-transform:capitalize !important;
border-bottom:0 !important;
height:10px !important;
font-size:11px !important;
-moz-border-radius:0 0 5px 5px;
border-radius:0 0 5px 5px;
padding:8px 10px 12px !important;
}

.dropdown.srdrop .selected {
color:#FF4500 !important;
font-weight:700;
}

.drop-choices {
top:30px !important;
left:0 !important;
background:#3e3e3e !important;
border:0 !important;
border-left:1px solid #555 !important;
border-bottom:1px solid #555 !important;
border-bottom-right-radius:5px;
border-bottom-left-radius:5px 0;
-moz-border-radius-bottomleft:5px;
-moz-box-shadow:10px 10px 10px -10px #222;
padding:10px !important;
}

.drop-choices hover {
background: #111;
color: #fff;
}

#sr-more-link {
background:#1e1e1e !important;
top:8px !important;
color:#FF4500 !important;
padding-right:15px !important;
}

.tabmenu {
position:absolute;
bottom:20px;
}

.tabmenu li {
margin:0 !important;
}

.tabmenu li a {
border:0 !important;
background:none !important;
color:#efefef !important;
font-size:13px !important;
margin:0 5px 0 0 !important;
padding:7px 12px !important;
}

.tabmenu li a:hover,.tabmenu li.selected a:hover {
border:0 !important;
background:#FF4500 !important;
color:#fff !important;
-moz-border-radius:7px !important;
-moz-box-shadow:1px 1px #000 !important;
border-radius:7px;
margin:0 5px 0 0 !important;
padding:7px 12px !important;
}

.tabmenu li.selected a {
background:#000 !important;
color:#FF4500 !important;
-moz-border-radius:7px !important;
-moz-box-shadow:1px 1px #000 !important;
border-radius:7px;
margin:0 5px 0 0 !important;
}

#header-img-a {
position:absolute;
top:40px;
}

#header-img {
background:#fff;
max-height:50px !important;
border:none !important;
-moz-border-radius:10px;
border-radius:10px;
padding:5px;
}

.redditname,.pagename {
color:#eee !important;
position:absolute !important;
top:40px;
right:0;
font-size:30px !important;
font-variant:normal !important;
background:none !important;
margin:0 !important;
}

.redditname a {
color:#595959 !important;
font-size:30px !important;
font-variant:normal !important;
margin:0 !important;
}

#header-bottom-right {
background:none !important;
bottom:17px !important;
font-weight:700;
color:#000 !important;
}

#header-bottom-right a {
color:#efefef !important;
}

.separator {
font-weight:400;
}

.content {
background:#1e1e1e !important;
width:655px !important;
-moz-border-radius:10px;
-moz-box-shadow:1px 1px #000 !important;
border-radius:10px;
margin:0 !important;
padding:10px !important;
}

.content .content {
background:none !important;
width:auto !important;
-moz-box-shadow:none !important;
position:relative;
}

.organic-listing {
background:#1e1e1e !important;
border:1px solid #1e1e1e !important;
-moz-border-radius:5px;
border-radius:5px;
}

ul.tabmenu.formtab {
position:absolute;
bottom:30px;
right:125px;
}

.side {
background:none !important;
margin:0 !important;
}

.side .spacer {
background:#1e1e1e !important;
-moz-border-radius:10px;
-moz-box-shadow:1px 1px #000 !important;
border-radius:10px;
margin:0 0 15px !important;
}

.side .spacer:first-of-type {
-moz-border-radius:5px;
}

.side .spacer #search input {
width:270px !important;
font-family:Helvetica, Arial, Verdana, sans-serif !important;
font-weight:400;
font-size:15px !important;
background:#fff;
border:1px solid #a9a9a9 !important;
-moz-border-radius:5px;
border-radius:5px;
margin:10px !important;
padding:5px !important;
}

.sidecontentbox {
margin:10px !important;
padding:10px 0 !important;
}

.sidecontentbox .content {
width:auto !important;
clear:both;
border:0 !important;
-moz-box-shadow:none !important;
margin:0 !important;
}

.fancy-toggle-button a {
color:#fff !important;
font-size:15px !important;
font-weight:700;
border:0 !important;
-moz-border-radius:5px !important;
padding:4px 8px !important;
}

.fancy-toggle-button .remove {
background:#d54a61 !important;
}

.fancy-toggle-button .add {
background:#54d54a !important;
}

.titlebox h1 {
background:url(data:image/gif;base64,R0lGODlhBwANAIABAHJycv///yH5BAEAAAEALAAAAAAHAA0AAAITjA9nkMj+Apty2lvt0jt2VYFSAQA7) no-repeat 0 6px;
overflow:visible;
margin:0 0 10px -17px !important;
padding:0 0 5px 17px !important;
}

.titlebox .number,.titlebox .word {
color:#90d3f5 !important;
font-weight:700;
font-size:11px !important;
}

.titlebox .bottom {
border-top:1px solid #eee !important;
color:#eee !important;
padding:10px 0 0 !important;
}

.morelink {
display:block !important;
background:#004365 !important;
border:0 !important;
-moz-border-radius:5px !important;
border-radius:5px !important;
-moz-box-shadow:1px 1px #7fc3e6 !important;
padding:0 !important;
}

.morelink:hover {
background:#FF4500 !important;
-moz-box-shadow:1px 1px red !important;
}

.sidebox.create .spacer,.sidebox.submit .spacer,.sidebox.gold .spacer {
min-height:0 !important;
-moz-box-shadow:none !important;
margin:0 !important;
padding:10px 0 0 !important;
}

.sponsorshipbox.thing {
border:0 !important;
padding:0 !important;
}

.linkinfo {
background:none !important;
border:0 !important;
padding:10px !important;
}

.linkinfo .downvotes {
color:#90d3f5 !important;
}

.thing {
margin-right:0 !important;
margin-left:0 !important;
border-bottom:1px dashed #666;
}

.comment.thing {
padding-left:0 !important;
}

m.comment {
margin-right:10px !important;
}

.linklisting .thing {
padding:15px 0 !important;
}

.organic-listing .link {
background-color:#1e1e1e !important;
}

.link.promotedlink.promoted {
background:#1e1e1e !important;
}

.link .score {
font-weight:700;
font-size:15px !important;
color:#63c1ff !important;
}

.midcol {
margin:0 10px !important;
}

.thumbnail {
background:#fff;
margin:0 10px 0 0 !important;
padding:0 !important;
}

.thing .title .loggedin {
color:#ccc !important;
}

.thing .title .loggedin:visited {
color:#888 !important;
}

.arrow {
height:15px !important;
}

.menuarea .drop-choices {
border-left:1px solid #eee !important;
border-bottom:1px solid #eee !important;
}

.footer-parent {
text-align:left !important;
padding:20px 0 0 !important;
}

.footer {
border:0 !important;
margin:0 !important;
padding:0 !important;
}

.footer .col {
border-left:1px solid #000 !important;
margin:0 0 20px !important;
padding:10px 20px !important;
}

.footer .col:first-of-type {
border-left:0 !important;
padding:10px 20px 10px 0 !important;
}

.title p {
color:#cfc !important;
}

.flat-vert.title {
color:#fff !important;
text-shadow:1px 1px #000;
}

.md {
color:#eee !important;
}

.link .usertext .md {
background:#222 !important;
}

.usertext-edit textarea {
width:630px !important;
}

a,.user,.entry .buttons li a,.tagline a,.nextprev a,.footer .col a {
color:#63c1ff !important;
}

a:hover,.linkinfo .upvotes,.thing .title .loggedin:hover {
color:#FF4500 !important;
}

#sr-header-area a,.sr-bar .separator,.morelink a,.bottommenu,.bottommenu a {
color:#fff !important;
}

.sr-bar.hover {
position:relative !important;
overflow:auto !important;
}

.titlebox,.submit,.create,.gold {
padding:10px !important;
}

.morelink .nub,.link .rank {
display:none !important;
}

.sidebox .subtitle,.domain,.domain a,.tagline {
color:#aaa !important;
}

.comment {
border-radius:7px !important;
}

.comment .child {
border-left:none !important;
}

.thing.comment {
margin-right:10px !important;
}

.roundfield {
background:#292929 !important;
-moz-border-radius:5px;
border-radius:5px;
}

.roundfield textarea {
width:492px !important;
}

.content .spacer {
margin:15px 0 !important;
}
}
