css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("joystiq.com")
{

/* Colors */
	body {
		background: #000 !important;
		color: #808080 !important;
	}

	div#content, div#sub-hero, div#container, div#mc-outer, div#GH_strip, div#footer, div#sub-content, div#comments, div#main-content, div#branding {
		background: #000 !important;
		color: #808080 !important;
	}

	div[class="post"], div[class="post "], div[class="post mini"], div[class="post-body"], div[class="post-inner"], div[class="post-head"], div[class="post-meta"], div[class="social-tools"], div[class="rmod"], div[class="post permalink"], div[class="GH-hat-ddbox"], div[class="join_instructions"], div[class="badges"], .from-massively {
		background: #000 !important;
		color: #808080 !important;
	}

	div[class="console-badge"] img {
		border:0px solid #000 !important;
		background-color:#FFF !important;
	}

	h1 a, h2 a {
		color: #248AFF !important;
	}
	
	div#main-carousel, div#main-carousel-captions {
	   background-color:black !important;
	   background-image:none !important;
	}

/* Horizontal Article Breaks to Grey */
	.post-inner {
		border-bottom: 6px solid #3D3D3D !important;
	}

	div[class="post permalink"] .post-head {
		border-bottom: 9px solid #3D3D3D !important;
	}

/* Article Mini Image Frames Hide */
	#main-content .mini .post-inner .post-body img {
		border: 0px solid #000 !important;
	}

/* Poll Question Frams to Grey */
	div.poll, table.poll {
		border: 4px solid #3D3D3D !important;
	}
	.poll td {
		border-left: 3px solid #3D3D3D !important;
	}

/* Gallery Frames to Grey */
	.postgallery {
		border: 8px solid #3D3D3D !important;
	}

/* Gallery Image Frames Hide */
	#main-content .post .postgallery ul li img {
		background: none !important;
		border: 0px solid #000000 !important;
	}

/* Black Form Boxes, Buttons, & Dropdown Menus */
	input, input#s, select, textarea {
		background: #000 !important;
		border-color: #000 !important;
		-moz-appearance: none !important;
		color: #808080 !important;
	}

	input#search-field {
		background: #262626 !important;
	}

/* Layout */
	div#top-leader, div#sub-content,  div#slide-content, div.post-meta, div#sub-hero, div#grid, div[class="aol-logo"], div[class="GH-hat-mail"], div[class="GH-hat-like"], div#footer-inner {
		display: none !important;
	}

	div#main-content, div.post-head, h2.headline, div.post-inner, div.post-body {
		width: 871px !important;
	}

	#micro-posts .m-post .body {
		width: 618px !important;
	}

	div.post-inner {
		min-height: 220px !important;
	}

	div#mp-footer, div.post {
		width: 950px !important;
	}

	div.postgallery {
		width: 847px !important;
	}

	li.next {
		float: right !important;
	}

	li.prev {
		float: left !important;
	}

/* Comments */

	#comments, #comments .child, #comments .comment {
		background: #000 !important;
	}

	#comments .comment {
		border-bottom: 1px solid #3D3D3D !important;
		border-top: 1px solid #3D3D3D !important;
	}

	div.comment-body {
		width: 670px !important;
	}

	div.comment-inner {
		width: 770px !important;
	}
	
/* LiveFyre Comment System */

    div[class="mention_notice"], div[class="lf_follow_link"], div[class="lf_action_bar"] {
        background-color: black !important;
    }

    #livefyre .lf_post {
        background-image: -moz-linear-gradient(100% 100% 90deg, #000, #555 100%) !important;
    }

/* Joystiq & Console Logos */
	#branding a.logo {
		background:url("http://img843.imageshack.us/img843/6583/mainspritey.png") no-repeat scroll left -384px transparent !important;
		display:block; float:left; height:64px; margin-right:24px; margin-top:15px; text-indent:-9999px; width:169px;
	}

	#branding #console-menu .ps3 a {
		background:url("http://img843.imageshack.us/img843/6583/mainspritey.png") no-repeat scroll 0 -53px transparent !important;
		float: left; height: 53px; margin-right: 6px; text-indent:-9999px; width:37px;
	}
	#branding #console-menu .ps3 a:hover {
		background-position:0 -115px !important;
	}

	#branding #console-menu .xbox a {
		background: transparent url("http://img843.imageshack.us/img843/6583/mainspritey.png") -35px -53px no-repeat !important;
		float: left; width: 38px; height: 53px; margin-right: 8px; text-indent: -9999px;
	}
	#branding #console-menu .xbox a:hover {
		background-position: -35px -115px !important;
	}

	#branding #console-menu .wiiu a {
		background: transparent url("http://img843.imageshack.us/img843/6583/mainspritey.png") -74px -53px no-repeat !important;
		float: left; width: 35px; height: 53px; margin-right: 4px; text-indent: -9999px;
	}
	#branding #console-menu .wiiu a:hover {
		background-position: -74px -115px !important;
	}

	#branding #console-menu .ds a {
		background: transparent url("http://img843.imageshack.us/img843/6583/mainspritey.png") -105px -53px no-repeat !important;
		float: left; width: 33px; height: 53px; margin-right: 4px; text-indent: -9999px;
	}
	#branding #console-menu .ds a:hover {
		background-position: -105px -115px !important;
	}

	#branding #console-menu .vita a {
		background: transparent url("http://img843.imageshack.us/img843/6583/mainspritey.png") -138px -53px no-repeat !important;
		float: left; width: 35px; height: 53px; margin-right: 4px; text-indent: -9999px;
	}
	#branding #console-menu .vita a:hover {
		background-position: -138px -115px !important;
	}

	#branding #console-menu .pc a {
		background: transparent url("http://img843.imageshack.us/img843/6583/mainspritey.png") -172px -53px no-repeat !important;
		float: left; width: 33px; height: 53px; margin-right: 2px; text-indent: -9999px;
	}
	#branding #console-menu .pc a:hover {
		background-position: -172px -115px !important;
	}

	#branding #console-menu .mobile a {
		background: transparent url("http://img843.imageshack.us/img843/6583/mainspritey.png") -207px -53px no-repeat !important;
		float: left; width: 33px; height: 53px; margin-right: 4px; text-indent: -9999px;
	}
	#branding #console-menu .mobile a:hover {
		background-position: -207px -115px !important;
	}

	#branding #console-menu .move a {
		background: transparent url("http://img843.imageshack.us/img843/6583/mainspritey.png") -297px -53px no-repeat !important;
		float: left; width: 34px; height: 53px; margin-right: 10px; text-indent: -9999px;
	}
	#branding #console-menu .move a:hover {
		background-position: -297px -115px !important;
	}

	#branding #console-menu .kinect a {
		background: transparent url("http://img843.imageshack.us/img843/6583/mainspritey.png") -244px -53px no-repeat !important;
		float: left; width: 43px; height: 53px; margin-right: 13px; text-indent: -9999px;
	}
	#branding #console-menu .kinect a:hover {
		background-position: -244px -115px !important;
	}

/* Search button */
	#search input#search-button {
		background:url("http://img547.imageshack.us/img547/7772/btnsearch.png") no-repeat scroll 0 0 transparent !important;
		border:medium none; color:transparent; cursor:pointer; height:31px; margin:0; opacity:0.8; padding:0; position:absolute; text-indent:-9999px; width:33px;
	}



/**********/
/* Images */
/**********/

/* Corner Colors: #000000 and #212121 */

/* Continue Reading Button */
	#main-content .post .post-inner .post-body p.continue-reading a {
		display: block !important;
		background: url(data:image/png;base64,) !important;
filter:alpha(opacity=100) !important;
-moz-opacity:1.0 !important;
-khtml-opacity: 1.0 !important;
opacity: 1.0 !important;
	}
	#main-content .post .post-inner .post-body p.continue-reading a:hover {
		filter:alpha(opacity=80) !important;
		-moz-opacity:0.8 !important;
		-khtml-opacity: 0.8 !important;
		opacity: 0.8 !important;
	}

/* Older Posts */
	#mp-footer #mp-paginate li.next a {
		display: block !important;
		background: url(data:image/png;base64,) !important;
	}

/* Newer Posts */
	#mp-footer #mp-paginate li.prev a {
		display: block !important;
		background: url(data:image/png;base64,) !important;
	}


/* Press Release Button */
/* http://www.blogcdn.com/www.joystiq.com/media/2010/06/joy-show-pr.png */
	#pr_box_button {
		display: block !important;
		background-image: url(data:image/png;base64,) !important;
	}


/* Reader Comments Icon - Speech Bubble */
	div#comments h2 {
		display: block !important;
		background-image: url(data:image/png;base64,) !important;
	}

/* Post Badges */

    div[class="console-badge"] a {
        background-image: url("http://img69.imageshack.us/img69/3732/badgesf.png") !important;
    }

}
