@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("facebook.com") {

/* BACKGROUND */
.profile_top_wash { background: rgba(255,255,255,.5) !important; }

#basic_info_summary_box, DIV[id^="box_app_"],
#profile_stream_filters, #profile_stream_container,
#profile_settings,
.UIRecentActivity_Stream,
#profile_minifeed,
.UIStandardFrame_Content,
.UIWashFrame_MainContent { background: #FFF !important; }

.UIStory_Hide { padding-right: 5px !important; }
#profile_stream_container { border: 1px solid #000 !important; }
body:not(.transparent_widget) { background: transparent url('http://i43.tinypic.com/2rxjzw8.jpg') top left !important; }

/* HOME PAGE */
.UIImageBlock.clearfix.fbxWelcomeBox { padding-left: 10px !important; }
#mainContainer > #leftCol { border-left: 1px solid #000 !important; border-bottom: 1px solid #000 !important;
width: 186px !important; margin-left: -6px !important; background: #FFF !important; }
#contentCol.hasRightCol { border: 1px solid #000 !important; }

/* PROFILE NAME & STATUS */
H1#profile_name { color: #fff !important;
text-shadow: #666666 0px 0px 1px, #111111 1px 1px 4px, #333333 1px 1px 4px, white 0px 0px 6px, white 0px 0px 6px !important; }
#profile_status > SPAN#status_text,
SPAN#status_time_inner { color: #000 !important; }

/* LEFT SIDEBAR */
#profile_action_view_photo,
#profile_action_suggest_friends { border-left: 1px solid #000 !important;
border-top: 1px solid #000 !important; border-right: 1px solid #000 !important; }

#profile_action_view_video,
#profile_action_send_message,
#profile_action_report_block { border-left: 1px solid #000 !important;
border-top: 0px !important; border-right: 1px solid #000 !important; }

.profile_action.actionspro_a:last-child,
#profile_action_remove_friend { border-left: 1px solid #000 !important;
border-right: 1px solid #000 !important; border-bottom: 1px solid #000 !important; }

a[id^="profile_action"]:not(:hover):not(:active),
a[class^="profile_action"]:not(:hover):not(:active) { background-color: #FFF !important; color: #003E7E !important; }
a[id^="profile_action"]:hover,
a[id^="profile_action"]:active,
a[class^="profile_action"]:hover,
a[class^="profile_action"]:active { color: #FFF !important; background-color: #008752 !important; }

#profile_blurb { color: #FFF !important; border-color: #000 !important; background-color: #008752 !important; }
#basic_info_summary_box,
div[id^="box_app_"] { border-left: 1px solid #000 !important; border-color: #000 !important; }
#boxes_left { border-color: #000 !important; }
H5.box_header.UITitle.UITitle_h5 { color: #FFF !important; background: #008752 !important; }

/*
.actions.secondary_actions { margin-top: 5px !important; }
.action.actionspro_a[href="/badges/profile.php"] { border: 1px solid #000 !important;
border-top: 0px !important; background-color: #FFF !important; margin-top: -40px !important; }
*/

/* FILL EMPTY SPACES */
#tab_canvas { width: 102.7% !important; }
.UIStandardFrame_Content { width: 100.6% !important; }

/* INFO PAGE */
.profile-pagelet-section { border: 1px solid #000 !important; background: #FFF !important; }
H3.uiHeaderTitle { padding-left: 10px !important; }
.uiTextSubtitle.uiHeaderActions.rfloat { padding-right: 10px !important; }
#pagelet_tab_content TH.label { padding-left: 10px !important; }
TH.label + TD.data + TD.rightCol { padding-right: 10px !important; }

/* NOTES PAGE */
.notePermalinkMaincol.rfloat { width: 79% !important; }

/* COMMON */
a:not(.dropdown_item), button, label, input[value="Delete"]:not(#delete):not([name="delete_story"]):not(#delete_button) { color: #003E7E !important; }

/* ACCOUNT MENU */
#navAccount.openToggler > #navAccountLink { background-color: transparent !important; }
#navAccountLink + UL { border-color: #000 !important; border-top: 0px !important; }

#navAccountInfo > #navAccountName { color: #003E7E !important; }
#navAccountInfo > #navAccountPic:active,
#navAccountInfo > #navAccountPic:hover,
#navAccountInfo > #navAccountName:active,
#navAccountInfo > #navAccountName:hover { background-color: transparent !important; }

INPUT[value="Log out"]:active,
INPUT[value="Log out"]:hover { background-color: #008752 !important; }

LI > A#ufSettings_menu,
INPUT[value="Log out"],
#navAccountInfo + LI,
#navAccountInfo + LI > a,
#navAccountInfo + LI + LI A.dropdown_item,
#navAccountInfo + LI + LI + LI A.dropdown_item,
#navAccountInfo + LI + LI + LI + LI A.dropdown_item,
#navAccountInfo + LI + LI + LI + LI + LI A.dropdown_item,
#navAccountInfo + LI + LI + LI + LI + LI + LI A.dropdown_item { color: #003E7E !important; }

LI > A#ufSettings_menu:active,
INPUT[value="Log out"]:active,
#navAccountInfo + LI:active,
#navAccountInfo + LI > a:active,
#navAccountInfo + LI + LI A.dropdown_item:active,
#navAccountInfo + LI + LI + LI A.dropdown_item:active,
#navAccountInfo + LI + LI + LI + LI A.dropdown_item:active,
#navAccountInfo + LI + LI + LI + LI + LI A.dropdown_item:active,
#navAccountInfo + LI + LI + LI + LI + LI + LI A.dropdown_item:active { border-color: #000 !important; color: #FFF !important; }

LI > A#ufSettings_menu:hover,
INPUT[value="Log out"]:hover,
#navAccountInfo + LI:hover,
#navAccountInfo + LI > a:hover,
#navAccountInfo + LI + LI A.dropdown_item:hover,
#navAccountInfo + LI + LI + LI A.dropdown_item:hover,
#navAccountInfo + LI + LI + LI + LI A.dropdown_item:hover,
#navAccountInfo + LI + LI + LI + LI + LI A.dropdown_item:hover,
#navAccountInfo + LI + LI + LI + LI + LI + LI A.dropdown_item:hover { border-color: #000 !important; color: #FFF !important; }

/* TOP HEADER */
#blueBar { background-color: #003E7E !important; border-bottom: 6px solid #008752; }
#navSearch { margin: 2px 0px 0px 2px !important; }
#navAccount.openToggler { margin-top: 0px !important; background-color: #FFF !important; }

#headNavOut { border: 0px !important; border-bottom: 1px solid #008752 !important; background-color: #008752 !important; }
#pageNav a { margin-top: 2px !important; color: #FFF !important; }

#pageNav a:hover,
#pageNav a:focus,
#pageNav a:active,
#pageLogo a:hover,
#pageLogo a:focus,
#pageLogo a:active { color: #003E7E !important;  background-color: #008752 !important; background-position: -21px -31px !important; }
#pageLogo a { margin-left: -1px !important; background: #008752 url('http://i776.photobucket.com/albums/yy47/Hucz/Stylish/facebook.png') no-repeat -21px 0 !important;
border-right: 2px solid #008752 !important; }

/* THREE JEWELS */
#jewelCase > .jewel.openToggler > .jewelToggler { background-color: #FFF !important; }

div[id$="Wrapper"].jewel { height: 30px !important; background-color: #008752 !important;
border: 1px solid #008752 !important; border-top: 0 !important; }

.jewelToggler { height: 29px !important; margin-top: 0px !important;
background: no-repeat url('http://i776.photobucket.com/albums/yy47/Hucz/Stylish/facebook.png') !important;}

.openToggler .jewelToggler { height: 29px !important; background-color: #008752 !important; }
.openToggler:not(.fbNub) { margin-top: 13px !important; }

#jewelMail { background-position:-24px -73px !important; }
#jewelMail:hover, #jewelMail:focus, #jewelMail:active { background-position: -51px -73px !important; }
.jewelNew #jewelMail { background-position: -78px -73px !important; }
.openToggler #jewelMail { margin-top: 1px !important; background-position: -105px -74px !important; }

#jewelRequest { background-position:-24px -105px !important; }
#jewelRequest:hover, #jewelRequest:focus, #jewelRequest:active { background-position:-51px -105px !important; }
#jewelRequestsContent .jewelRequest .jewelRequestContent{padding-top:5px}
#jewelRequestsContent .uiTextTitle{font-size:11px}
#jewelRequestsContent .requestLabel{line-height:12px}
.jewelNew #jewelRequest { background-position: -78px -105px !important; }
.openToggler #jewelRequest { margin-top: 1px !important; background-position: -105px -106px !important; }

#jewelNotif { background-position: -24px -137px !important;}
#jewelNotif:hover, #jewelNotif:focus, #jewelNotif:active { background-position: -51px -137px !important; }
.jewelNew #jewelNotif { background-position:-78px -137px; }
.openToggler #jewelNotif { margin-top: 1px !important; background-position:-105px -138px !important; }
.jewelBox { border-color: #008752 !important; }

/* PROFILE TABS */
#profile_tabs > LI a { color: #FFF !important; }
#profile_tabs > LI.selected a { background-color: #008752 !important; }
#profile_tabs > LI:not(.selected):not(:last-child) a:not(:hover) { background-color: #003E7E !important; }
#profile_tabs > LI:not(.selected) { height: 23px !important; }
#profile_tabs > LI:not(.selected) > .tab_link.profile_tab:active,
#profile_tabs > LI:not(.selected) > .tab_link.profile_tab:hover { background-color: #008752 !important; }

.tab_link.profile_tab,
#profile_tab_add { border: 1px solid #000 !important; }
#profile_tab_add { max-height: 22px !important; border-bottom: 0px !important; background-color: #003E7E !important; }

.profile .top_bar ul.tabs li.add_tab a.tab_link { background-position: left -25px !important; border: 0px !important; }
.profile .top_bar ul.tabs li.add_tab a.tab_link:active,
.profile .top_bar ul.tabs li.add_tab a.tab_link:hover { background-color: #008752 !important; }
.profile_top_bar_container { margin-left: -4px !important; }

/* WHAT'S ON YOUR MIND? */
#feedwall_with_composer > DIV[class^="UIComposer"] { margin-top: -15px !important; }

.UIComposer_InputArea { border: none !important; }
.pas.UIComposer_Box.UIContentBox.gray_box { border-color: #000 !important; background-color: #008752 !important; }
/*
.uiTooltip > I.mrs.img.spritemap_cfv8br.sx_f4c73f { background: no-repeat url("file:///C:/Documents and Settings/Mason/My Documents/facebook3.png") !important;}
*/
.UIComposer_Content li { color: #FFF !important; }
.UIComposer_SubmitButton { border-color: #FFF !important; background: #003E7E !important; }

/* WALL & NEWS FEED */

/*
.UIStory_Hide > a[href^="/ajax/minifeed/remove_confirm.php?"] { border-color: #FFF !important; background-color: #003E7E !important; }
.UIStory_Hide > a > .uiButtonText { color: #003E7E !important; }
*/

.UIIntentionalStory_Pic { padding-left: 5px !important; }
.advanced_controls_link { margin: 5px 5px 0px 0px !important; }

/* CHAT */
.fbNubFlyoutTitlebar,
A[id*="_buddy_list_item_"]:active,
A[id*="_buddy_list_item_"]:hover { color: #FFF !important; border-color: #000 !important; background-color: #008752 !important; }

/* FOOTER */
.lfloat *, .lfloat a, .rfloat a, .rfloat { margin-top: 3px !important; color: #FFF !important; }
#contentCurve { display: none !important; }
#pageFooter { margin-top: -4px !important; height: 40px !important; border: 0px !important;
background: transparent url('http://cdn.nhl.com/canucks/images/upload/2009/07/canucks_stripe_bg.gif') center !important; }
}
