css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("randomc.net") {

/* Ad Removal for those of you crazy people that don't have Adblock */
	#google_adsense_header, #google_adsense_footer, #google_adsense_sidebar {
		display: none !important;
	}

/***************/
/* Backgrounds */
/***************/

/* Main Body Background & Text */
	body {
		background: #000 !important;
		color: #808080 !important;
	}

/* Secondary Body Background */
	div#wrapper {
		background: #000 !important;
	}

/* Rotating Banner - Anime Title Text & Background*/
	li[class="wp-rb-current"] {
    background-color: #5184FF !important;
    }
    li[class="wp-rb-current"] a {
    color: #222 !important;
    }

/* Filmstrip Captions */
	div[class="filmstrip"] p {
		background: #111 !important;
	}

/* Filmstrip Image Background */
	.filmstrip .snapshot {
		background: #000 !important;
	}

/*********************************/
/* FIX COLORS BACK TO LIGHT BLUE */
/*********************************/

/* Text, Links back to light blue */
	a, a:visited, h1, h2, h3, .sidebar ul ul li:before, .maincolor1, .posttitle, .commenttitle {
		color: #5184FF !important;
	}
	a:hover {
		color: #8CAFFF !important;
	}

/* Right Menu Links */
	.sidebar p a:link, .sidebar p a:visited {
		color: #5184FF !important;
	}

	.sidebar a:link, .sidebar a:visited {
		color: #808080 !important; 
	}

	.sidebar a:hover {
		color: #8CAFFF !important;
	}
/* Top Rotating Banner Image Border */
    .wp-rb-slides_container img {
        border: 0px #8CAFFF !important;
    }

/* Top Banner Image Border */
	#headerimage {
		border-top: 1px solid #8CAFFF !important;
		border-bottom: 1px solid #8CAFFF !important;
	}

/* Image Borders */
	img {
		border: 1px solid #8CAFFF !important;
	}
	li[class="gravatar"] img, div#recent-comments img {
		border: none !important;
	}

/* Calendar Background */
	table {
		background: #1F1F1F !important;
	}

/* Calendar Numbers Background & Image Tables */
	td {
		background: #000 !important;
	}

/* Navigation Drop-down */
	#topmenu ul li a:link {
		border-bottom: 2px solid #248FFF !important;
	}
	#topmenu ul li ul a:link {
		border-bottom: none !important;
	}

	div#topmenu a, div#topmenu a:visited {
		background-color: #000 !important;
		color: #5184FF !important;
	}

	div#topmenu a:hover {
		background-color: #2D2D2D !important;
		color: #8CAFFF !important;
	}

	div#topmenu ul li ul li {
		opacity: 0.85 !important;
		filter:alpha(opacity=85) !important;
		-moz-opacity:0.85 !important;
		-khtml-opacity: 0.85 !important;
	}

/* Divine's Random Song of the Moment - Hidden until I can figure out how to make it black */
	p[class="maincolor1"], div#media_player {
		display: none !important;
	}

/* Shoutbox */
	div.sk-comment {
		border: 1px solid #1F1F1F !important;
	}

	div[class="sk-userdata-user"] strong {
		color: #99a !important;
	}

	div[class="sk-userdata-user"] img[class="avatar avatar-32 photo"] {
		display:none !important;
	}

/* Poll */
	.pds-box {
		background-color: #000 !important;
		border: 2px solid #1F1F1F !important;
	}

	.pds-input-label, .pds-question-top, .pds-feedback-label, .pds-total-votes, .pds-view-results, .pds-return-poll, .pds-pd-link {
		color: #808080 !important;
	}

	.pds-view-results:hover, .pds-return-poll:hover, .pds-pd-link:hover {
		color: #8CAFFF !important;
	}

	input[type="checkbox"] {
		-moz-appearance: none;
		background: black !important;
		background-color: black !important;
		color: #FFF !important;
		border: 2px solid #808080 !important;
	}

	.pds-vote-button {
		background: #060606 !important;
	}
	.pds-vote-button:hover {
		border: 1px solid #8CAFFF !important;
	}

	.pds-answer-feedback {
		background-color: #222 !important;
		border: 1px solid #444 !important;
	}


/***********************/
/* Forms, Boxes, Menus */
/***********************/

/* Form Boxes and buttons */
	input {
		background: #000 !important;
		border-color: #000 !important;
		-moz-appearance: none !important;
		color: #808080 !important;
	}

/* Search Form */
	input#s {
		background: #000 !important;
		border-color: #000 !important;
		-moz-appearance: none !important;
		color: #808080 !important;
	}

/* Reply Box */
	textarea {
		background: #000 !important;
		border-color: #000 !important;
		-moz-appearance: none !important;
		color: #808080 !important;
	}

/* Drop-down menus */
	select {
		background:#000 !important;
		border-color:#000 !important;
		color:#808080 !important;
		-moz-appearance:none !important;
	}



/************/
/* Comments */
/************/

/* Even DARK PRUPLE */
    li[class^="comment even"] {
		background: #100030 !important;
	}

/* Odd DARK BLUE */
    li[class^="comment odd"] {
        background: #000043 !important;
    }

/* Highly Rated to DARKISH GREEN */
    li[class*="ckrating_highly_rated"], div[style^="background-color:#D6FACB"] {
        background: #003000 !important;
    }
    
/* Poorly Rated to VERY DARK BROWN */
    li[class*="ckrating_poorly_rated"] {
        background: #181009 !important;
    }
    
/* Hotly Rated to DARK GREY */
    li[class*="ckrating_hotly_rated"] {
        background: #1F1F1F !important;
    }

/* Other Writer to PURPLE */
    li[class^="comment byuser"] {
        background: #300000 !important;
    }

/* Author to RED */
	li[class*="bypostauthor"] {
		background: #400000 !important;
	}
	

/**********/
/* Images */
/**********/

/* Permalink - Document Icon */
	.postlnk {
		background: url(data:image/gif;base64,) !important;
		background-repeat: no-repeat !important;
	}

/* Comment - Speech Bubble Icon */
	.comlnk {
		background: url(data:image/gif;base64,R0lGODlhCwANAJEDABUVFQQEBDQ0NP///yH5BAEAAAMALAAAAAALAA0AAAIjnI+pJ+3CgpwACglyDvXO3wGcJlaDIIKQ8WTNgpqwDK/LUQAAOw==) !important;
		background-repeat: no-repeat !important;
	}

/* Edit Post - Pencil Icon */
	.pelnk {
		background: url(data:image/gif;base64,) !important;
		background-repeat: no-repeat !important;
	}

/* Edit Tag - Tag Icon */
	.taglnk {
		background: url(data:image/gif;base64,) !important;
		background-repeat: no-repeat !important;
	}


/* removes stats advert on the bottom of the right panels */
	a[href="http://www.sitemeter.com/stats.asp?site=s22randomc"], a[href="http://s22.sitemeter.com/stats.asp?site=s22randomc"] {
		display:none!important;
	}

/* replaces ALL comment avatars with a dark version.  I'm not sure how to only do the default one. */
	img[class="avatar photo"] {
		width:0 !important;
		height: 45px !important;
		background-image: url(data:image/png;base64,) !important;
		background-repeat: no-repeat !important;
		padding-left: 45px !important;
	}

/**************/
/* Rare Posts */
/**************/

/* Season Schedules */
    .asobi_bg {
		background-color: rgb(255,153,102) !important;
	}
	.cherrie_bg {
		background-color: rgb(245,166,234) !important;
	}
	.divine_bg {
		background-color: #C5E2FF !important;
	}
	.enzo_bg {
		background-color: #9BFFB0 !important;
	}
	.gray_bg {
		background-color: #444444 !important;
	}
	.guest_bg {
		background-color: #ECD672 !important;
	}
	.kairi_bg {
		background-color: rgb(60,179,113) !important;
	}
	.kiiragi_bg {
		background-color: #FCFD5F !important;
	}
	.prooof_bg {
		background-color: #FFBE38 !important;
	}
	.seishun_bg, td[bgcolor="#00FFFF"] {
		background-color: rgb(0,255,255) !important;
	}
	.stilts_bg {
		background-color: rgb(145,200,204) !important;
	}
	.takaii_bg {
		background-color: #C5B9FC !important;
	}
	.zanibas_bg {
		background-color: rgb(194,253,47) !important;
	}
	.zephyr_bg {
		background-color: rgb(255,255,128) !important;
	}



/* Filmstrip Posts */
	.filmstrip {
		background-image: url(data:image/gif;base64,) !important;
		background-position: center top !important;
		background-repeat: repeat-y !important;
	}

}
