css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {





*{text-shadow:none !important; color:#C7C7C7 !important;}



Body{
background: url('http://static.tumblr.com/h4wxerm/Xo8lenra1/untitled-1.jpg') center right #333333 repeat fixed !important;

color:#C7C7C7 !important;

font-size:12px !important;

}
#footer.fixed {display:none;}
#right_column .dashboard_nav_item {
color: #C4CDD6;
font: normal normal normal 11px/normal 'Lucida Grande', Verdana;
}
#auto_pagination_loader {display:none;}
#right_column .dashboard_nav_item .dashboard_controls_radar_media {
background: #ffffff;
color: #1F2B38;
font: normal normal normal 12px/normal 'Lucida Grande', Verdana, sans-serif;
line-height: 16px;
margin: 10px auto 0px;
overflow: ;
padding: 4px 6px;
position: relative;
width: 190px;
word-wrap: break-word;
}


#dashboard_controls_radar_media_fold{display:none;}









#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog .dashboard_controls_suggested_blog_follow {display:none;}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog .dashboard_controls_suggested_blog_title img {
border: 2px solid #c7c7c7;
margin-right: 4px;
vertical-align: -6px;
}

#right_column #dashboard_switch_blog_menu_wrapper #dashboard_switch_blog_menu {
-webkit-text-stroke: 1px transparent;
background: #ffffff;
border-bottom-left-radius: 4px 4px;
border-bottom-right-radius: 4px 4px;
border-top: ;
border-top-left-radius: 4px 4px;
border-top-right-radius: 4px 4px;
color: #334556;
font: normal normal 18px/normal 'Lucida Grande', Verdana;
line-height: 24px;
overflow: hidden;
white-space: nowrap;
}

#dashboard_switch_blog_menu_header{border-bottom:1px solid #ffffff;}

#right_column a#dashboard_switch_blog_menu_current {
-webkit-text-stroke: 1px transparent;
color: #D6DCEA;
display: block;
font: normal normal 18px/normal 'Lucida Grande', Verdana;
height: 24px;
line-height: 24px;
margin: 5px auto 0px;
overflow: hidden;
padding: 5px 5px 3px 10px;
position: relative;
text-decoration: none;
text-shadow: black 0px 1px 0px;
white-space: nowrap;
width: 200px;
}

#right_column #dashboard_switch_blog_menu_wrapper #dashboard_switch_blog_menu a.dashboard_switch_blog_menu_item {
border-bottom: 1px solid #BCCBD8;
border-top: ;
color: #3B4F61;
display: block;
font: normal normal normal 13px/normal 'Lucida Grande', Verdana;
padding: 5px 0px 5px 11px;
position: relative;
text-decoration: none;
}

#right_column #dashboard_switch_blog_menu_wrapper #dashboard_switch_blog_menu a#dashboard_switch_blog_menu_footer {
color: #465A6C;
font: normal normal normal 11px/normal 'Lucida Grande', Verdana;
text-align: center;
}



#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog {
border-bottom: 1px solid #fafafa;
border-top: 1px solid #fafafa;
padding: 6px;
position: relative;
}

#posts .post .permalink {
background: transparent url(http://assets.tumblr.com/images/dashboard_master_sprite.png) no-repeat -500px -190px;
display: none;
height: 15px;
position: absolute;
right: 0px;
top: 0px;
width: 15px;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color:transparent !important}

#pagination a {
background-color: #fbfbfb;
color: #c7c7c7;
margin-left: 10px;
padding: 5px 8px;
text-decoration: none;
white-space: nowrap;
}
#right_column .dashboard_nav_item #hide_radar {
background: transparent;
display: none;
height: 15px;
position: absolute;
right: 0px;
text-align: right;
top: 0px;
width: 45px;
}

#right_column .dashboard_nav_item
#dashboard_controls_suggested_blogs {
background: #ffffff;
border: solid #ffffff;
margin: 10px auto 0px;
overflow: hidden;
width: 200px;
}

#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, #right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column .dashboard_nav_item .dashboard_controls_radar_media.video {
background: white;
padding: 5px;
}

#dashboard_controls {display:none;}
#right_column .dashboard_nav_item .dashboard_controls_radar_button  {display:none;}
#right_column .dashboard_nav_item ul.dashboard_subpages li a .icon {display:none;}
#right_column .dashboard_nav_item:hover .icon.gear {display:none;}
#right_column .dashboard_nav_item .icon.heart {display:none;}
#right_column .dashboard_nav_item.active .icon.arrow {display:none;}
#right_column .dashboard_nav_item.active .icon.dude {display:none;}
#right_column .dashboard_nav_item .icon.dude {display:none;}
#right_column .dashboard_nav_item .icon.gear:hover {display:none;}

#left_column #posts .post .permalink {

background-color:#transparent !important;

}



#left_column #posts .post .post_avatar,

#left_column #posts .post .sub_avatar .sub_avatar_inner {

background-color: #fbfbfb !important;

border-bottom-color: #eee !important;

}





#left_column #posts .post{

border-bottom:1px #eee solid !important

}





#nav .nav_item.active,

#nav .nav_item .nav_menu,

#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {

background-color:#fbfbfb !important;

background-image:none !important;

border:0px !important

}





#nav .nav_item .nav_menu {

border-bottom:0px !important

}





#nav .nav_item .nav_menu a {

color:#888 !important;

border-top-color:#eee !important;

}



#nav .nav_item .nav_menu a:hover {

color:#666 !important;

}



#footer, #footer a {

color:#c7c7c7 !important;

}





.no_posts_found, h1.dashboard_header, h1.dashboard_header a, h1.dashboard_header span {

color:#c7c7c7!important

}



#right_column .dashboard_nav_item {

border-bottom:1px solid #eee !important;

background-image:none  !important

}





#right_column .dashboard_nav_item .dashboard_nav_title,

                    #right_column .dashboard_nav_item .dashboard_nav_title a,

                    #right_column .dashboard_nav_item a,

                    #right_column .dashboard_nav_item ul.dashboard_subpages,

                    #right_column .dashboard_nav_item ul.dashboard_subpages li a {

                        color:#666 !important;

}



#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {

                        color:#888 !important ;

                    }



#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {

                        display:: none !important; background: none !important;

                    }



#show_radar a, #hide_radar a,

                        #hide_recommended_blogs a, #show_recommended_blogs a {

                            display:: none !important;  color:#666 !important;

                        }





#left_column #posts .notification {

                        background-color:#fbfbfb !important;

                        color:#666 !important;

                        border-bottom-color: #eee !important

                    }





#left_column #posts .notification .block {

                            color:#333 !important;

                        }



#left_column #posts .notification.alt {

                        background-color: #f7f7f7 !important;

                    }

#left_column #posts .notification.last_notification,

                    #left_column #posts .notification.single_notification {

                        border-bottom-color:#ececec !important;

                    }

#left_column #posts .notification a {

                        color:#565656 !important;

                        border-bottom:0px !important;

                    }



#left_column #posts .notification blockquote {

                        border-left-color:#eee !important;

                    }



#left_column #posts .notification .avatar {

                        border-color:transparent !important;

                        background-color:#fbfbfb !important;

opacity:0.5 !important;

                    }



#left_column #posts .notification .avatar:hover{

opacity:1 !important;

                    }

#header_container{

color:transparent !important;

}

.dashboard_switch_blog_menu_header{

background-color:#fbfbfb !important;

color:#666 !important;

}

.dashboard_switch_blog_menu_item{

background-color:#fbfbfb !important;

color:#666 !important;

border:0px !important

}

.dashboard_switch_blog_menu_footer{

background-color:#fbfbfb !important;

color:#666 !important;

}

.dashboard_switch_blog_menu_wrapper{

background-color:#fbfbfb !important;

}



#new_post_notice_container{

color:#fff !important

}



#new_post_notice_container a{

color:#fff !important

}



#dashboard_switch_blog_menu_current,

#dashboard_switch_blog_arrow_and_divider,

#dashboard_switch_blog_arrow_current

{

background-image:none !important

}

.notification_type_icon{

display:none !important

}





h1,h2,h3,h4,h5,h6{color:#555555 !important}



a:{color:white !important}


ol#posts li.post.new_post {

background: #ffffff url('http://i46.tinypic.com/257oe84.jpg') center center no-repeat;

}



img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{

    width:0;

    height:70px;

    padding-right:250px;

    opacity: 0;

}

#logo {

height: 0 !important;

	width: 0 !important;

	/* these numbers match the new image's dimensions */

	padding-left: 230px !important;

	padding-top: 64px !important;

	background: url(http://i29.tinypic.com/2mruzo6.jpg) no-repeat !important;



}

.sub_avatar{width:32px !important; height:32px !important; background-color:transparent !important}

}
