css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
  url-prefix("http://stadtvater.ares68.plusserver.de/") {
body {
	background: url(http://forum.mystic-legends.de/images/ml-dark-style/background2.jpg) !important;
	color: #e0e0e0 !important;
}
body a:link, body a:visited, body a:active, body a:hover, body a font {
	color: #ffffff !important;
}
body table {
	border-collapse: collapse !important;
	border-color: #000000 !important;
}
hr {
	border: 1px #e0e0e0 solid !important;
	height: 1px !important;
	margin: 10px !important;
}
a:hover {
	text-decoration: none !important;
}
input, select {
	font-size: 0.9em !important;
	background: #f0f0f0 !important;
}
body table tr td font {
	color: #d0d0d0;
}
}

@-moz-document
  url-prefix("http://stadtvater.ares68.plusserver.de/cgi-bin/stadt.pl"),
  url("http://stadtvater.ares68.plusserver.de/logo.shtml"),
  url("http://stadtvater.ares68.plusserver.de/rechts.htm") {
body center a img {
	border: 1px #000000 solid !important;
}
table, table tr td, table tr th {
	border: 1px #000000 solid !important;
	color: #000000 !important;
}
table tr th, table tr:first-child td {
	/* background: #629462 !important; */
	background: #aa8833 !important;
}
table tr td {
	/* background: #73a573 !important; */
	background: #ccaa55 !important;
}
table tr td img {
	border: none !important;
}
table tr td a:link, table tr td a:visited, table tr td a:active, table tr td a:hover {
	color: #000077 !important;
}
table tr td center font i {
	display: block !important;
	text-align: center !important;
	background: #e0e0e0 !important;
}
body table tr td font {
	color: #202020 !important;
}
}
