css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {

#pageHead {
position:relative;
}

#pageLogo {
top:0;
left:0;
}

#pageLogo a {
background:none !important;
background:#6B88BF !important;
width:24px !important;
height:31px !important;
margin-top:-2px;
margin-right:2px;
}

#pageLogo a:hover {
background:#3B5998 !important;
}

#blueBar {
background:none !important;
border:none !important;
}

#headNav {
background:none !important;
border:none !important;
}

#jewelCase {
top:0px !important;
left:40px !important;
}

#jewelCase .jewelBox {
border-color:#ccc !important;
left:2px !important;
top:31px !important;
}

#jewelCase .jewel {
border:none !important;
width:29px !important;
}

#jewelNotif:hover, #jewelMail:hover, #jewelRequest:hover {
background-color:#fff !important;
}

.jewelToggler, .jewelToggler:hover, .jewelToggler:active {
background-image:none !important;
background:none !important;
}

.jewelCount {
display:block !important;
position:relative !important;
text-align:center !important;
border:none !important;
background:none !important;
}

.jewelCount span {
-moz-border-radius:0 !important;
-webkit-border-radius:0 !important;
background-color:#ccc !important;
border:none !important;
text-align:center !important;
margin:0 auto !important;
padding:10px !important;
}



#pageNav li a {
color:#aaa !important;
}
#pageNav li a:hover {
background:none!important;
color:#ccc !important;
}

#pagelet_eventbox, #pagelet_connectbox {
display:none !important;
}

.UIImageBlock_Image .img, img.UIImageBlock_Image, i.UIImageBlock_Image {
display:none !important;
}

.uiSideNav .item, .uiSideNav .subitem {
padding:3px 4px 4px 4px !important;
}

#sideNav span.imgWrap {
display:none !important;
}

form.commentable_item {
margin-left:25px !important;
}

.uiUfi .ufiItem {
background-color:#eee !important;
}

#rightCol {
width:0 !important;
padding:0 !important;
margin:0 !important;
display:none !important;
}

#bookmarks_menu {
display:none !important;
}

.fbDock {
display:none !important;
}

#fbFriendsSidebar {
display:none !important;
}

#navSearch .uiTypeahead, #navSearch .uiTypeahead .wrap {
border-color:#ccc !important;
}

.uiStreamSource .img {
display: none !important;
}

.uiListLight {
border-color:#aaa !important;
}

#pageHead {
/*background:url(http://www.google.com/intl/en_ALL/images/srpr/logo1w.png) center 15px no-repeat;
height:160px !important;*/
}

#headNavOut {
margin-left:340px !important;
}

#navSearch {
top: 7px !important;
left: 236px !important;
}

#contentCol {
border-left:none !important;
}
#mainContainer {
border-right:none !important;
margin-top:20px !important;
}

#content {
margin-top:15px !important;
}

#pagelet_ads {
display:none !important;
}

#right_column {
width:720px !important;
}

.profile_two_columns #tab_canvas {
width:720px !important;
}

#boxes_left {
display:none !important;
}

#left_column div.actions {
display:none !important;
}

.UIProfileImage_LARGE {
display:none !important;
}

.UIIntentionalStory {
padding-left:7px !important;
}

.profile .profile_top_wash {
background:none !important;
}

#left_column .profileimage {
opacity:.2 !important;
-webkit-transition:1s opacity !important;
}

#left_column .profileimage:hover {
opacity:.5 !important;
}

#jewelCase .openToggler {
margin-top:13px !important;
}

}
