css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www15.plala.or.jp/uhyo_/javascript") {

body {
  position: relative;
  width: 600px;
  margin-left: auto !important;
  margin-right: auto !important;
  background: #101010 !important;
  color: #EFEFEF !important;
  font-family: 'ãƒ’ãƒ©ã‚®ãƒŽè§’ã‚´ Pro W3', 'Hiragino Kaku Gothic Pro', 'Trebuchet MS', Arial, 'ï¼­ï¼³ ï¼°ã‚´ã‚·ãƒƒã‚¯', sans-serif;
  line-height: 1.6;
  letter-spacing: 1px;
  font-size: 14px !important;
}

/**
 * Top page
 */
div.top {
  text-indent: 0 !important;
  background: #101010 !important;
  color: #EFEFEF !important;
}

ul.menu {
  overflow: hidden;
  padding: 0 !important;
  background: none !important;
  font-size: 12px !important;
}

ul.menu > li > ul {
  padding: 0 15px !important;
}

ul.recent,
ol {
  width: 580px;
  border: 1px solid #393939 !important;
  background: #202020 !important;
  color: #F0F0F0 !important;
}

/**
 * Navigation
 */
h1 + p {
  position: fixed;
  top: 20px;
  left: 50%;
  width: 120px;
  margin: 0 0 0 330px;
  padding: 10px;
  -moz-border-radius: 10px;
  -webkit-border-radius: 10px;
  color: #323232;
  background: #323232;
}

h1 + p a {
  display: block;
  float: left;
  line-height: 1.8;
}

h1 + p a:not(:first-child) {
  width: 120px;
}

h1 + p a:nth-child(2):before {
  content: "<<";
}

h1 + p a:nth-child(3):before {
  content: ">>";
}

/**
 * Elements
 */
a {
  color: #999 !important;
}

a:hover,
a:active {
  text-decoration : none !important;
}

h2 {
  padding: 0 !important;
  border: 0 !important;
  font-size: 1.6em !important;
  color: #693;
}

h3 {
  border: 0 !important;
  padding: 0 !important;
  font-size: 1.3em !important;
  color: #369 !important;
  font-weight: bold !important;
}

h3:before {
  margin: 0 2px 0 0;
  content: "â—";
  color: #369;
  font-size: 1.3em;
}

h4 {
  padding: 0 !important;
  border: 0 !important;
  font-size: 1.1em !important;
  color: #369 !important;
  font-weight: bold !important;
}

h4:before {
  margin: 0 2px 0 0;
  content: "â—";
  color: #369;
  font-size: 1.1em;
}

p {
  margin-bottom: 1.5em !important;
}

img.ss {
  margin: 0 15px 1.5em 0 !important;
}

dfn,
kbd {
  color: #963;
}

var {
  color: #369;
}

strong {
  color: #963;
}

code {
  display: block;
  white-space: pre;
  width: 600px !important;
  margin: 1.5em 0 !important;
  padding: 7px !important;
  border: 0 !important;
  background: #202020 !important;
  color: #F0F0F0 !important;
  font-family:
    'ãƒ’ãƒ©ã‚®ãƒŽè§’ã‚´ Pro W3', 'Hiragino Kaku Gothic Pro', osaka,
    'VL ã‚´ã‚·ãƒƒã‚¯', 'ï¼­ï¼³ ã‚´ã‚·ãƒƒã‚¯', sans-serif !important;
  overflow-x: scroll;
  overflow-y: visible;
  line-height: 1.4;
}

q {
  background: #222 !important;
}

q:before, q:after {
  background: #101010 !important;
}

q:before {
  padding-right: 2px;
  content: "â€œ";
}

q:after {
  padding-left: 2px;
  content: "â€";
}


}
