css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.pentaho.com") {

    body , body div {
       font-size:14px !important;
       font: 14px/1.231 Arial,Verdana,helvetica,clean,sans-serif !important;
       font-family: Arial,Verdana,helvetica,clean,sans-serif !important;

    }

    .threadbit .nonsticky a.threadstatus, .threadbit .sticky a.threadstatus {
        width: 10px !important;
    }

    .threadbit .alt {
        background:inherit !important;
    }

    #thread_info .options_block_container, .threaddetailicons, #navtabs .floatcontainer, #globalsearch .navbar_advanced_search, #forum_ad, #forum_ad_title, #pentaho_header, #footer_wrapper, #forum_info_options .options_block_container, #forumdisplay_navpopup { display:none !important; }
    .navbar {height: 15px !important;}
    
    .pagination span a {border: 1px solid #FFFFFF !important;}

    .threadlisthead, .postbit .posthead, .postbitlegacy .posthead, .eventbit .eventhead {
        background: none;
    }

    .threadbit .sticky {
        background: none !important;
    }
    
    .threadbit {
        -moz-box-shadow:none !important;
        -webkit-box-shadow:none !important;
    }

    .threadbit .nonsticky, .threadbit .discussionrow {
        background: none !important;
    }

    .postbit .posthead, .postbitlegacy .posthead, .eventbit .eventhead, .threadlisthead, .forumhead, .blockhead {
    background-color:#A5BC65;
    border-color:#949E3E;
    }

    

}
