/*----------Back & Forward Button----------*/

#back-button ,#nav-bar[saved-iconsize]  #back-button,#back-button:hover ,#nav-bar[saved-iconsize] #back-button:hover,#back-button:active,#nav-bar[saved-iconsize] #back-button:active,#back-button[disabled="true"],#nav-bar[saved-iconsize] #back-button[disabled="true"],#forward-button,#nav-bar[saved-iconsize] #forward-button,#forward-button:hover,#nav-bar[saved-iconsize] #forward-button:hover,#forward-button:active,#nav-bar[saved-iconsize] #forward-button:active,#forward-button[disabled="true"],#nav-bar[saved-iconsize] #forward-button[disabled="true"] 
{
list-style-image: url("data:image/png;base64,") !important;
}

#back-button ,#nav-bar[saved-iconsize]  #back-button 
{
-moz-image-region: rect(28px 28px 56px 0px) !important; 
}

#back-button:hover ,#nav-bar[saved-iconsize] #back-button:hover
{
-moz-image-region: rect(56px 28px 84px 0px) !important;
}

#back-button:active,#nav-bar[saved-iconsize] #back-button:active
{
-moz-image-region: rect(84px 28px 112px 0px) !important;
}

#back-button[disabled="true"],#nav-bar[saved-iconsize] #back-button[disabled="true"] 
{
-moz-image-region: rect(0px 28px 28px 0px) !important ;
}

#forward-button,#nav-bar[saved-iconsize] #forward-button
{
-moz-image-region: rect(28px 56px 56px 28px) !important;
}

#forward-button:hover,#nav-bar[saved-iconsize] #forward-button:hover 
{
-moz-image-region: rect(56px 56px 84px 28px) !important;
}

#forward-button:active,#nav-bar[saved-iconsize] #forward-button:active
{
-moz-image-region: rect(84px 56px 112px 28px) !important;
}

#forward-button[disabled="true"],#nav-bar[saved-iconsize] #forward-button[disabled="true"]
{
-moz-image-region: rect(0px 56px 28px 28px) !important ;
}

/*----------Back List----------*/

#back-forward-dropmarker 
{
-moz-appearance: none  !important;
list-style-image: url("data:image/png;base64,") !important;
-moz-image-region: rect(0px 48px 24px 24px) !important;
border:0 !important ;
padding:0 !important  ;
margin:0 !important;
}

#back-forward-dropmarker[disabled="true"],#back-forward-dropmarker[disabled="true"]:hover,#back-forward-dropmarker[disabled="true"]:active,#back-forward-dropmarker[disabled="true"]:hover:active 
{
-moz-image-region: rect(0px 24px 24px 0px) !important;
}

#back-forward-dropmarker:hover
{
-moz-image-region: rect(0px 72px 24px 48px) !important;
}

#back-forward-dropmarker:hover:active
{
-moz-image-region: rect(0px 96px 24px 72px) !important;
}

/*----------Reload Button----------*/

#reload-button,#reload-button:not([disabled="true"]):hover,#reload-button[disabled="true"],#reload-button:not([disabled="true"]):hover:active
{
-moz-appearance: none !important;
width: 26px !important;
padding: 0px !important;
margin: 0px !important;
margin-left: -4px !important;
border: none !important;
}

#reload-button > .toolbarbutton-icon 
{
list-style-image: url("data:image/png;base64,") !important;
-moz-image-region: auto !important;
margin:0px !important;
padding: 0px !important;
border: none !important;
}

/*----------Stop Button----------*/

#stop-button,#stop-button:not([disabled="true"]):hover,#stop-button[disabled="true"],#stop-button:not([disabled="true"]):hover:active 
{
-moz-appearance: none !important;
width: auto !important;
padding: 0px !important;
margin: 0px !important;
margin-right: 5px !important;
border: 0 !important;
}

#stop-button > .toolbarbutton-icon
{
list-style-image: url("data:image/png;base64,") !important;  
-moz-image-region: auto !important;
margin:0px !important;
padding: 0px !important;
border: 0 !important;
}

/*----------Bookmarks----------*/

#PersonalToolbar 
{
-moz-appearance: none !important;
background: #FFF url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAaCAIAAAD5ZqGGAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAEJJREFUeNo8i8EJwDAMxGxxIYbOlk+h+4+RPovjNAd6CHSM+yHiovdArSEJc8c3JHDczFK1WV5FzdP/to68c34CDACa0QSoVM+4DAAAAABJRU5ErkJggg==") repeat-x bottom !important;
border:1px solid red;
color:#fff;
}

#personal-bookmarks toolbarbutton:hover
{
-moz-appearance: none !important;
border:0 !important;
}

#personal-bookmarks toolbarbutton
{
padding: 0 10px !important;
}

.bookmark-item .toolbarbutton-text 
{
color:#fff !important;
text-shadow:black 0px 0px 10px;
}

.bookmark-item .toolbarbutton-icon 
{
display: none !important;
}
