css:
/*
name:       deviantSEMI (N0NNAKED) v7 (rotane)
version:    0.1
author:     rotane.deviantart.com
concept:    boffinbrain.deviantart.com
created:    september 4, 2010 - v0.1
short info: semi-naked comments based on BoffinbraiN's idea: http://fav.me/d2ohxb1 and rotane's script: http://fav.me/d2kpbvr
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {

/* commentboxes */
#deviantART-v7 #gmi-CComment > div.ch-ctrl
{ background: #B5BFB5 !important; margin: 0 0 -5px 0 !important; border: 1px solid #A6B2A6 !important; -moz-border-radius: 6px !important; padding: 0 !important; }
#deviantART-v7 #gmi-CComment > div.ch-ctrl .cc-in
{ border: none !important; min-height: 81px !important;  background: #DDE5D8 !important; }
#deviantART-v7 #gmi-CComment > div.ch-ctrl span.cc-avatar
{ margin: 7px 15px 0 9px !important; }
#deviantART-v7 #gmi-CComment > div.ch-ctrl a.reply
{ margin: 0px 0 5px 6px !important; }

/* speechbubblearrow fix */
#deviantART-v7 #gmi-CComment > div.ch-ctrl > i.l
{ margin-left: 3px !important; background: #B5BFB5 url(http://st.deviantart.net/minish/comments/bubble_arrow.gif) no-repeat top left !important; }

/* speechbubblearrow fix in groups */
#deviantART-v7 .bpp-private-thread #gmi-CComment > div.ch-ctrl > i.l
{ margin-left: 4px !important; }

/* fixes for 'marked as spam' posts */
#deviantART-v7 #gmi-CComment > div.ch-ctrl .creason
{ color: #506256 !important; font-size: 11px !important; background: transparent !important; }
#deviantART-v7 #gmi-CComment > div.ch-ctrl .creason em
{ text-decoration: underline !important; }

/* fix for easyreplybox in message centre */
#deviantART-v7 #messages .messages-right .mcbox div.ch-ctrl
{ -moz-border-radius-topleft: 0 !important; -moz-border-radius-topright: 0 !important; }
#deviantART-v7 #messages .messages-right .mcbox div.ch-ctrl:first-child
{ -moz-border-radius: 6px !important; }



/* artist's comments/desciption */
#deviantART-v7 #artist-comments .gr-headless .hh 
{ border: 1px solid #A6B2A6 !important; -moz-border-radius: 6px !important; margin-left: 17px !important; margin-top: 11px !important; margin-right: -14px !important; margin-bottom: -19px !important; padding-top: 0px !important; background: url(http://st.deviantart.net/minish/gruzecontrol/gmtop.gif?1) top left repeat-x #DAE4D9 !important; }
#deviantART-v7 #artist-comments .gr-headless .hh > div 
{ border-top: 1px solid #F2FCF1 !important; -moz-border-radius: 6px !important; padding-top: 10px !important; }
#deviantART-v7 #artist-comments .gr-headless .cc-name img 
{ margin-top: 4px !important; margin-left: 4px !important; }

/* artist's comments/desciption speechbubblearrow fix */
#deviantART-v7 #artist-comments .gr-headless .gr i.l
{ background: url(http://st.deviantart.net/minish/deviation/artist-comment-l.gif?2) 0px 0px no-repeat !important; position: relative !important; z-index:99 !important; float: left !important; left: 6px !important; top: 16px !important; }

/* artist's comments/desciption group b focusgroup fix */
#deviantART-v7.dev-test-b #artist-comments .gr-headless .text-ctrl 
{ margin-left: 0px !important; }

}
