@-moz-document domain("mixi.jp") {
.emoji{display:none !important}
p.adBanner, div#diaryComment div.diaryBottomAd{display:none !important}
#bodySub, li.home, li.mikly, li.review, li.news{display:none !important}
body,div#headerArea{width:720px !important}
div#headerArea,div#bodyArea,div#footerArea, div#footerArea p,div#footerArea ul{width:720px !important}
p.statusIcon{display:none !important;}
ul.localNaviHome li{width:75px !important;background-position:15px 0% !important;}
ul.localNaviFriend li{width:83px !important;background-position:15px 0% !important;}
a#diaryPullDownButton,a#photoPullDownButton,a#videoPullDownButton{display:none !important}
}
