css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fnb.co.za") {
	/* general forms */

	.inputWrapper input {
		background-image: none !important;
		background-color: #F8FDFD !important;
		color: #004F4F !important;
		padding: 0 0 0.4em 0 !important;
		text-indent: 0 !important;
		letter-spacing: 0 !important;
		font-family: Arial, Helvetica, sans-serif !important;
		font-weight: normal !important;
		height: 1.9em !important;
	}
	
	textarea {
		background-image: none !important;
		background-color: #F8FDFD !important;
		color: #004F4F !important;
		padding: 0 !important;
		text-indent: 0 !important;
		letter-spacing: 0 !important;
		font-family: Arial, Helvetica, sans-serif !important;
		font-weight: normal !important;
	}
	
	.textareaWrapper, .inputWrapper {
		background-image: none !important;
		background-color: #F8FDFD !important;
		border: 1px solid #67CFCF !important;
	}
	
	.textareaWrapper {
		padding: 0.2em !important;
	}
	
	.textareaWrapper2 {
		left: 0 !important;
	}
	
	div#remLen {
		display: inline !important;
	}

	.selectWrapper, .selectWrapperrrr {
		border: 1px solid #67CFCF !important;
		height: 2em !important;
	}

	.selectWrapper2rrrr {
		height: 2em !important;
	}

	.selectWrapper select, .selectWrapper2rrrr select {
		color: #004F4F !important;
		font-weight: normal !important;
		font-family: Arial, Helvetica, sans-serif !important;
	}

	.formButtonInput {
		color: #fff !important;
		background-color: #01AAAD !important;
		font-family: Arial, Helvetica, sans-serif !important;
		font-weight: bold !important;
		padding: 0.2em 0.5em !important;
		margin-top: 0.3em !important;
	}

	.formButtonInput:hover {
		background-color: #007F7F !important;
	}

	.genButtons .formButtonInput {
		margin-top: 0 !important;
	}

	form .formButton {
		float: left !important;
		padding: 0 !important;
		margin-right: 0.5em !important;
	}

	html body .genericLeadForm .formButton {
		border-left: none !important;
	}

	/* call back form */

	#bodysection .radioWrapper {
		border: 1px solid #67CFCF !important;
		margin-top: 0.4em !important;
	}

	/* login form in header */

	.userfield input {
		padding: 0 0.3em !important;
	}

	/* search form in header */

	#searchFeilds input {
		padding: 0 0.3em !important;
	}

	/* miscellaneous */
	
	html body #bodysection .genericLeadForm .formButtonaction input, html body #floatbox2 .formButtonaction input {
		background-image: none !important;
	}
}
