css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("minichan.org") {

 body{

  background-color:#F39CF0 !important;

  color:#0EF6DA !important;

  font-family:Comic Sans MS;

  font-size:13px;

  padding:0 1em;

  margin:0px;

  }

  .body{

   background-color:#E8D440 !important;

   line-height:1.5em;

   margin:0;

   overflow:hidden;

   padding:.5em;

   white-space:normal;

   word-wrap:break-word;

  }

  .body img{

   border:none;

   float:left;

   margin-bottom:6px;

   margin-right:7px;

  }

  .body ul.menu{

   clear:both;

   margin:.5em 0 0;

   text-align:right;

  }

   .body ul.menu li{

    margin:0 0 0 .5em;

   }



 .codebox{

         overflow: auto;

         display: block;

         height: auto;

         max-height: 250px;

         white-space: normal;

         padding-top: 5px;

         font: 0.9em monaco, "andale mono","Comic Sans MS", courier, mono;

         line-height: 1.3em;

         color: #D79EDD;

         margin: 3px 0;

 }



 span.quote{

  color:#e20 !important;

 }



 .highlighted{

  background-color:#F5EDEE;

 }



 .spoiler{

  background-color:#F90A10 !important;

 }

  .spoiler:hover{

   color:#fff;

  }



 .border{

  background-color:#ffa !important;

  border:3px dashed #cc5 !important;

  margin:1em;

  padding:.5em;

 }



 .highlight{

  background-color:#4DAD51;

  font-style:normal;

  font-weight:800;

 }



 a{

  color:#631B1D !important;

 }



 #notice{

  background-color:#5E6973;

  border:2px solid #95d38e;

  padding:.5em;

 }



 h1{

  font-size:50px;

  text-align:center;

 }

  h1 a{

   color:#000;

   text-decoration:none;

  }



 h2{

  font-size:16px;

 }

  h2 a:hover{

  text-decoration:underline;

  }



 h3{

  background-color:#fa5 !important;

  font-size:12px;

  font-weight:400;

  margin:0;

  padding:.5em;

 }



 .reply_id{

  float:right;

  text-align:right;

  vertical-align:middle;

 }



 h4.section{

  font-size:15px;

  margin-bottom:7px;

 }



 h4.user{

  font-size:14px;

  margin:0 0 .5em;

 }



 label.common{

  float:left;

  padding-right:.5em;

  text-align:right;

  width:15em;

 }



 input,textarea{

  display:block;

 }



 textarea{

  font-family:Comic Sans MS;

  width:100%;

 }



 input[type="text"]:focus,input[type="password"]:focus,textarea:focus{

  outline:1px solid #eaffe8;

 }



 fieldset{

  margin:1em 0;

 }



 ul.body,ul.error_message{

  list-style-type:none;

 }



 ul.error_message{

  margin:0;

  padding:0;

 }

  ul.error_message li{

   font-style:italic;

   padding:.2em;

  }



 ul.menu{

  margin:1em;

  text-align:center;

 }

  ul.menu li{

   display:inline;

   font-weight:700;

   margin:0 1em;

  }



 ul#main_menu{

  background-color:#ffeeaa !important;

  margin:0 0 1em;

  padding:.5em;

 }



 ul.stuff{

  margin-bottom:2em;

 }

  ul.stuff li{

   margin-bottom:.5em;

  }



 table{

  border-collapse:collapse;

  margin-bottom:1em;

  width:100%;

 }

  table th{

   background-color:#fea !important;

   padding:.5em;

   text-align:left;

  }

  table td{

   background-color:#FFABFB !important;

   padding:.5em;

   vertical-align:top;

  }

  table tr.odd td{

   background-color:#ffeab0 !important;

  }

  table tr:hover td{

   background-color:#ffb !important;

  }

  table .minimal{

   white-space:nowrap;

   width:4%;

 }



 .profile_fix{

         overflow:hidden;

         white-space:normal;

         word-wrap: break-word;

 }



 .reply_body_snippet,.snippet{

  font-style:italic;

 }



 .topic_headline a:visited{

  font-weight:400;

 }



 .topic_info{

  float: right;

  cursor: default;

 }



 em.marked{

  background-color:#ff9;

  font-style:normal;

  font-weight:770;

 }



 p.caption{

  font-size:11px;

  margin-top:2px;

 }



 .inline{

  display:inline;

 }



 #last_seen_marker{

  border-top:2px solid #fe0 !important;

 }



 #snapback_link{

  left:2px;

  position:fixed;

  top:50%;

 }



 .video,video,a.video{

  clear:both;

  margin:10px 0;

 }



 .standalone,.row{

  margin-bottom:1em;

 }



 .body ul.menu li,.body ul.menu li a,.body ul.menu li span,.unimportant,.unimportant *{

  font-size:15px;

 }



 a:hover,h2 a,#snapback_link a{

  text-decoration:none;

 }



 a.youtube_alternate,.noscreen{

  display:none;

 }



 h3#preview,h3#error,h3#replying_to,label,legend,.topic_headline a,.reply_body_snippet a,.snippet a{

  font-weight:700;

 }



 .help,.help_cursor{

  cursor:help;

 }

}
