css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("minichan.org") {
 body{
  background:#ccf !important;
  color:#012 !important;
  font-family:nina, sans-serif !important;
  font-size:12px;
  padding:0 1em;
  margin:0px;
  }
  .body{
   -moz-border-radius-bottomleft:1em; -webkit-border-bottom-left-radius:1em;
   -moz-border-radius-bottomright:1em; -webkit-border-bottom-right-radius:1em;
   background-color:transparent !important;
   border:1px solid #000 !important;
   border-top:0 !important;
   line-height:1.5em;
   margin:0;
   overflow:hidden;
   padding:.5em !important;
   white-space:normal;
   word-wrap:break-word;
  }
  h3:hover{
   background-color:#fff !important;
   color:#000 !important;
  }
  .body:hover, h3:hover + .body{
   background-color:#eaeaff !important;
   border:1px solid #fff !important;
   border-top:none !important;
  }
  /* w3 seriously needs to add a fucking previous element selector */

  .body img{
   border:none;
   float:left;
   margin:-.5em 7px 0 -.5em !important;
  }
  .body ul.menu{
   clear:both;
   margin:.5em 0 0;
   text-align:right;
  }
   .body ul.menu li{
    margin:0 0 0 .5em;
   }

 .codebox{
  -moz-border-radius:1em; -webkit-border-radius:1em;
  background-color:#aac !important;
  overflow: auto;
  display: block;
  height: auto;
  max-height: 250px;
  white-space: normal;
  padding: 5px;
  font: 0.9em monaco, "andale mono","courier new", courier, mono;
  line-height: 1.3em;
  color: #00f !important;
  margin: 3px 0;
 }

 span.quote{
  -moz-border-radius:1em; -webkit-border-radius:1em;
  background-color:#000 !important;
  color:#aac !important;
  padding-left:3px !important;
  padding-right:3px !important;
 }
  span.quote strong:first-child{
   display:none !important;
  }
  span.quote a{
   color:#FF0 !important;
  }

 .highlighted{
  border:1px solid #fff !important;
  border-top:none !important;
  background-color:#eaeaff !important;
 }

 .spoiler{
  -moz-border-radius:1em; -webkit-border-radius:1em;
  background-color:transparent !important;
  border:1px solid #fff !important;
  color:transparent !important;
  padding-left:3px !important;
  padding-right:3px !important;
 }
  .spoiler:hover{
   background-color:#fff !important;
   color:#00f !important;
  }

 .border{
  -moz-border-radius:1em; -webkit-border-radius:1em;
  background-color:#aac !important;
  border:none !important;
  color:#000 !important;
  margin:1em;
  padding:.5em;
 }

 .highlight{
  -moz-border-radius:1em; -webkit-border-radius:1em;
  background-color:#000 !important;
  border:1px solid #ff0 !important;
  color:#ff0 !important;
  font-style:normal;
  font-weight:700;
  padding-left:3px !important;
  padding-right:3px !important;
 }

 a{
  color:#000;
 }

 #notice{
  -moz-border-radius:1em; -webkit-border-radius:1em;
  background-color:#000 !important;
  border:none !important;
  color:#ccf !important;
  padding:.5em;
 }
  a#notice{
   color:#ff0 !important;
  }

 h1{
  font-size:17px;
  text-align:left !important;
 }
  h1 a{
   color:#fff !important;
   text-decoration:none;
  }

 h2{
  font-size:16px;
 }
  h2 a:hover{
  text-decoration:underline;
  }

 h3{
  -moz-border-radius-topleft:1em; -webkit-border-top-left-radius:1em;
  -moz-border-radius-topright:1em; -webkit-border-top-right-radius:1em;
  background-color:#000 !important;
  color:#ccf !important;
  font-size:12px;
  font-weight:400;
  margin:0;
  padding:.5em;
 }
  h3 a{
   color:#ff0 !important;
  }

 .reply_id{
  float:right;
  text-align:right;
  vertical-align:middle;
 }

 h4.section{
  font-size:15px;
  margin-bottom:7px;
 }

 h4.user{
  font-size:14px;
  margin:0 0 .5em;
 }

 label.common{
  float:left;
  padding-right:.5em;
  text-align:right;
  width:15em;
 }

 input,textarea{
  display:block;
 }

 textarea{
  font-family:monospace;
  width:100%;
 }

 input[type="text"]:focus,input[type="password"]:focus,textarea:focus{
  outline:1px solid #eaffe8;
 }

 fieldset{
  margin:1em 0;
 }

 ul.body,ul.error_message{
  list-style-type:none;
 }

 ul.error_message{
  margin:0;
  padding:0;
 }
  ul.error_message li{
   font-style:italic;
   padding:.2em;
  }

 ul.menu{
  margin:1em;
  text-align:center;
 }
  ul.menu li{
   display:inline;
   font-weight:700;
   margin:0 1em;
  }

 ul#main_menu{
  -moz-border-radius:1em; -webkit-border-radius:1em;
  background-color:transparent !important;
  border:1px solid #fff !important;
  border-left:none !important;
  float:right;
  margin:0 0 1em;
  padding:.5em;
 }

 ul.stuff{
  margin-bottom:2em;
 }
  ul.stuff li{
   margin-bottom:.5em;
  }

 table{
  border-collapse:collapse;
  margin-bottom:1em;
  width:100%;
 }
  table th{
   -moz-border-radius-topleft:1em; -webkit-border-top-left-radius:1em;
   -moz-border-radius-topright:1em; -webkit-border-top-right-radius:1em;
   background-color:#000 !important;
   color:#ccf !important;
   padding:.5em;
   text-align:left;
  }
  table td{
   background-color:transparent !important;
   padding:.5em;
   vertical-align:top;
  }
  table tr.odd td{
   background-color:#eaeaff !important;
  }
  table tr:hover td{
   background-color:#eafaff !important;
  }
  table .minimal{
   white-space:nowrap;
   width:1%;
 }

 .profile_fix{
         overflow:hidden;
         white-space:normal;
         word-wrap: break-word;
 }

 .reply_body_snippet,.snippet{
  font-style:italic;
 }

 .topic_headline a:visited{
  font-weight:400;
 }

 .topic_info{
  float: right;
  cursor: default;
 }

 em.marked{
  background-color:#ff9;
  font-style:normal;
  font-weight:700;
 }

 p.caption{
  font-size:11px;
  margin-top:2px;
 }

 .inline{
  display:inline;
 }

 #last_seen_marker{
  border-top:2px solid #000 !important;
 }

 #snapback_link a{
  -moz-border-radius-topright:1em; -webkit-border-top-rightradius:1em;
  -moz-border-radius-bottomright:1em; -webkit-border-bottom-right-radius:1em;
  background-color:#000 !important;
  color:#000 !important;
  left:0 !important;
  padding:3px !important;
  position:fixed;
  top:50%;
 }

 .video,video,a.video{
  clear:both;
  margin:10px 0;
 }

 .standalone,.row{
  margin-bottom:1em;
 }

 .body ul.menu li,.body ul.menu li a,.body ul.menu li span,.unimportant,.unimportant *{
  font-size:10px;
 }

 a:hover,h2 a,#snapback_link a{
  text-decoration:none;
 }

 a.youtube_alternate,.noscreen{
  display:none;
 }

 h3#preview,h3#error,h3#replying_to,label,legend,.topic_headline a,.reply_body_snippet a,.snippet a{
  font-weight:700;
 }

 .help,.help_cursor{
  cursor:help;
 }
}
