css:
menubar {
background-image: -moz-linear-gradient(bottom,#fcfcfc, #f8f8f8 100%) !important;
}

#nav-bar {
margin: -4px 0 -7px 0 !important;
background-image: -moz-linear-gradient(bottom,#ddd, #fcfcfc 100%) !important;
}

#mb-pagetools-container {
border-color: #aaa #ccc #ccc #999 !important;
margin-top: 5px !important;
min-height: 21px !important;
max-height: 21px !important;
}

#mb-pagetools-container:hover {
border-color: #888 #bbb #bbb #777 !important;
}

#PersonalToolbar {
background: #e0e0e0 !important;
margin-bottom: -4px !important;
}

#PersonalToolbar * {
color: #999 !important;
margin: -3px 0 !important;
}

radio[pane]:hover,
#viewGroup radio:hover {
color: #fff !important;
background-image: -moz-radial-gradient(center 45deg, circle closest-corner, rgba(255,255,255,1) -100%, rgba(10,140,140,1)125%) !important;
}

radio[pane][selected="true"],
#viewGroup radio[selected="true"] {
color: #fff !important;
background-image: -moz-radial-gradient(center 45deg, circle closest-corner, rgba(255,255,255,1) -100%, rgba(10,140,140,1)125%) !important;
}

richlistitem[selected="true"] {
color: #333 !important;
background-color: #ddd !important;
}

#sidebar {
border-right: 1px solid #aaa !important;
}

tabs {
height: 20px !important;
}

.tabbrowser-tabs {
background-image: -moz-linear-gradient(bottom,#f9f9f9, #e0e0e0 100%) !important;
}

.tabbrowser-tab {
margin-right: -3px !important;
border-color: #aaa #ccc #ccc #999 !important;
-moz-border-radius-topleft: 3px !important;
-moz-border-radius-topright: 3px !important;
}

.tabbrowser-tab:hover, .tabbrowser-tab[selected="true"], .tabs-newtab-button:hover {
border-color: #888 #bbb #bbb #777 !important;
}

.tabbrowser-tab:first-child {
margin-left: 0 !important;
}

.tabbrowser-tab > .tab-text, .tabbrowser-tab > .tab-icon-image {
margin-top: -5px !important;
}

.tabbrowser-tab .tab-close-button {
margin-bottom: 4px !important;
}

.tabs-newtab-button {
border: 1px solid !important;
border-color: #aaa #ccc #ccc #999 !important;
margin: 0px 0 4px 4px !important;
-moz-border-radius-topleft: 3px !important;
-moz-border-radius-topright: 3px !important;
padding: 0 2px !important;
background-color: #e6e6e6 !important;
}

.tabbrowser-arrowscrollbox ~ .tabs-newtab-button {
margin: 0 4px !important;
}

.tabs-bottom {
border-color: #aaa !important;
}

#urlbar, #search-container > #searchbar > .searchbar-textbox {
border-color: #aaa #ccc #ccc #999 !important;
-moz-border-radius: 3px !important;
}

#urlbar[focused="true"], #search-container > #searchbar > .searchbar-textbox[focused="true"] {
border-color: #888 #bbb #bbb #777 !important;
}

#urlbar .autocomplete-textbox-container, .textbox-input-box {
margin-top: -1px !important;
}

#urlbar-container {
margin: 1px 0 !important;
}

#identity-box {
margin: -1px !important;
}

#search-container {
margin: 1px 0 3px 0 !important;
padding-top: 2px !important;
}

.searchbar-textbox {
min-height: 20px !important;
}
