css:
@-moz-document url-prefix("http://www.poneyvallee.com/forum"){

/* fond */
Body { Background-image : url("http://img820.imageshack.us/img820/1174/village2.jpg") !important;
       background-size : 104% !important;
	Background-position : center !important;
	Background-attachment : fixed !important;
        Background-repeat : repeat !important;}
#bg td{background-image: none !important;}
.right{border-style: none !important;}
.left{border-style: none !important;}
.center{border-style: none !important;}

head{background : none !important;}

.pun img[alt="Catitiz_PDT_12"]{list-style: url("http://img836.imageshack.us/img836/6587/testud.jpg") inside !important;}
.pun a[href="search.php"]{list-style: url("http://img836.imageshack.us/img836/6587/testud.jpg") inside !important;}

.pun {
  color: rgb(15,15,150) !important;
  margin-left : -25% !important;
  margin-right : -25% !important;}

/* menus */
#brdmenu{background-color: rgba(89, 146, 209,0.9) !important;
         margin-left:-1px !important;
         margin-right:-1px !important;}
#brdmenu a:link { color: rgb(225,15,15) !important;}
#brdmenu a:visited { color: rgb(225,15,15) !important;}
#brdmenu a:hover { color: white !important;}
#brdmenu a:active { color: white !important;}

#brdwelcome{background-color:rgba(255,255,255,0.3) !important;
-moz-border-radius:10px 10px 10px 10px !important;}

DIV.box,.pun BLOCKQUOTE,DIV.codebox, #adminconsole FIELDSET TH, .rowodd, .roweven,#postreview {
      background-color : rgba(255,255,255,0) !important;
-moz-border-radius:10px 10px 10px 10px !important;
}


.box{border-style :none !important;}
.tcl{-moz-border-radius:10px 0px 0px 10px !important;}
.tcr{-moz-border-radius:0px 10px 10px 0px !important;}

TD.tc2, TD.tc3, TD.tcmod, #postpreview, #viewprofile DD, DIV.forminfo,
#adminconsole FIELDSET TD, DIV.blockmenu DIV.box, #adstats DD {background : transparent !important;}

/* Barres de titres de chaque partie */
.pun H2{background-color: rgba(89, 146, 209,0.9) !important;
        color : rgb(225,15,15) !important;
        -moz-border-radius:10px 10px 10px 10px !important;}

.pun td{border-style : none !important;
        background-color: rgba(255, 255, 255,0.8) !important;
        border-bottom-style: groove !important;
        border-bottom-color: white !important;
        border-bottom-width: 2px !important;}

.pun TH{background-color : rgba(89, 146, 209,0.9) !important;
                color : white !important;}

.linkst,.linksb,.postlinksb{background-color: rgba(255,255,255,0.3) !important;
-moz-border-radius:10px 10px 10px 10px !important;}

/* post, colonne gauche hors avatar et cadre à avatar */
DIV.blockpost DIV.box, DIV.postright, DIV.postfootright {background : transparent !important;}
DIV.postright, DIV.postfootright {BORDER-LEFT-COLOR: transparent !important;}
DIV.postleft, DIV.postfootleft, DIV.blockpost LABEL {background : transparent !important;}

/* fond de la barre à date de chaque post et couleur du #numéro */
DIV.blockpost H2 SPAN.conr {COLOR: white !important;}
.blockpost h2 a:link{color: red !important;}
.blockpost h2 a:visited{color: red !important;}
.blockpost h2 a:hover{color: red !important;}
.blockpost h2 a:active{color: red !important;}


.blockpost{border: 1px groove rgba(255,255,255,1) !important;
	padding: 5px !important;
        background-color: rgba(255,255,255,0.8) !important;}


a:link { color: rgb(29, 155, 71) !important;
 font-weight: bold !important;} /* lien non-visité */
a:visited { color: rgb(29, 155, 71) !important;
      font-weight: bold !important;} /* lien visité */
a:hover { color: rgb(119, 86, 57) !important;
      font-weight: bold !important;} /* lien survolé */
a:active { color: rgb(119, 86, 57) !important;
      font-weight: bold !important;} /* lien activé */

#message {background-color : rgba(255,255,255,1) !important;}
#postreview{background-color : rgba(255,255,255,0.8) !important;
            -moz-border-radius:10px 10px 10px 10px !important;
            margin-top : 0px !important;}
#postreview .postright{border-top: 1px solid rgba(255,255,255,1) !important;}

.inform{background-color: rgba(255,255,255,0.3) !important;
        -moz-border-radius:10px 10px 10px 10px !important;
         margin-left: -20px !important;
         margin-right: -20px !important;}

.pmlink a:link{color:DarkOrange !important;}
.pmlink a:visited{color:DarkOrange !important;
   text-decoration : blink !important;}

div.icon{background-image: url("http://img717.imageshack.us/img717/7001/champignonvert2.gif") !important;
 background-size : 90% !important;}
div.inew{background-image: url("http://img84.imageshack.us/img84/4927/champignonjaune.gif") !important;
 background-size : 105% !important;
padding-bottom: 0px !important;}
div.txtarea{width: 670px !important;
            height: 280px !important;
            margin-left: 0px !important;
}
div.inform{margin-top: 0px !important;}
#smileys{height: 130px !important;}

.box input[name="submit"]{margin-left: 340px !important;}
div.txtarea textarea{height: 90px !important;}

.iclosed{color: grey !important;}
.iclosed .icon{background-image: url("http://img690.imageshack.us/img690/6867/champignoncass2.gif") !important;
 background-size : 90% !important;}
.iclosed a:link{color: grey !important;}
.iclosed a:visited { color: grey !important;}
.iclosed a:hover { color: grey !important;}
.iclosed a:active { color: grey !important;}


 div[style="position:fixed; z-index:10000; width:100%; height:24px; left:0px; top:0px; background-color:#494949; overflow:hidden"] {background-color: transparent !important;
                               background-image:none !important;
            display : none !important;}
div[style="height: 100%; width: 300px; margin-left: 5px;"]{display: none !important;}

}
