css:
@-moz-document 
domain("lifehacker.com"), domain("gizmodo.com"),  domain("i.gizmodo.com"), domain("valleywag.com"), domain("gawker.com"), domain("jezebel.com"), domain("defamer.com"), domain("jalopnik.com"), domain("io9.com"), domain("deadspin.com"), domain("kotaku.com")
{


/*  REMOVE THE SINGLE LINE BELOW IF YOU WANT LINK COLORS TO BE SITE SPECIFIC  */


a {color: #999 !important}





BODY {background: #f1f1f1 !important; color: #999 !important}

#header { background: #f1f1f1 !important; border: solid 20px #f1f1f1 !important; -moz-border-radius: 20px 20px 20px 20px !important}

#splashPosts  {width: 992px !important; background: #f1f1f1 !important; border: solid 4px #f1f1f1 !important; -moz-border-radius: 6px!important}

#header{


display:none !important;

}

 div#header,
  div#sidebar,
  div#skyscraper,
  .content > .welcome_form,
  .ad_editorial-sponsorship,
  .permalink_ads,
  div[class^="ad_160"],
  div[class^="ad_300"],
  div[class^="ad_728"],
  .dateHeadline,
  div.sponsored,
  div.sponsored ~ div,
  div.postmeta > div.postmeta_ad
  {
    display: none !important;
  }



#footer{

display:none !important;

}


#menubar {width: 1040px !important; background: #f1f1f1 !important; border: solid 4px #f1f1f1 !important; margin-left: -24px !important; -moz-border-radius: 20px 20px 20px 20px !important; margin-top: 10px !important}

#container { width: 1040px !important; background: #eaeae8 !important; -moz-border-radius: 20px 20px 20px 20px !important; padding-top: 6px !important}

#masthead { background: #f1f1f1 !important; -moz-border-radius: 6px !important}

#siteInfo, #skySpacer, #spacer160 {display: none !important}

a {color: #0c0c0d !important;}

a:hover {color: #0c0c0d !important; text-decoration: none !important}

.content {color: #0c0c0d !important;}

.topTag {background: #eaeae8 !important; border: solid 1px #eaeae8 !important; display:none!important}

.post {-moz-border-radius: 8px !important; background: #eaeae8 !important; border: solid 8px #eaeae8 !important; }

.bigExcerptHolder {background: #eaeae8 !important; margin: 0px !important; padding: 4px !important}

.spimage {margin: 0px !important;}

.content.postBigPic {background: #eaeae8 !important;}

.comment:hover {background: #eaeae8 !important}

.commentbox_widget.commentbox {background: #eaeae8 !important; width: 338px !important}
 
.cb_wrapper.smalltype {background: #eaeae8 !important; border: solid 2px #444 !important; -moz-border-radius: 4px !important}

.cb_l.cb_onlyexp.cb_default {border: none !important}

.related_posts {background: #eaeae8 !important}

.rp_item {background: #eaeae8 !important; -moz-border-radius: 8px !important}

.postmeta_permalink {border-bottom: solid 2px #eaeae8 !important}


}
