css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("boards.4chan.org"){
 body{
  font:10pt Arial, sans-serif !important;
  background:#dfdfff !important;
  color:#000 !important;
 } 
 td{
  font-size:10pt !important;
 }
 td.reply{
  border-radius:1em !important;
  -webkit-border-radius:1em !important;
  background-color:transparent !important;
  border:1px solid #000 !important;
  color:#000 !important;
  width:100% !important;
 }
 blockquote{
  font-size:10pt !important;
 }
 .doubledash{
  visibility:hidden !important;
 }
 .inputtext{
  font:10pt Arial, sans-serif !important;
 }
 .logo img{
  display:none !important;
 }
 .logo b span{
  font:17px arial, sans serif !important;
  font-weight:bold !important;
  float:left !important;
  letter-spacing:normal !important;
 }
 .logo font{
  float:left !important;
  text-align:left !important;
 }
 hr{
  display:none !important;
 }
 .postblock{
  border-radius-topleft:1em !important;
  -webkit-border-top-left-radius:1em !important;
  border-radius-bottomleft:1em !important;
  -webkit-border-bottom-left-radius:1em !important;
  border:1px solid #fff !important;
  background-color:transparent !important;
  color:#fff !important;
  font-size:10pt !important;
 }
 a.quotelink{
  color:#a0a !important;
 }
 .logo{
  text-align:right !important;
  color:#aa0 !important;
 }
 .unkfunc{
  border-radius:1em;
  -webkit-border-radius:1em;
  background-color:#000 !important;
  color:#dfdfff !important;
  padding-left:3px !important;
  padding-right:3px !important;
 }
 .filetitle,.replytitle{
  background:inherit;
  color:#990 !important;
  font-weight:800;
 }
 .postername,.commentpostername{
  color:#000 !important;
 }
 .postertrip{
  color:#aaa !important;
 }
 .replyhl{
  border-radius:1em !important;
  -webkit-border-radius:1em !important;
  background-color:transparent !important;
  border:1px solid #fff !important;
  color:#000 !important;
  width:100% !important;
 }
 #ad{
  display:none !important;
 }
 #bottomAd{
  display:none !important;
 }
 #bottomAdOuter{
  display:none !important;
 }
}
@-moz-document domain("dis.4chan.org"){
 body{
  font:10pt Arial, sans-serif !important;
 }
 body.board{
  background:#dfdfff !important;
  color:#000 !important;
 }
 body.read{
  background:#dfdfff !important;
  color:#000 !important;
 }
 .head, body.board .thread{
  border-radius:1em !important;
  -webkit-border-radius:1em !important;
  background:#dfdfff !important;
  border:none !important;
 }
 .postnum{
  font-size:20px !important;
 }
 .postername{
  color:#000 !important;
 }
 .postertrip{
  color:#aaa !important;
 }
 blockquote{
  font:10pt Arial, sans-serif !important;
 }
 .border, .hborder{
  border-radius:1em !important;
  -webkit-border-radius:1em !important;
  border:1px solid #000 !important;
  background-color:transparent !important;
 }
 h2 a:link, h2 a:visited, h2 a:active, h2 a:hover{
  color:#909 !important;
 }
 #ad div.ad-title a, .bottomAdFoot a:hover, .adText, .bottomAdTitle, .postblock{
  display:none !important;
 }
 #bottomAdOuter{
  display:none !important;
 }
 .spoiler{
  background-color:#000 !important;
  text-shadow:none !important;
 }
 .spoiler:hover{
  background-color:transparent !important;
  color:#000 !important;
 }
 .quote{
  border-left:solid 5px #f00 !important;
  padding:0 0 0 5px !important;
  margin:3px 0 !important;
 }
 .quote .quote{
  border-left:solid 4px #0f0 !important;
 }
 .quote .quote .quote{
  border-left:solid 3px #00f !important;
 }
 .quote .quote .quote .quote{
  border-left:solid 2px #f0f !important;
 }
 .quote .quote .quote .quote .quote{
  border-left:solid 1px #0ff !important;
 }
 .quote:hover{
  background-color:transparent !important;
  color:#fff !important;
  text-shadow:#000 0px 0px 5px !important;
 }
}
