css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("anond.hatelabo.jp") {
body{
	color:#151E3A !important;
}


#globalheader{
	margin:0 auto !important;
	padding-left:0.2em !important;
	width:75% !important;
	border-left:30px double #A9BDF4;
}
.hatena-body{
	margin:0 auto !important;
	padding-left:0.2em !important;
	width:75% !important;
	border-left:30px double #A9BDF4;
}


h1{
	margin-top:0 !important;
	padding-top:0.5em !important;
	color:#293D74 !important;
}
#intro{
	display:none;
}
.pager-l,
.pager{
	letter-spacing:1px;
}
.pager-l strong,
.pager strong{
	padding:0 5px;
	background-color:#EEF2FD !important;
	color:#4667C4 !important;
}
.pager-l a:link,
.pager a:link,
.navigation a:link,
h3 a:link,
.refererlist ul li a:link{
	color:#3E5BAD !important;
}
.pager-l a:visited,
.pager a:visited,
.navigation a:visited,
h3 a:visited,
.refererlist ul li a:visited{
	color:#9FAED7 !important;
}
#breadcrumbs,
.navigation{
	margin-top:0 !important;
	padding-top:0.5em !important;
	font-size:10.5pt !important;
	line-height:1.5 !important;
}
.navigation{
	padding:0.5em 0 !important;
}


h2{
	background-color:#FFFFFF !important;
	text-align:right;
}
h2 a{
	text-decoration:none;
}
.date{
	color:#EEF2FD !important;
	font-size:300% !important;
}
h3{
	padding-bottom:0.5em !important;
	color:#3E5BAD !important;
}
h3 > a:first-child{
	text-decoration:none;
}

h3:before{
	margin-left:-0.85em;
	content:"â†’";
	color:#D4DDF9;
	font-size:80pt;
	opacity:0.80;
}
.body{
	position:relative;
	top:-20px;
	border:none !important;
	padding:0 !important;
}
.day p{
	text-indent:0 !important;
	text-align: justify;
}
.section{
	padding:0.2em 1.5em !important;
	background-color:#EEF2FD !important;
	-moz-border-radius:5px;
	color:#151E3A !important;
}
.section a:link{
	color:#151E3A;
}
.section a:visited{
	color:#8B8F9D;
}
a.keyword{
	color:#151E3A !important;
	border:none!important;
}
.section p{
	padding:0.2em !important;
	line-height:1.5 !important;
}
.section blockquote{
	margin:0.5em 0 !important;
	border:1px solid #D4DDF9 !important;
}
#breadcrumbs a:link,
.sectionfooter a:link,
.explain a:link{
	color:#50576C !important;
}
#breadcrumbs a:visited,
.sectionfooter a:visited,
.explain a:visited{
	color:#C4C7CE !important;
}


.caption{
	border:none !important;
}
.refererlist ul{
	padding:0.5em 0 !important;
	background-color:#F3F6FE !important;
	-moz-border-radius:5px;
	width:100% !important;
	overflow:hidden;
}
.refererlist ul li{
	margin:0 1.5em;
}


#sbi{
	border:2px solid #D4DDF9 !important;
}
#sbb,
.post-submit input{
	background-color:#FFFFFF !important;
	color:#293D74 !important;
	border:2px solid #D4DDF9 !important;
	-moz-border-radius:5px;
}


#text-title,
.section textarea{
	background-color:#FFFFFF !important;
	color:#293D74 !important;
	border:1px solid #D4DDF9 !important;
}
.section textarea{
	margin-bottom:1.5em;
}
}
