@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gallery.the-leaky-cauldron.org") {

/*Remove ads*/
#ad {display: none !important;}
#inner-ad {display: none !important;}
#picad {display: none !important;}
#left-well #idologic {display: none !important;}
#left-well #left-ad {display: none !important;}
#aos {display: none !important;}

/*Clean up browse bar and use nicer font*/
#browse {font-family: helvetica !important; font-size: 12px !important;}
#browse {margin-left: -60px !important;}
#brimg {display: none !important;}

/*Space out page a bit better*/
#search {margin-left: 485px !important;}
#content {position: relative; top: -40px !important;}
#recent-uploads {margin-top: -1px !important; margin-left: 32px !important;}

/*Get rid of RSS logos*/
#home-right-well #rss {display: none !important;}

/*Use a better looking font in certain places*/
#home-cats {font-family: helvetica !important;}
#home-topbar {font-family: helvetica !important;}
#home-right-well {font-family: helvetica !important;}
#categories {font-family: helvetica !important;}
#albums {font-family: helvetica !important;}
#pagetitle {font-family: helvetica !important;}
#qb-inner {font-family: helvetica !important;}
#pages {font-family: helvetica !important;}

/*Clean up single picture pages*/
#picrate {display: none !important;}
#picspacer {display: none !important;}
#picinfo {font-family: helvetica !important;}

}
