css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("video.the-leaky-cauldron.org") {

*/Remove ads*/
#ad {display: none !important;}
#ad {display: none !important;}
#inner-ad {display: none !important;}
#picad {display: none !important;}
#aos {display: none !important;}
#left-well #left-ad {display: none !important;}
.home .ad {display: none !important;}
 
/*Cleanup browse bar*/
#browse {font-family: helvetica !important; font-size: 12px !important;}
#browse {margin-left: -60px !important;}
#brimg {display: none !important;}
#search {margin-left: 485px !important;}

/*Make page less cramped*/
#content {position: relative; top: -105px !important;}

.home .recent {font-family: helvetica !important;}
.home .sponsored-sites {display: none !important;}
.home .rss {display: none !important;}


/* Resizes videos, requiring less squinting */
.video .right {float:none !important; margin-left: auto !important; margin-right: auto !important;}
[class="flash"] embed
{
width:700px !important;
height:500px !important;
float:none !important; margin-left: auto !important; margin-right: auto !important;
}

}
