/*
Name:        : Warez-BB Original Theme (FISubIce2)
Author       : xKernel
Website      : http://www.warez-bb.org/
Description  : The original theme for Warez-BB (FISubIce2).
Details      : This script is a temporary fix for the colors. So, it will never be complete (since that will be a waste of time). Enjoy.
*/

@charset "utf-8" !important;
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.warez-bb.org") {

/* The content of the posts (body of text) #ecf0f6; */
body{background:#ecf0f6 !important;color:#000000 !important;font:12px Verdana,Arial,Helvetica,sans-serif !important;margin:6px !important;padding:0 !important;
}

/* General font families for common tags */
font,th,td,p{font:12px Verdana,Arial,Helvetica,sans-serif}

/* General text */
.gensmall{font-size:10px !important}
td.genmed,.genmed{font-size:11px !important}

.javascript_error{
	margin:0 !important;
	padding: 2px 2px 5px 2px !important;
	color:#134a8b !important;
	font-size:12px !important;
	font-family: Arial, Helvetica, sans-serif !important;
	font-weight:bold !important
}

.genlarge {
	font-size:13px !important;
	font-family: Arial, Helvetica, sans-serif !important;
}

.explaintitle{font-size:11px !important;font-weight:bold !important;color:#5c81b1 !important}
.admin_signal{font-size:11px !important;font-weight:bold !important;color:#c10000 !important;font-family: Arial, Helvetica, sans-serif !important}
.header_genmed{font-size:11px !important;font-weight:normal !important;color:#000000 !important;font-family: Arial, Helvetica, sans-serif !important}
.reasons{font-size:11px !important;font-weight:bold !important;color:#c10000 !important}

/* General page style */

a:link:not([href*="profile.php?mode=viewprofile"]),a:active:not([href*="profile.php?mode=viewprofile"]),a:visited:not([href*="profile.php?mode=viewprofile"]),a.postlink:not([href*="profile.php?mode=viewprofile"]){color:#32475f !important;text-decoration:none !important;text-shadow:none !important}
a:hover{color:#839fbc !important; text-shadow:none !important}

a:link[href*="profile.php?mode=viewprofile"]:not([style*="color:"]),a:active[href*="profile.php?mode=viewprofile"]:not([style*="color:"]),a:visited[href*="profile.php?mode=viewprofile"]:not([style*="color:"]),a.postlink[href*="profile.php?mode=viewprofile"]:not([style*="color:"]){color:#32475f !important;text-decoration:none !important;text-shadow:none !important}



/* titles for the topics:could specify viewed link colour too */
.topictitle{font-size:11px !important;font-weight:bold !important}
a.topictitle:visited{color:#496687 !important}
a.topictitle:hover{color:#839fbc !important}

/* Name of poster in viewmsg.php and viewtopic.php and other places */
.name{font-size:11px !important;font-weight: bold !important}

hr{border: 0px solid #ffffff !important;border-top-width:1px !important;height:0px !important}

/* Category gradients*/
td.cat{font-weight:bold !important;letter-spacing:1px !important;color:#516a88 !important;background:#d9e2ec url(http://i56.tinypic.com/15mb42g.jpg) !important;
height:29px !important;text-indent:4px !important;text-shadow:none !important}

/* Main table cell colours and backgrounds */
.row1{background:#f4f6fb !important}
.row2,.helpline{background:#e1e9f2 !important}
.row3{background:#d7e2ef !important}
.row4{background:#c7d6e7 !important}
td.spacerow{background:#cad9ea !important}

/* This is for the table cell above the Topics,Post & Last posts on the index.php */
td.rowpic{background:url(http://i52.tinypic.com/2zxqtue.jpg) #ffffff repeat-y !important}

/* Table Header cells */
th{background:#c6d3e4 url(http://i52.tinypic.com/2ika4iv.jpg) !important;color:#516a88 !important;font-size:11px !important;
font-weight:bold !important;height:27px !important;white-space:nowrap !important;text-align:center !important;padding-left:8px !important;padding-right:8px; font-weight:bold !important;text-shadow:none !important}

/* This is the border line & background colour round the entire page #f7f8fc */
body > table.bodyline{background:#f7f8fc !important;background-repeat:repeat !important;border:1px solid #98aab1 !important}

.footerline{background:#f7f8fc !important;background-repeat:repeat !important;border:1px solid #98aab1 !important;margin-top:5px !important}

/* This is the outline round the main forum tables */
.forumline{background:#ffffff !important;border:1px solid #486386 !important}

/* The largest text used in the index page title and toptic title etc. */
.maintitle,h1{font:bold 20px/120% "Trebuchet MS",Verdana,Arial,Helvetica,sans-serif !important;text-decoration:none !important;color:#839fbc !important}

/* The largest text used in the index page title and toptic title etc. */
h1{font:bold 20px/120% "Trebuchet MS",Verdana,Arial,Helvetica,sans-serif !important;text-decoration:none !important;color:#839fbc !important}

.header_maintitle{font:bold 20px/100% "Trebuchet MS",Verdana,Arial,Helvetica,sans-serif !important;text-decoration:none !important;color:#839fbc !important;margin-top:5px !important}

.subtitle,h2{font:bold 18px/180% "Trebuchet MS",Verdana,Arial,Helvetica,sans-serif !important;
text-decoration:none !important}

/* Used for the navigation text,(Page 1,2,3 etc) and the navigation bar when in a forum */
.nav{font-size:11px !important;font-weight:bold !important}
.postbody{font-size:12px !important;line-height:125% !important}
.nav2{font-size:9px !important;}

/* Location,number of posts,post date etc */
.postdetails{font-size:10px !important;color:#00396a !important}

/* Quote blocks 	background-color: #FAFCFE; */
.quote{color: #465584 !important;
	border-left: 4px solid #8394B2 !important;
	border-right: 1px dotted #000 !important;
	border-top: 1px dotted #000 !important;
	border-bottom: 1px dotted #000 !important;
	padding: 4px !important;
	background: #FAFCFE url(http://img8.warez-bb.org/images/bg_quote_content.gif) bottom right no-repeat !important;
	margin: 0px auto 8px auto !important;}

/* Moderator blocks */
.moderator{color: #465584 !important;
	background-color: #FAFCFE !important;
	border-left: 4px solid #b28383 !important;
	border-right: 1px dotted #000 !important;
	border-top: 1px dotted #000 !important;
	border-bottom: 1px dotted #000 !important;
	padding: 4px !important;
	padding-left: 25px !important;
	background: #FAFCFE url(http://img8.warez-bb.org/images/mod.gif) center left no-repeat !important;
	margin: 0px auto 8px auto !important;}

/* Code blocks */
.code{font-family: Courier, Courier New, Verdana, Arial !important;
	color: #465584 !important;
	background-color: #FAFCFE !important;
	border: 1px dotted #000 !important;
	padding: 2px !important;
	width:98% !important;
	margin: 0px auto 0px auto !important;
	/*overflow: auto !important;
	height: 200px !important;*/}

/* This is for the error messages that pop up */
.errorline{background:#add8e6 !important;border:1px solid #000000 !important}
.pm{color:#ffffff;background:#d3262a !important;border:1px solid #000000 !important;text-decoration:none !important}

/* Form elements */
form{display:inline !important}

input{font:11px Verdana,Arial,Helvetica,sans-serif !important}

select{background:#ffffff !important;font:11px Verdana,Arial,Helvetica,sans-serif !important}

input.post,textarea.post{background:#ffffff !important;border:1px solid #000000 !important;
font:11px Verdana,Arial,Helvetica,sans-serif !important;padding-bottom:2px !important;padding-left:2px !important}


a.but,a.but:hover,a.but:visited{color:#000000 !important;text-decoration:none !important}

/* This is the line in the posting page which shows the rollover
help line. Colour value in row2 */
.helpline{border:0 solid !important;font-size:10px !important}

/* This is the gradient background at the top of the page */
table.topbkg{background: #dbe3ee url(http://img8.warez-bb.org/images/cellpic_bkg.jpg) repeat-x  !important}
.topnav{font-size:10px !important;background: #b0c4d8 url(http://i56.tinypic.com/15mb42g.jpg) repeat-x !important;
height:21px !important;white-space:nowrap;border: 0px solid #91a0ae !important;border-width: 1px 0px 1px 0px !important}

/* Admin & Moderator Colours MODification */
.admin,.mod{font-size:11px !important;font-weight:bold !important}
.admin,a.admin,a.admin:visited{color:#ff0000 !important}
.mod,a.mod,a.mod:visited{color:#006600 !important}
a.admin:hover,a.mod:hover{color:#839fbc !important}

/* Specify the space around images */
.imgtopic,.imgicon{margin-left:3px !important}
.imgspace{margin-left:1px !important;margin-right:2px !important}
.imgfolder{margin:1px 4px 1px 4px !important}

/* Gets rid of the need for border="0" on hyperlinked images */
img{border:0 solid !important}

/* Background images for tables */
.tbl{border-collapse:collapse !important;height:4px !important;width:100% !important}
.tbll{background: url(http://img8.warez-bb.org/images/tb4_l.gif) no-repeat;width:8px !important}
.tblbot{background: url(http://img8.warez-bb.org/images/tb4_m.gif) repeat-x;width:100% !important}
.tblr{background: url(http://img8.warez-bb.org/images/tb4_r.gif) no-repeat;width:8px !important}

/* Mod block */
.moderation{border:1px solid red !important;color:black !important;font:11px "Courier New",sans-serif !important; line-height:125% !important}

span.alive{
	color:#85c331 !important;
	font-weight:bold !important
}

span.dead{
	color:#f42d34 !important;
	text-decoration:line-through !important;
	font-weight:bold !important
}

span.liloff{
	color:gold !important;
	font-weight:bold !important;
}

* html span.corners-top, * html span.corners-bottom { background-image: url("http://img8.warez-bb.org/images/corners_left.gif") !important; }
* html span.corners-top span, * html span.corners-bottom span { background-image: url("http://img8.warez-bb.org/images/corners_right.gif") !important; }

span.corners-top, span.corners-bottom, span.corners-top span, span.corners-bottom span {
	font-size: 1px !important;
	line-height: 1px !important;
	display: block !important;
	height: 5px !important;
	background-repeat: no-repeat !important;
}

span.corners-top {
	background-image: url("http://img8.warez-bb.org/images/corners_left.png") !important;
	background-position: 0 0 !important;
	/*margin: 0 -10px 5px -10px;*/
	margin: 0 -5px !important;
}

span.corners-top span {
	background-image: url("http://img8.warez-bb.org/images/corners_right.png") !important;
	background-position: 100% 0 !important;
}

span.corners-bottom {
	background-image: url("http://img8.warez-bb.org/images/corners_left.png") !important;
	background-position: 0 100% !important;
	/*margin: 5px -10px 0 -10px;*/
	margin: 0 -5px !important;
	clear: both !important;
}

span.corners-bottom span {
	background-image: url("http://img8.warez-bb.org/images/corners_right.png") !important;
	background-position: 100% 100% !important;
}

p {
	margin: 0;
	padding: 0;
}

#loading {
		font-size: 12px !important;
		right: 1px !important;
		background: #0070ea !important;
		border:1px solid #0052ac !important;
		color: white !important;
		font-family: arial,sans-serif !important;
		position: absolute !important;
		top: 1px !important;
		z-index: 3 !important;
}

#header-error {
		position:absolute !important;
		font-family:verdana,arial !important;
		background:#b21b1b !important;
		border:1px solid #610e0e !important;
		color:white !important;
		font-size:10px !important;
		padding:2px !important;
		top:2px !important;
		left:2px !important;
		opacity: 0.8 !important;
		z-index: 3 !important;
}	
		
#theToolTip{
	position: absolute !important;
	left: -300px !important;
	width: 250px !important;
	border: 2px solid #98AAB1 !important;
	padding: 6px 0px 6px 10px !important;
	background-color: #D7E2EF !important;
	visibility: hidden !important;
	z-index: 100 !important;
}

#ToolTipPointer{
	position:absolute !important;
	left: -300px !important;
	z-index: 101 !important;
	visibility: hidden !important;
}

#theToolTip p{
	font-size: 1.1em !important;
	color: #333333 !important;
	margin-right:10px !important;
	margin-top:0 !important;
}

#ToolTipTextWrap {
	font-weight:bold !important;
	font-size: 1.2em !important;
	color: #592C16 !important;
	margin-right:10px !important;
}


