@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.beppegrillo.it") {
 #prodotti, #chiusura_prodotti
 {
  display:none;
 }

 #footer
 {
  display:none;
 }

 #container
 {
  width:100%;
 }

 #colonna_blog
 {
  width:auto !important;
 }

 #post_principale
 {
  width:auto !important;
  border-bottom: solid 3px black;
  border-right: none !important;
 }

 #post_secondario
 {
  width:100% !important;
  margin: 0 auto;
  display: block !important;
 }

 #battaglie
 {
  display:none !important;
 }

 #colonna_grillorama
 {
  display:none!important;
 }

 #topbanner
 {
  display:none !important;
 }

 #menubar
 {
  width: 1900px;
  display: inline;
 }

 #searchbox
 {
  position:relative;
  left:0;
  top:5px;
 }

 #datacorrente
 {
  display:none;
 }
}
