	line-height:1.2 !important;
	max-height:3.5em !important;
	overflow:hidden;
}
.pager > ul > li:first-child,
.pager ul li.prev + li {
	width:100% !important;
	padding:0px !important;
}
.pager ul li > div.ui-selectbox-container {
	position:relative !important;
	display:block !important;
	width:100% !important;
	margin:0px !important;
	padding:0px !important;
	z-index:1 !important;
}
.pager ul li > div.ui-selectbox-container ul {
	display:block !important;
	width:100% !important;
	margin:0px !important;
	padding:0px !important;
	border:0px !important;
	border-top:1px solid #d6dee5 !important;
	border-bottom:1px solid #d6dee5 !important;
	outline:0px !important;
	text-align:center !important;
	position:relative !important;
	font-size:90% !important;
}
.pager ul li > div.ui-selectbox-container ul li {
	display:inline !important;
	font-size:110% !important;
	font-weight:normal !important;
}
.pager ul li > div.ui-selectbox-container ul li.current {
	font-weight:bold !important;
	font-size:125% !important;
	color:#555 !important;
}
.pager li.next,
.pager li.prev,
.column-main .pager ul li div.ui-selectbox-container .ui-modal-trigger {
	display:none !important;
}
.column-main nav.order {
	margin:0px !important;
	padding:0px 10px !important;
	position:relative !important;
}
.column-main nav.order ul {
	margin:0px !important;
	padding:0px !important;
	position:absolute;
}
.column-main nav.order ul li {
	font-weight:bold !important;
	font-size:125% !important;
}
.column-main nav.order .badge-new {
	display:none !important;
}
nav.pager {
	padding-top:1.7em;
}
nav.context ul.sub {
	position:relative;
	z-index:999;
}
