}
section.novel_listview article div.rank-content ul.tags li {
	padding:5px 0px 0px 0px !important;
	display:inline-block !important;
}
section.novel_listview article div.rank-content ul.tags li a {
	margin:0px !important;
	padding:0px !important;
	font-size:90% !important;
}
section.novel_listview article div.rank-content ul.tags li + li:before {
	content:none !important;
}
section.novel_listview article div.rank-content ul.tags li:after {
	color:#999999 !important;
	content:"/" !important;
}
section.novel_listview article div.rank-content ul.tags li:nth-last-child(2) + li:after {
	content:none !important;
}

