.ui-layout-west > div.area_new {
	display:none !important;
}
.contents-right .item:nth-of-type(2) {
	display:none !important;
}
.contents-right .item:nth-of-type(3) {
	margin-top:0 !important;
}
/* end document-mypage */
}
@-moz-document 
url("http://www.pixiv.net/novel/"),
url("http://www.pixiv.net/cate_r18.php"),
url("http://www.pixiv.net/novel/cate_r18.php") {
/* start document-novel_r18 */
.ui-layout-west > div.area_new {
	display:none !important;
}
/* end document-novel_r18 */
}

@-moz-document
url-prefix("http://www.pixiv.net/member_illust.php"),
url-prefix("http://www.pixiv.net/member_tag_all.php"),
url-prefix("http://www.pixiv.net/novel/show.php") {
/* start document-illust */
.ui-layout-west div[style*="border: 1px solid rgb(222, 228, 234);"] {
	display:none !important;
}
.two_column_body .works_area + div[style*="height"],
.two_column_body .action + div[style*="height"] {
	display:none !important;
}
.two_column_body iframe[id*="adingo"] {
	display:none !important;
}
.two_column_body div.works_display {
	clear:both !important;
	padding-top:15px !important;
}
/* end document-illust */
}

