@-moz-document 
url("http://www.pixiv.net/"),
url-prefix("http://www.pixiv.net/index.php") {
/* start document-login */
/* ========== login ========== */
#pageHeader {
	padding:5px 0px !important;
}
.login-nav {
	top:5px !important;
	right:0px !important;
}
#login-block {
	top:1px !important;
	right:0px !important;
}
#login-block .offscreen {
	left:0px !important;
	_display:block !important;
}
/* end document-login */
}




@-moz-document domain("pixiv.net") {
/* start spLogo */
#global-header .global-menu,
#global-header .ui-search {
	_left:200px !important;
}
h1 #logoMap {
	_height:65px !important;
}
#global-header .global-menu li.notification {
	_left:410px !important;
}


h1 #logoMap img {
	opacity:0.0 !important;
}
h1 #logoMap {
	width:130px !important;
	height:65px !important;
	background:url(http://source.pixiv.net/source/images/sprite-2nd.png) no-repeat 0 8px !important;
}
/* end spLogo */
}
