css:
/**************************************
 * del.icio.us  - love my blue
 *
 * Author      : Intra
 * Created on  : October 1st, 2007
 * Description : colors on del.icio.us changed to blue, all links, all text, the sidebars, and even the bundle tag edit  
 *               has been completely changed to blue. I like it, not sure if you will but i love my blue. 
 *************************************/
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("del.icio.us") {
body {
	background-color: #164D72 !important;	
        color: #CBE2F1 !important;	
}

/* Most of the links */

a:link {	color: #CBE2F1 !important;}
a:visited {color: #90C0E0 !important;}
a:hover {color: #63AEE0 !important;}
a:active { color: #CBE2F1 !important;}
.alwaysblue a:link, .alwaysblue a:visited { color: #CBE2F1 !important; }

/* Most of the text */

.grey, .bundles .grey span, #content, .abouthead, .bg, #items-per-page, .meta, .pager, .date, #header-global-links, #page-desc, .aboutsmall, #taglist, #help-page h1, #help-page h2, #leftcol,li, h3 { color: #CBE2F1 !important; }
.ltgrey { color: #90C0E0 !important; }
.important {color: #90C0E0 !important;}
.r {color: #63AEE0 !important;}
.private span {color: #63AEE0 !important;}

/* TAG BUNDLE EDIT */

#bundle-edit .usedmember{background-color: #415174 !important; border:1px solid #2A3D66 !important;}
#bundle-edit .used{border: 1px solid #2A3D66 !important;}
#bundle-edit .member { background-color: #415174 !important;}

/*Most of the headers*/

#settings-intro-2{ border-left:1px solid #CBE2F1 !important;}
#header-mini { background-color: #5C6A88 !important;}

/* The Image */
#header-l {background-image: url("data:image/jpeg;base64,") !important;}

#header, #fp-hotlist .hotlist .tags .label, .bundles .grey span { background-color: #164D72 !important;}
#header a:link, #header a:visited { color: #CBE2F1 !important; }
#header #socialbookmarking { color: #415174 !important; }
#header .auth-h1, .bundles .label {color: #CBE2F1 !important;}
#header-warning {background-color: #1C2C4E !important; }
#header-auth-links, #header-user-links{color: #CBE2F1 !important;}
#banner-hr { background-color: #7281A2 !important; border: 1px solid #000 !important; }
#fp-tagblurb {background-color: #5891B8 !important; color: #164D72 !important; }
#fp-tagblurb-inner a:link, #fp-tagblurb-inner a:visited { color: #CBE2F1 !important; }
.desc h4 a:link, .desc h4 a:visited{ color: #CBE2F1 !important;}
.desc h4 a:hover {color:#63AEE0 !important; }

/* The TextField Right Beside del.icio.us/USER/ userpage */
.crumb {background-color: #5C6A88 !important; color: #CBE2F1 !important; border-color: #164D72 !important; }

/* The Actual INPUT(search area) and button  */
input {background-color: #5C6A88 !important; border-color: #164D72 !important; color: #CBE2F1 !important;}

/* The DROPDOWN LIST */
#searchtype, select {background-color: #5C6A88 !important; color: #CBE2F1 !important;}

/* HOTLIST  On Main Page */

#fp-hotlist-title { background-color: #5C7F97 !important; color: #fff !important; }
#fp-hotlist-bylinks { color: #8EACC0 !important; }
#fp-hotlist .hotnow { color:#5891B8 !important;}
#fp-hotlist .hotlist {color:#fff !important;}
#fp-hotlist .hotlist li h4 img {border: 1px solid #1C2C4E !important; }
#fp-hotlist .hotlist li .savethis a { color: #CBE2F1 !important;}
#fp-hotlist .hotlist li .savethis a:visited { color: #164D72 !important; }
#fp-hotlist .hotlist li .meta strong .num .numbox { background-color: #1C2C4E !important;}
#fp-hotlist .hotlist li .meta strong .num .numbox a { color:#CBE2F1 !important; }
#fp-hotlist .hotlist li .meta strong .num .numbox a:visited { color: #CBE2F1 !important; }
#fp-hotlist .hotlist .tags ul li {border-left:2px solid #CBE2F1 !important;}
/* HOTLIST TAGS */

#fp-hotlist .hotlist .tags a, #fp-hotlist .hotlist .tags a:visited {color:#CBE2F1 !important;}
#fp-hotlist .hotlist .tags, #fp-hotlist .hotlist .tags ul { background:#7281A2 !important;}
#fp-hotlist .hotlist .tags li a, a:visited, #fp-hotlist .hotlist .tags li a:hover  {color:#CBE2F1 !important;}

/* HOT NOW BAR, TAGS TO WATCHLIST AND MOST OF THE OTHER SIDEBARS*/

#curated h3, #sidebar { background-color: #5C7F97 !important; color: #CBE2F1 !important;}
#curated ol li ol li {border-top: 1px solid #1C2C4E !important;}

.sidebar-inner {background-color: #164D72 !important; border-color: #5C7F97 !important}
#sidebar, #sidebar-related {border-color: #5C7F97 !important;}
#related-sidebar .list {border-color: #5C7F97 !important;}

/* On the userPage Top BAR where the search is */

#infobar {background-color: #5C7F97 !important; border-color: #5C7F97 !important;}
#infobar a:link, #infobar a:visited { color: #CBE2F1 !important; }

/* POP Is the Saved By ### Other People BG is for the tag bundle background */

.pop, .bg{background-color: #5C6A88 !important;}

/* HISTORY FOR URL SECTION */
.historylist span {background-color:#5C7F97 !important; }
.datehead,.historylist li p {color: #5C7F97 !important;}
.separator h3 {color: #CBE2F1 !important;}


.posts li, .postui .popup {background-color: #164D72 !important; }
.post .meta .postui .bundles .label { color: #CBE2F1 !important;}
.post .meta a { color: #63AEE0 !important;}
.post p.notes, .post .meta .extended {color: #7281A2 !important;}

#inline-suggestions { border: 2px solid #7281A2 !important;background-color: #164D72 !important;}
#inline-suggestions .tag {color:#CBE2F1 !important;}
#inline-suggestions .selected, #suggestions .tag:hover  { background-color: #5C6A88 !important; color: #fff !important;}

.posts .gripe { border: 2px solid #7281A2 !important; }
.posts .private h4.desc { color: #2A3D66 !important; }
.post button { border: 2px outset #7281A2 !important; background-color: #7281A2 !important;}

/*FOOTER*/

#footer-hr, .sidebar-break { background-color: #5C7F97 !important; border: 1px solid #CBE2F1 !important; }

/* REMOVES THE SPONSORED LINKS */
#sponsored {display: none !important;}
}
