css:
html {
	background:#D4EAF5 url(data:image/gif;base64,R0lGODlhAQAeAIAAAP///wAAACH5BAAAAAAALAAAAAABAB4AAAIFhI+pawUAOw==) repeat-x 0 0 !important;
	padding-top: 0px;
}

body {
	background:#D4EAF5 !important;
	margin:0px auto !important;
	font-family: Helvetica, Arial, Verdana, sans-serif !important; 
	font-size: 11px !important;
	font-weight:normal !important;
}


#header {
	background: none !important;
	border-bottom:0px !important;
	height:90px !important;
    margin-left: 10px !important;
    margin-right: 10px !important;
}

#sr-header-area {
	background:#fff !important; 
	text-transform: capitalize !important;
	border-bottom:0px !important; 
	padding: 8px 10px 12px 10px !important;
	height:10px !important;
	overflow:hidden;
	font-size:11px !important;
}

#sr-footer-area {
	background:#fff !important; 
	border-bottom:0px !important; 
	padding: 8px 0 12px 0 !important;
	font-size:11px !important;
}

.dropdown.srdrop .selected {
	font-weight:bold !important;
}

.drop-choices {
	border:0px !important;
	border-left:1px solid #eee !important;
	border-bottom:1px solid #eee !important;
	padding:10px !important;
	-moz-border-radius-bottomright: 5px;
	-moz-border-radius-bottomleft: 5px;
	-moz-box-shadow: 10px 10px 10px -10px #888;
}

#sr-more-link {
	background: #fff !important;
	top: 8px !important;
	color: orangered !important
}

.tabmenu {
	position:absolute;
	left: 140px;
	top:65px;
}

.tabmenu li {
	margin: 0 !important;
}

.tabmenu li a {
	border: 0px !important;
	padding: 3px 7px !important;
	background:none !important;
	color:#494949 !important;
	font-size: 13px !important;
}

.tabmenu li.selected a {
	background: #fff !important;
	color:orangered !important;
	-moz-border-radius: 7px !important;
	-moz-box-shadow: 1px 1px #c1dbf5 !important;
}

#header-img-a {
	position:absolute;
	top:40px;
}

#header-img {
	max-height:50px !important;
}

.redditname, .pagename {
	color:#494949 !important;
	position:absolute !important;
	top:40px;right:0px;
	margin:0 !important;
	font-size: 11px !important;
	font-variant:normal !important;
	background: none !important;
}

.redditname a {
	color:#494949 !important;
	font-variant:normal !important;
	font-size: 11px !important;
	font-variant:normal !important;
	margin: 0 !important;
} 


#header-bottom-right {
	background: none !important;
	bottom: 0px !important;
	font-weight:bold !important;
	color:#000 !important;
}

#header-bottom-right a {
	color:#494949 !important;
}

.separator {
	font-weight:normal !important;
}

.sr-bar .separator {
	color:#fff !important;
}

.user {
	color:#43a1cf !important;
}


.content {
	background: #fff !important;
	margin:0 !important;
    margin-left: 20px !important;
    margin-right: 330px !important;
	padding:5px !important;
	-moz-border-radius: 10px;
	-moz-box-shadow: 1px 1px #c1dbf5 !important;
}

.content .content {
	background: none !important;
	width: auto !important;
	-moz-box-shadow: none !important;
	position:relative;
}

ul.tabmenu.formtab {
	position: absolute;
	bottom:15px;
	right:125px;
}

.side {
	margin:0 !important;
	background: none !important;
	width: 310px !important;
	margin-right: 10px !important;
}

.side .spacer {
	background:#fff !important;
	margin:0 0 5px 0 !important;
    margin-right: 5px !important;
	-moz-border-radius: 10px;
	-moz-box-shadow: 1px 1px #c1dbf5 !important;
}

.side .spacer:first-of-type {
	-moz-border-radius: 5px;
}

.side .spacer #search input {
	width: 250px !important;
	margin:6px !important;
	padding:5px !important;
	font-family: Helvetica, Arial, Verdana, sans-serif !important;
	font-weight:normal !important;
	font-size: 15px !important;
	border:1px solid #494949 !important;
}

.sidecontentbox {
	margin:5px !important;
	padding: 5px 0 !important;
}

.sidecontentbox .content {
	width: auto !important;
	clear:both;margin:0 !important;
	border: 0 !important;
	-moz-box-shadow: none !important;
}

.fancy-toggle-button a {
	color:#fff !important;
	font-size:15px !important;
	font-weight:bold !important;
	border:0px !important;
	padding:4px 8px !important;
	-moz-border-radius: 5px !important;
}

.fancy-toggle-button .remove {
	background:#d54a61 !important;
}

.fancy-toggle-button .add {
	background:#54d54a !important;
}

.titlebox {
	padding: 10px !important;
}

.titlebox h1 {
	padding: 0 0 5px 17px !important;
	margin: 0 0 10px -17px !important;
	background: url(data:image/gif;base64,R0lGODlhBwANAIABAHJycv///yH5BAEAAAEALAAAAAAHAA0AAAITjA9nkMj+Apty2lvt0jt2VYFSAQA7) no-repeat 0 6px;
	overflow:visible;
}

.titlebox .number, .titlebox .word {
	color: #90d3f5 !important;
	font-weight:bold !important;
	font-size:11px !important;
}

.titlebox .bottom {
	border-top: 1px solid #eee !important;
	padding: 10px 0 0 0 !important;
}

.submit, .create, .gold {
	padding: 10px !important;
}

.morelink {
	display:block !important;
	background:#90d3f5 !important;
	border:0 !important;
	-moz-border-radius: 5px !important;
	padding:0 !important;
	-moz-box-shadow: 1px 1px #7fc3e6 !important;
}

.morelink:hover 
{
		background: orangered !important;
		 -moz-box-shadow: 1px 1px red !important;
}

.morelink a 
{
		color: #fff !important;
}
.morelink .nub 
{
	display: none !important;
}

.sidebox.create .spacer, .sidebox.submit .spacer, .sidebox.gold .spacer 
{
	padding:10px 0 0 0 !important;
	margin:0 !important;
	min-height: 0 !important;
	-moz-box-shadow: none !important;
}

.sponsorshipbox.thing 
{
	padding:0 !important;
	border:0 !important;
}

.linkinfo 
{
	background: none !important; 
	border: 0 !important;
	padding: 10px !important;
}

.linkinfo .upvotes 
{
	color: orangered !important;
}

.linkinfo .downvotes 
{
	color: #90d3f5 !important;
}

.thing 
{
	padding: 20px 0 15px 0 !important; 
	margin:0 !important; 
	border-bottom: 1px solid #eee;
	background-image:url(data:image/gif;base64,%3D%3D) repeat-x 0 0 !important;
	background: #f9f9ff;
}
.odd
{
	background: #ffffff !important;
}

.organic-listing
{
	border:1px solid #E4F1F7 !important;
	-moz-border-radius:5px;
}

.link.promotedlink.promoted 
{
	background: #F5F9FB !important; 
}

.link .rank 
{
	display:none !important;
}

.link .score 
{
	font-weight: normal!important;
	font-size: 11px !important;
	color: #43a1cf !important;
}

.midcol 
{
	margin:0 5px 0 5apx !important;
}

.thumbnail 
{
	padding:0 !important;
	margin: 0 10px 0 10px !important;
}

.thing .title.loggedin 
{
	color: #494949!important;
}

.thing .title.loggedin:visited 
{
	color: #888 !important;
}
.domain, .domain a,.tagline 
{
		color: #aaa !important;
}

.entry .buttons li a, .tagline a 
{
	color: #43a1cf!important;
}

.arrow 
{
	height: 15px !important;
}

.arrow.up 
{
	background: url(data:image/gif;base64,) no-repeat 0 0 !important;
} 

.arrow.down 
{
	background: url(data:image/gif;base64,) no-repeat 0 -15px !important;
}

.arrow.upmod 
{
	background: url(data:image/gif;base64,) no-repeat 0 -30px !important;
}

.arrow.downmod 
{
	background: url(data:image/gif;base64,) no-repeat 0 -45px !important;
}

.nextprev a 
{
	color: #43a1cf !important;
}

.menuarea .drop-choices 
{
		border-left:1px solid #eee !important;
		border-bottom:1px solid #eee !important;
}

.comment 
{
	border-bottom: 0px solid gray !important;
	padding: 0 !important;
}

.comment .child 
{
	border-left: 1px dashed #ddd !important;
}

.footer-parent 
{
	text-align: left !important;
	padding:20px 0 0 0 !important;
}

.footer 
{
	margin: 30px 30px  !important; 
	padding: 0 !important;
	border: 0 !important;
}

.footer .col 
{
	border-left: 1px solid #c1dbf5 !important;
	padding:10px 20px !important;
	margin: 0 0 20px 0 !important;
}

.footer .col a 
{
	color: #43a1cf !important;
}

.footer .col:first-of-type 
{
	border-left: 0px !important;
	padding:10px 20px 10px 0 !important;
}

.flat-vert.title 
{
	color: #fff !important;
	text-shadow: 1px 1px #c1dbf5;
}

.bottommenu, .bottommenu a 
{
	color: #fff !important;
}

.thing a.title.loggedin:link {
	font-weight: bold !important;
}

.thing a.title.loggedin:visited {
        color: #ccc !important;
	font-weight: normal !important;
}

.thing a.title.loggedin:hover {
        color: #c00 !important;
}

.thumbnail img
{
  width: 100px !important;
}

a.thumbnail
{
  width: 100px !important;
}

.siteTable
{
  padding-left: 0px !important;
  margin: 0px !important;
}
