css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("lifehacker.com")
{

/*************/
/* Main Page */
/*************/

/* Background */
	body {
		background: #000 !important;
		color: #808080 !important;
	}

/* Upper Links */
	h1 a {
		color: #808080 !important;
	}

/* Lifehacker Tab */
	div.selected {
		background-color: #222 !important;
		border-color: #555 !important;
	}
	#topMenu {
		background: #000 !important;
		border-bottom: 2px solid #555 !important;
		width: 1000px !important;
	}

/* Linux Tab */
	#linuxtab {
		background-image: url(data:image/png;base64,) !important;
	}
	#linuxtab:hover {
		background-image: url(data:image/png;base64,) !important;
	}

/* Tag hr */
	.tp_head_title {
		border-bottom: 2px solid #555 !important;
	}

/* Top Stories - Increase from 5 to 7 */
	#splashPosts {
		margin-left: 10px !important;
	}
	.topStory {
		width: 129px !important;
	}
	#splashPosts .topStory img {
		border: 1px solid #555 !important;
	}
	#splashPosts .hide {
		display: block !important;
	}

/* Lifehacker Menu & Share Bar */
	div#menubar {
		background: #222 !important;
		border: 1px solid #555 !important;
		color: #808080 !important;
	}
	div#popTags, div#popTags a {
		color: #808080 !important;
	}

/* Main Content */
	div#container, div#sidebar {
		background: #222 !important;
		color: #808080 !important;
	}

	div#container h1 a {
		color: #786E29 !important;
	}

	.content {
		color: #808080 !important;
	}

/* Date Separators */
	.dateHeadline {
		background-color: #444 !important;
		border-bottom: 3px solid #333 !important;
		color: #CCC !important;
	}

/* Posts with Large Pics & Vids */
	.postBigVid, .postBigPic {
		background-color: #000 !important;
		border: 1px solid #555 !important;
	}

/* Posts with Pics */
	.postpic img {
		border-color: #555 !important;
	}

/* Post Tags */
	.topTag {
		background-color: #111 !important;
		border: 1px solid #555 !important;
		color: #786E29 !important;
	}

/* Form Boxes and Drop Down Menus */
	input, input#s, textarea, select, div[class="cb_inputarea"] {
		background: #000 !important;
		border-color: #000 !important;
		-moz-appearance: none !important;
		color: #808080 !important;
	}
	.cb_wrapper {
		border: 1px solid #555 !important;
	}
/* Search form */
	div#top_search_control form#search_form {
		background: #000 !important;
	}
	div#top_search_control input#term {
		background: #222 !important;
		border: 1px solid #555 !important;
	}
	div#top_search_control .right {
		display: none !important;
	}

/* Sidebar h3 Titles */
	div#masthead #team {
		background: url(data:image/gif;base64,) !important;
		padding:0px 0px 11px 137px !important;
		height:0px !important;
		width:0px !important;
	}
	div#masthead #lhshop {
		background: url(data:image/gif;base64,) !important;
		padding:0px 0px 11px 137px !important;
		height:0px !important;
		width:0px !important;
	}
	div#weekly-subscribe h3 {
		color: #808080 !important;
	}

/*****************/
/* Login Overlay */
/*****************/
	.modal_overlay {
		background-color: #000 !important;
	}
	.inner {
		background-color: #000 !important;
	}
	.inner h1 {
		background-color: #222 !important;
	}

/*****************/
/* Article Pages */
/*****************/

/* Right Panel - Permalink hr */
	.pm_line {
		border-top: 2px dotted #555 !important;
	}
	.pm_meta_border {
		border-left: 2px dotted #555 !important;
	}
	.postmeta_permalink {
		border-bottom: 2px dotted #555 !important;
	}

/* Right Panel Images - Clock, Flame, Comment */
	img[src="http://cache-01.gawkerassets.com/assets/base.v9/img/icons/clock-empty.png"] {
		background: url(data:image/png;base64,) !important;
		padding: 0px 0px 15px 15px !important;
		height: 0 !important;
		width: 0 !important;
	}
	img[src="http://cache-01.gawkerassets.com/assets/base.v9/img/icons/flame-orange.png"] {
		background: url(data:image/png;base64,) !important;
		padding: 0px 0px 15px 10px !important;
		height: 0 !important;
		width: 0 !important;
	}
	img[src="http://cache-01.gawkerassets.com/assets/base.v9/img/icons/flame-gray.png"] {
		background: url(data:image/png;base64,) !important;
		padding: 0px 0px 15px 10px !important;
		height: 0 !important;
		width: 0 !important;
	}
	img[src="http://cache-01.gawkerassets.com/assets/base.v9/img/icons/comment-empty.png"] {
		background: url(data:image/png;base64,) !important;
		padding: 0px 0px 15px 15px !important;
		height: 0 !important;
		width: 0 !important;
	}
	img[src="http://cache-01.gawkerassets.com/assets/base.v9/img/icons/comment-filled.png"] {
		background: url(data:image/png;base64,) !important;
		padding: 0px 0px 15px 15px !important;
		height: 0 !important;
		width: 0 !important;
	}

/* Right Panel - Related Tags */
	.related_posts {
		background-color: #444 !important;
	}
	.rp_head, .rp_post, .rp_foot, rp_item, rp_comment, div[class="rp_item rp_comment"] {
		background-color: #222 !important;
	}
	.rp_post a {
		color: #808080 !important;
	}

/* Follow Us Email Box */
	.welcome_form {
		background-color: #000 !important;
		border: 1px solid #2A2A2A !important;
	}
	.cn_cancel {
		background: url(data:image/png;base64,) !important;
	}
	div[class="welcome_form"] .fbblue {
		background-color: #555 !important;
		border: 1px solid #000 !important;
	}

/* Comment Box */
	.commentbox {
		background: #333 !important;
		border: 1px solid #555 !important;
	}
	.cb_l {
		background: #222 !important;
		border-top: 2px solid #555 !important;
	}

/* Comments */
	.threadWrapper {
		border-bottom: 1px dotted #777 !important;
	}
	.comment:hover {
		background-color: #2A2A2A !important;
	}

/**************/
/* Ad Removal */
/**************/

	div#ad-300x250, div[class="ad_300x250"], div#skyscraper, div#skySpacer {
		display: none !important;
	}
	element.style {
		border-color: #555 !important;
	}

}
