css:
@-moz-document url("chrome://browser/content/browser.xul") {

  

    


/* FAVICON NEW */
.tabbrowser-tab[image$='static.endoftheinter.net/images/dealwithit.ico'] .tab-icon-image,
#page-proxy-favicon[src$="static.endoftheinter.net/images/dealwithit.ico"],

.bookmark-item[statustext*="endoftheinter."] .menu-iconic-icon,
.bookmark-item[statustext*="endoftheinter."] .toolbarbutton-icon,
.bookmark-item[image*="endoftheinter."]:not([container]) .menu-iconic-icon,
.bookmark-item[image*="endoftheinter."]:not([container]) .toolbarbutton-icon {
  width: 0px !important;
  padding-left: 16px !important;

background-image: url(data:image/png;base64,);

}


}


/* Hide "End of the Internet" header */
@-moz-document url(http://endoftheinter.net/main.php),
url(http://endoftheinter.net/main.php),
domain(archives.endoftheinter.net),
domain("archives.clouds.endoftheinter.net"),
url(https://endoftheinter.net/main.php),
url(https://endoftheinter.net/main.php),
url-prefix(http://boards.clouds.endoftheinter.net/topics/)
{
h1{display:none;}
}


@-moz-document url("http://boards.endoftheinter.net/showtopics.php?board=42"),
domain("boards.clouds.endoftheinter.net"),
url-prefix(http://boards.endoftheinter.net/showmessages.php?),
url(https://boards.endoftheinter.net/showtopics.php?board=42),
url-prefix(https://boards.endoftheinter.net/showmessages.php?),
url-prefix(http://boards.clouds.endoftheinter.net)
{
h1{display:none;}
}
@-moz-document url-prefix(http://boards.clouds.endoftheinter.net/showmessages.php?topic=)
{
h2{display:none;}
}

@-moz-document url-prefix(http://boards.endoftheinter.net),
url-prefix("http://archives.endoftheinter.net"),
url-prefix("http://links.endoftheinter.net"),
url-prefix("http://boards.endoftheinter.net"),
url-prefix("https://boards.endoftheinter.net"),
url-prefix("https://archives.endoftheinter.net"),
url-prefix("https://links.endoftheinter.net"),
url-prefix("https://boards.endoftheinter.net"),
url-prefix("http://boards.clouds.endoftheinter.net")
{


/* body {
max-width: 80%;
margin-left:auto!important;
margin-right:auto!important;
} */

}


@-moz-document domain("endoftheinter.net") {
h1 {margin-top:-6px!important;margin-bottom:0px!important;}

div.menubar {display:none!important}
.quickpost-nub {display:none!important;}

a:link,a:visited {text-decoration:none !important}

center dramalinks_ticker {display:none!important;}

}

@-moz-document url-prefix("http://boards.endoftheinter.net/showtopics.php"),
url-prefix("http://endoftheinter.net/inbox"),
url-prefix("https://boards.endoftheinter.net/showtopics.php"),
url-prefix("https://endoftheinter.net/inbox"),
url-prefix("http://boards.clouds.endoftheinter.net")
{
div.infobar {display:none!important;} 
}



@-moz-document url-prefix(http://boards.endoftheinter.net/showtopics.php), url-prefix(http://boards.endoftheinter.net/search.php), url-prefix(http://archives.endoftheinter.net/search.php), url-prefix(http://boards.endoftheinter.net/history.php), url-prefix(http://boards.endoftheinter.net/showfavorites.php),
url-prefix(http://links.endoftheinter.net/),
url-prefix(http://endoftheinter.net/main.php), 
url-prefix(http://endoftheinter.net/priv.php),
url-prefix(http://endoftheinter.net/inbox),
url-prefix(http://boards.endoftheinter.net), 
url-prefix(http://boards.endoftheinter.net/search.php), 
url-prefix(http://archives.endoftheinter.net/search.php), 
url-prefix(http://boards.endoftheinter.net/history.php), 
url-prefix(http://boards.endoftheinter.net/showfavorites.php),
url-prefix(http://links.endoftheinter.net/),
url-prefix(http://endoftheinter.net/main.php), 
url-prefix(http://endoftheinter.net/priv.php),
url-prefix(http://endoftheinter.net/inbox) {

a:hover {color:#666!important; text-decoration:none!important;}
a:visited:hover {color:#36c!important; text-decoration:none!important}

.grid {margin-left:auto!important;margin-right:auto!important; border-radius: 1px !important;}

}




@-moz-document url-prefix(http://boards.endoftheinter.net/), 
url-prefix(http://archives.endoftheinter.net), 
url-prefix(http://links.endoftheinter.net), 
url-prefix(http://endoftheinter.net/main.php),
url-prefix(http://endoftheinter.net/priv.php),
url-prefix(http://endoftheinter.net/inbox),
url-prefix(http://endoftheinter.net/profile),
url-prefix(http://boards.endoftheinter.net/), 
url-prefix(https://archives.endoftheinter.net), 
url-prefix(https://boards.endoftheinter.net), 
url-prefix(https://links.endoftheinter.net), 
url-prefix(https://endoftheinter.net/main.php),
url-prefix(https://endoftheinter.net/priv.php),
url-prefix(https://endoftheinter.net/inbox),
url-prefix(https://endoftheinter.net/profile),
url-prefix("http://boards.clouds.endoftheinter.net")
{

/* topic tags */
div.fr a {color: #AAA !important; 
font: .9em Consolas; 
letter-spacing: -0.02em; 
text-transform: uppercase; 
vertical-align: middle;
/* font-variant: small-caps; */}
div.fr a:visited {color:#444 !important;}
div.fr a:hover {color:#36c !important;}


/* topic list rows */
table.grid tr td {border:0px solid #bbb!important;}
table.grid tr th {border:0!important;padding:3px!important;
border:0px solid #bbb!important;
border-top-right-radius: 1px !important;
border-top-left-radius: 1px !important;}
table.grid {box-shadow:0px 4px 10px 0px #111!important;margin-top:2px!important;
border-top-left-radius: 1px !important;
border-top-right-radius: 1px !important;}

/* cells on topic list 
table.grid tr td {padding:5px 5px !important;}
*/

.userpic {border:0px solid #f00!important;}
.userpic-holder {padding:1px 4px 2px;}

div.userbar, div.infobar {margin:5px 0!important;padding:4px!important;box-shadow:0px 4px 10px 0px #111 !important; border-radius: 1px !important;}
div.userbar a:link, div.userbar a:visited {text-decoration:none !important; color:#EEE! important;}
div.userbar a:hover, div.userbar a:visited:hover{color: #FFF !important; }

table tr td {padding-top:3px!important;padding-left:5px!important;}


/***** POSTS *****/
.message-top {padding:2px 4px!important;margin-bottom:0px!important;
border-top-left-radius: 1px !important;
border-top-right-radius: 1px !important;}

/* for foxlinks quotes 
.quoted-message {margin:2px 10px!important;}
end foxlinks */

.message-container {margin-top:6px !important; margin-bottom:6px!important;box-shadow:0px 0px 10px 0px #111; border-radius: 0px !important;}

table.message-body td.message {padding:2px 5px!important;border-radius: 1px !important;}

table.message-body td.message a:link {} 
table.message-body td.message a:visited {color:#444!important; text-decoration:none!important;}
table.message-body td.message a:hover {color:#36c!important;}

.quoted-message .message-top {padding:0px 0px!important;}




/* BOTTOM PADDING FOR QUICKPOST
div.infobar#u0_4 {margin-bottom:290px!important;}
*/


}

/* hide last post column in topic list */
@-moz-document url-prefix(http://boards.endoftheinter.net/showtopics.php?board=),
url-prefix(https://boards.endoftheinter.net/showtopics.php?board=),
url-prefix("http://boards.clouds.endoftheinter.net"),
url-prefix("http://boards.endoftheinter.net/topics/") {
table.grid td + td +td + td,
table.grid th + th + th + th {display:none !important;} 
}

/* hide board name in message history */
@-moz-document url-prefix(http://boards.endoftheinter.net/history.php) {
table.grid td:first-child,
table.grid th:first-child {display:none !important;} 
}


/* hide Last Post and Your Last Post in Message History */
@-moz-document url-prefix(http://boards.endoftheinter.net/history.php) {
table.grid td + td +td + td,
table.grid th + th + th + th {display:none}

/* make messages column wider */
table.grid td + td +td,  
table.grid th + th + th {width:15% !important;}  
}


@-moz-document url-prefix(http://boards.endoftheinter.net/showmessages.php?), 
url-prefix(http://archives.endoftheinter.net/showmessages.php?),
url-prefix(https://boards.endoftheinter.net/showmessages.php?), 
url-prefix(https://archives.endoftheinter.net/showmessages.php?) {

/* bottom page numbers */

div.infobar#u0_3 a:link, div.infobar#u0_3 a:visited {color:#AAA!important; text-decoration:none!important;}
div.infobar#u0_3 a:hover, div.infobar#u0_3 a:visited:hover {color:#FFF!important; text-decoration:none!important;}

}


/* LINKS IN TOPICS */
@-moz-document url-prefix(http://boards.endoftheinter.net/showmessages.php?board=42&topic=), url-prefix(http://archives.endoftheinter.net/showmessages.php?board=42&topic=),
url-prefix(https://boards.endoftheinter.net/showmessages.php?board=42&topic=), url-prefix(https://archives.endoftheinter.net/showmessages.php?board=42&topic=) {


.message-top a:link {text-decoration:none!important;}
.message-top a:visited {color:#444!important;}
.message-top a:hover, a:visited:hover {color:#EEE!important;}

}




/* THIN */

@-moz-document 
domain(boards.endoftheinter.net),
domain(archives.endoftheinter.net),
url-prefix(http://endoftheinter.net/profile.php),
url-prefix(https://endoftheinter.net/profile.php),
url-prefix("http://boards.clouds.endoftheinter.net")
{
  body{max-width: 80%; margin-left:auto!important; margin-right:auto!important;} 
/* tbody tr td:nth-child(4), */

table td:nth-child(1) th:nth-child(4) {display:none !important;}
table td:nth-child(1) td:nth-child(4) {display:none !important;}

}

@-moz-document
domain(links.endoftheinter.net),
url(http://endoftheinter.net/main.php) {
 body{max-width: 80%; margin-left:auto!important; margin-right:auto!important;} 
/* tbody tr td:nth-child(4), */

table td:nth-child(1) th:nth-child(4) {display:none !important;}
table td:nth-child(1) td:nth-child(4) {display:none !important;}

}















/* BACKGROUNDS */


@-moz-document 
domain(endoftheinter.net),
domain(boards.endoftheinter.net),
domain(archives.endoftheinter.net),
domain(links.endoftheinter.net)
{
body {background-color: #CFCFCF !important; background:url("http://i.imgur.com/8PsRG.jpg")  top right fixed no-repeat !important;}
}

@-moz-document
url-prefix(http://links.endoftheinter.net/linkme.php)
{body {background:url("http://i.imgur.com/8PsRG.jpg") top right fixed no-repeat !important;}
}



@-moz-document 
url(http://endoftheinter.net/),
url(https://endoftheinter.net/),
url-prefix(https://endoftheinter.net/index.php?r=),
url-prefix(https://endoftheinter.net/?r=)
{

body {background:url("http://i.imgur.com/UYetA.jpg") top center fixed no-repeat !important;}

form {
  padding: 20px 40px !important;
  bottom: 50px !important;
  right: 70px !important;

background: rgba(0, 0, 0, 0.6) !important;
-moz-transition: background 600ms;
border-radius: 10px !important;
box-shadow: -5px 5px 25px 0 rgba(0,0,0,0.8) !important;
}

input {
padding: 5px 8px !important;
margin: 4px 0px !important;
text-align:center !important;
border-radius:3px !important;
background: rgba(0, 0, 0, 0.4) !important;
color: #BBB;
width: 6em !important;
/* border: none !important; BROKEN? */

}


span, input.submit {display: none !important;}


form:hover, input:hover { 
background: rgba(0, 0, 0, 0.7) !important;
-moz-transition: background 300ms !important;
color: #FFF !important;
}


}
