css:
@namespace url(http://www.w3.org/1999/xhtml);

/*--------------------------------------*/
/*     C S S   b y   H i r u m a n      */
/*--------------------------------------*/

@-moz-document url-prefix(http://userstyles.org), url-prefix("https://userstyles.org") {
body{
margin: 0 30px!important;
font-family: Myriad Pro, Helvetica sans-serif!important;
background-color:#111!important;
color:#bbb!important;
}
input, select{font-family:Segoe UI!important;}
h1{
color:#000!important;
font-size:50px!important;
font-family:arial!important;
letter-spacing:-2px!important;
background:-moz-linear-gradient(top, #fff, #ccc)!important;
text-shadow:0 1px 0 #fff!important;
text-align:center!important;
}
h2{color:#888!important;}
a{color:#aaa!important;text-decoration:none!important;}
a:hover{color:#fff!important;}
form{color:#666!important;}
#breadcrumbs{padding:10px!important}
ul,#style-list{color:#999!important;}
.prev_page, .next_page{color:#bbb!important;}
.pagination,.current,.disabled{color:#333!important;}
#install{background-color:#222!important;border-color:#555!important;background-image:-moz-linear-gradient(top, #444, #000)!important;-moz-box-shadow:0 3px 15px #000;}
pre{border:1px solid #555!important;background-color:#000!important;color:#ddd!important;}
code{font-family:Andale Mono, Monaco!important;}
select{border:1px solid #fff;}
option{padding:2px!important;}
textarea{-moz-appearance:none!important;background-color:#222!important;border:1px solid #000!important;padding:5px!important;color:#ddd!important;font-family: Monaco, Andale Mono, Consolas, Courier New!important;font-size:13px!important;}
}
