css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("groups.google.com") {

iframe body > table[id="t"][width="230"]  /* zap the ads frame  */
   {
       background-color : #000;
       visibility : hidden;
   }


body
{    background-color : #000   !Important;
     color            : #686868 !Important;
}

.lsh[bgcolor="#fff4c2"]  { background-color : rgb(0,23,0); }

td[width="5"] + td[align="left"][width="100%"] > span.fontsize5
        {
           font-size : 25pt   ;
           color     : #785838 !Important;
        }

div.minmaxwie       {  padding : 3px; }
div.minmaxwie       {  background-color : #020202 !important; }
div.minmaxwie > *   {  background-color : #020202 !important; }
div.minmaxwie > div {
                      border-top    : 1px solid #0C0B1C !important;
                      border-bottom : 1px solid #0C0B1C !important;
                    }
div.minmaxwie > div > div > table *
                    {  background-color : inherit; }
div.minmaxwie > div > div { padding : 4px; background-color : #0C0B1C; }

div.minmaxwie a:link  ,  div.minmaxwie a:visited
              {  color : #B5B5FF !Important;
                 text-decoration : none; }
div.minmaxwie a:hover
              {  color : #60AEEF;
                 text-decoration : underline;
              }

div.minmaxwie
  div
    div nobr > b
      {
           color : #49B89C !Important;
      }


/* Zap adframe */
iframe[name="google_ads_frame"][id="google_ads_frame"]
        {
           display : none ;
        }

/* Indexes */
table[cellpadding="0"][cellspacing="0"] a
        {
           color : #EDB090 !Important;
           text-decoration : none !Important;
        }
table[cellpadding="0"][cellspacing="0"] a font
        {
           display : inline !Important;
           font-size : 188% !Important;
        }

table[cellpadding="0"][cellspacing="0"] td font[size="-1"]
        {
           display : inline !Important;
           color : dodgerblue !Important;
           font-size : 10pt !Important;
        }
td[width="100%"] > a:link
        {
           color : red;
           font-style : oblique;
        }

/* Nav column */

table.cv tr.sel td.seltd
    {     background-color : #fff4c2 !Important;
          border : thin double #08b9f9 !Important;
    }
table.cv tr.sel td.seltd div.lnavim  a
    {
          font-size : 102% !Important;
          color     : #0000FF !Important;
    }
table.cv tr.sel td.seltd div.lnavim  a:before
    {
          font-size : 63% !Important;
          content : 'currently reading  ';
          color   : black !Important;
          text-decoration : none !Important;
    }


/* Reading threads view */

td[width="5"] + td[align="left"][width="100%"] > span.fontsize5 > b
        {
           font-size : 25pt;
           font-weight : normal;
           color     : #785838 !Important;
        }
td.thread_star > a:before
        {
    content : "Watch Topic";
    color   : #ADAD00 !Important;
    font    : 9pt Tahoma,Helvetica,sans-serif;
    text-align : center;
        }

div.mbody font.fixed_width
        {  font-family : Dina, Consolas, ProggyCleanTT, monospace;
           font-size   : 10pt;
           line-height : 108%;
           color       : #AECEBE !Important;
        }
div.mbody p a         { color : #A9F209; }
div.mbody p a:link    { color : #A9F209; }
div.mbody p a:visited { color : #898203; }

div.mbody { min-width  : 50em; }

font.fixed_width a div {
           color : #9B89BF !Important;
        }

font.fixed_width a div.sqt {
           max-width     : 30ex;
           padding-left  : 3px;
           border  : thin solid #0000FF;
           text-decoration : none;
           color   : #BB89FF !Important;
           background-color : #101010 !Important;
        }

font.fixed_width a div.hqt {
           max-width   : 30ex;
           padding-left : 3px;
           border  : thin solid #0000FF;
           text-decoration : none;
           color   : #BB89FF !Important;
        }

div.mbody div.qt {
           max-width : 47em           ;
           border: 1px dotted #BB89FF ; /* #B3F300 */
           border-right  : none       ;
           padding       : 2ex 0ex 0ex 0.5ex  ;
           margin-bottom : 0.9em      ;
           margin-top    : 0em        ;
           white-space   : nowrap ;
        }


a.qt:visited { text-decoration : none; }
a.qt:link    { text-decoration : none; }
div.mbody div.qt
        {
          background-color : #101010 !Important;
              /* font-style : oblique; */
          color            : #9B89BF !important;
        }
/*
div.mbody div.qt font.fixed_width br + br + br
        {
           display : none;
        }
*/

}
