css:
@-moz-document domain('tumblr.com'), url-prefix('http://www.tumblr.com/') , url-prefix("https://www.tumblr.com/") {















#dashboard_index{background:#2C4762 url(http://i213.photobucket.com/albums/cc100/Chante_n_Berries/ist2_5599211-floral-patterns-on-ott.jpg) repeat !important;}























#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color:#5B5563!important;}















ol#posts li.notification{background-color: #456179 !important;







border-bottom: 1px solid #4C97AB !important;







color: #A8BCCB !important;}















#left_column .dashboard_options_form {







   background: url('http://assets.tumblr.com/images/white_20.png')#514757;







}















#right_column a#dashboard_switch_blog_menu_current:hover {







   background: #514757;







}

#right_column #dashboard_switch_blog_arrow_current{



    display:none !important;



}



















#nav .nav_item.active{







   background-color:#514757







}















#left_column #posts .post .permalink{







   background-color:#615569;







}















#nav .nav_item.active, #nav .nav_item .nav_menu, #nav .nav_item .nav_item_nipple .nav_item_nipple_pixel{







   background-color:#514757;







}















}

@-moz-document url-prefix('http://www.tumblr.com/reblog/'), url-prefix('https://www.tumblr.com/reblog/')   {







#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {



    padding: 40px 0;



  }







}







@-moz-document url-prefix('http://www.tumblr.com/directory/'), url-prefix('https://www.tumblr.com/directory/')   {







#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {



    padding: 40px 0;



  }







}







@-moz-document url-prefix('http://www.tumblr.com/reblog/'), url-prefix('https://www.tumblr.com/reblog/')   {







#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {



    padding: 40px 0;



  }











}



@-moz-document url-prefix('http://www.tumblr.com/messages'), url-prefix('https://www.tumblr.com/messages')   {







  body {



    background: url('http://i213.photobucket.com/albums/cc100/Chante_n_Berries/ist2_5599211-floral-patterns-on-ott.jpg') repeat !important;}

}

#right_column #dashboard_controls_radar_media {



    background:none !important;



  }

  #dashboard_index #next_page_link, #previous_page_link {



    background: none #FFFFFF !important;



  }







  #dashboard_index #next_page_link, #previous_page_link a {



    color: #555555 !important;



  }



#right_column .dashboard_nav_item {







    background: none;







    background-attachment: initial;







    background-position-x: 50%;







    background-position-y: 100%;







    background-origin: initial;







    background-clip: initial;







    background-color: initial;







}















#right_column .dashboard_nav_item #hide_radar {







background-image: none;







background-repeat-x: no-repeat;







background-repeat-y: repeat;







background-attachment: initial;







background-position-x: 0%;







background-position-y: 0%;







background-origin: initial;







background-clip: initial;







background-color: transparent;







}
