@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mail.google.com") {

/** Common Settings **/
.yE, /* Inbox */
.gW /* Mail */ {
  width: 0px !important;
  height: 0px !important;
  padding: 16px 0 0 16px !important;
}
.yE { margin: 1px 0 -1px 0 !important }


/* Archives - RAR ZIP 7z*/
.yE[title$="rar"], .gW[title$="rar"],
.yE[title$="RAR"], .gW[title$="RAR"],
.yE[title$="zip"], .gW[title$="zip"],
.yE[title$="ZIP"], .gW[title$="ZIP"],
.yE[title$="7z"], .gW[title$="7z"],
.yE[title$="7Z"], .gW[title$="7Z"] {
  background: url(data:image/png;base64,) center !important;
}

/* Bitmap Image */
.yE[title$="bmp"], .gW[title$="bmp"],
.yE[title$="BMP"], .gW[title$="BMP"] {
  background: url(data:image/png;base64,) center !important;
}

/* JPEG Image */
.yE[title$="jpg"], .gW[title$="jpg"],
.yE[title$="JPG"], .gW[title$="JPG"],
.yE[title$="jpeg"], .gW[title$="jpeg"],
.yE[title$="JPEG"], .gW[title$="JPEG"] {
  background: url(data:image/png;base64,) center !important;
}

/* PNG Image */
