http://creativecommons.org/licenses/by-nc-sa/2.5/ch/
*/

/* code start */

/* darker background */
html.backgroundfill
	{
	background-color:#333 !important;
	}

/* center content */
div#wrapper
	{
	width:994px !important;
	}

/* black text for better contrast */
body, .story .story_head h1, .story .story_head h3,
.teaser a, .teaser_title h2 a,

div.entry p,
div[id^="pollbox"],
div#actionticker_topelement, div#actionticker_list,
div.top_comment
	{
	color:black !important;
	}

/* white text on dark backround boxes */
div.leserreporter_endblock,
div.leserreporter_endblock h2 a,
div.leserreporter_endblock div.teaser_text a,
div.teaser.panelbox.gallery div.caption
	{
	color: white !important;
	}

/* hide ad-scraper and billboards */
div#monstersky,
div#skyleft,
div#min_billboard,
div[id^="revolver"]
	{
	display:none !important;
	}

/* disable slide in navigation */
div#micronavi
	{
	display:none !important;
	}

/* hide sammelsurium on frontpage */
div.sammelsurium
	{
	display:none;
	}

/* hide headers images */
div#content_header
	{
	display:none;
	}

/* hide "your story" text on top and leserreporter box */
div#story_content > div.feedback_story,
img#teaser_leserreporter
	{
	display:none;
	}

/* hide social crap */
div.social_links_option,
div.recommend
	{
	display:none !important;
	}

/* hide promo boxes */
div#homegate-teaser-small,
div.sideline iframe,
div.teaser.promotion,
div#eCommerce
	{
	display:none !important;
	}

/* hide content after comments */
div.story_end
	{
	display:none;
	}

/* hide footer */
div#footer
	{
	display:none;
	}

/* bottom margin (replace footer) */
div#wrapper
	{
	border-bottom: 200px solid white;
	}

}
