css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("filelist.ro") {



/*



Filelist Theme by iOanE Design

Check website : www.ioane.info



*/



img[src$="pic/logo.png"] {

  width: 0px !important;

  height: 201px !important;

  padding-left: 760px !important;

  background-image: url("http://i257.photobucket.com/albums/hh211/ioaneee/logo-1.png") !important; }



body {

  background-color: #FFF;

  background-image: url("http://img820.imageshack.us/img820/8562/backme.png");

  background-repeat: repeat-x;

  font-family: Tahoma, Sans !important;

  font-size: 1pt !important;

  text-decoration: none !important;

  color: #00000;

}



table,table.main, table.mainouter, table.mainouter1, mainouter, mainouter1{

  background-color: #FFF ;

  border-color: #FFF ;

  border: none ;

}



td {

	font-size: 8pt;

	border: solid #09F 1px;

}



td.colhead {

  font-weight: bold;

  color: #000000;

}



td.outer, td.tablea {

     border: 0px;

}



A:link {  color: #000000; text-decoration: none}

A:visited {  color: #000000; text-decoration: none}

A:active {  color: #000000; text-decoration: none}

A:hover {  color: #000000;}



}
