css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("boingboing.net") {

  #adunit-leaderboard,
  #sidebar,
  #footer,
  #printposts { display: none !important }

  #navigation {
    width: 822px !important;
    margin: auto !important;
  }

  #header-subscribe-intro {
    position: absolute !important;
    top: 80px !important;
  }

  #header-subscribe-rss {
    position: absolute !important;
    top: 100px !important;
  }

  #header-subscribe-itunes {
    position: absolute !important;
    top: 120px !important;
  }

  #section-logo {
    margin-bottom: 20px !important;
    margin-top: 10px !important;
  }

  #search {
    position: absolute !important;
    margin: auto !important;
    top: 90px !important;
    padding-left: 479px !important;
  }

  #navigation-site {
    position: absolute !important;
    margin: auto !important;
    padding-left: 680px !important;
    top: 40px !important;
  }

  #content {width: 780px !important;}

  #wrap {width: 780px !important;}

  h3 > a {
    color: black !important;
    font-size: 14pt !important;
  }
}
