css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("reddit.com") {

.flat-list .state-button.hide-button a {
font-family: Helvetica, Arial; 
font-size: 2em !important; 
background-color: #cee3f8 !important; 
color: white !important; 
-moz-border-radius: 2px !important; 
-webkit-border-radius: 2px !important; 
padding: 0px 5px !important; 
margin-top: 3px !important; 
text-transform: uppercase; 
}

.flat-list .state-button.hide-button a:hover {
text-decoration: none !important;
background-color: #7db7f1 !important; 
}

.midcol .arrow.up {
background-image:url(data:image/png;base64,) !important;
background-position: left top !important;
}

.midcol .arrow.down {
background-image:url(data:image/png;base64,) !important;
background-position: left top !important;
}

.midcol .arrow.downmod {
background-image:url(data:image/png;base64,) !important;
background-position: left top !important;
}

.midcol .arrow.upmod {
background-image:url(data:image/png;base64,) !important;
background-position: left top !important;
}

.midcol .arrow {
width: 25px !important; 
height: 25px !important;
}

.link .score.likes   { 
color:#92ce39 !important; 
} 

.link .score.dislikes { 
color:#d92525 !important; 
}

.gadget .midcol {
width: 25px !important;
} 

.comment .midcol {
width: 25px !important;
}


}
