css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {
	.transwootTriger{
		position: absolute;
		bottom: -3px;
		right: -3px;
		text-align: right;
		font-size: .8em;
		font-weight: bold;
		padding: 3px 5px;
		background-color: #92a399 !important;
		margin: 0;
		width: 170px !important;
	}
	.transwootTriger span{
		height: auto;
		margin: 0;
		padding: 0;
		display: block;
		text-align: center;
		width: 100% !important;
		height: 100% !important;
	}
	.transwootTriger div{
		background-color: #92a399 !important;
		color: #393c4e !important;
		width: 180px !important;
		position: relative;
		left: 0;
	}
	.transwootTriger div ul a{color: #393c4e !important;}
	.transwootTriger br{display: none;}
}
