css:
/*Title "FTO - noscroll"

Created by Skasi
Version of 26. Julp 2011*/


@-moz-document url("http://www.faerytaleonline.com/main.php") {

*{background-image:none !important; margin:0 !important; }
*[width="766"]{width:500px !important;}

html>body>table{position:fixed; left:0; height:100% !important; padding-top:20px; padding-bottom:20px; width:600px !important; overflow:auto !important;}

/*html>body>table>tbody>tr>td{position:absolute; top:20px; bottom:20px; height:auto !important; overflow:auto !important;}*/

table[width="766"][height="349"]{height:auto !important;}
table[width="650"]{height:auto !important;}

td[width="350"]{width:0 !important; height:0 !important;}
td[width="350"]>table>tbody>tr>td[valign="top"]>font, input.btn[value="Send"]{display:none;}


/* Top (Avatar information, skills, description, travel, etc.) */
td[width="229"]{height:auto !important;}
td[width="229"]>table{width:260px !important;}
td[width="229"]>table[style="margin-top:18;margin-left:15; border: 0px solid #534D41"]{float:right; width:360px !important;}

td[width="229"]>div{width:260px !important;}

#insert_response_equip{width:100% !important;}

#avatar_description{height:80px; width:100% !important;}


/* Middle (boxes - Inventory, Buildings, People, Creatures, etc.) */

td[style="images/fon02.gif"]>table[height="321"]{height:auto !important;}

td[style="images/fon01.gif"]>table[height="286"]{height:auto !important;}
td[style="images/fon01.gif"]>table>tbody>tr{height:50% !important;}

div[style="border: 1px solid rgb(83, 77, 65); width: 160px; height: 200px; overflow: auto; margin-top: 0pt;"], div[style="border: 1px solid rgb(83, 77, 65); width: 150px; height: 190px; overflow: auto; margin-top: 13px; background-color: black;"]
{}


/* Bottom (links to Portal, Logout, ..) */

table[height="37"]{width:100% !important;}


/* Remaining playtime */
body>table>tbody>tr>td:not([background]){
width:auto!important; z-index:50; position:absolute; left:0;bottom:0; background-color:#000; padding:1px 1em;
}


/* Chat */
#insert_response{
position:fixed !important;
height:auto !important;
width:auto !important;
right:0;
left:650px;
top:20px;
bottom:20px;
}

/* Tips - mouseovers */
.tip, .tip_description{position:fixed!important;
left:650px !important; right:0px !important;
top:20px !important; bottom:20px;
height:auto !important; width:auto !important; max-width:none !important;
border:none !important;}

.tip>table {vertical-align:50% !important;}

}
