css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("wykop.pl"),
domain("www.wykop.pl"),
url("http://www.wykop.pl/")
{

#header-container .wrapper ul#quick-login > li > a > span {
color: #655B84 !important;
}

body div.illustration{
background: url("http://img337.imageshack.us/img337/989/bgillustrationt1.png") no-repeat scroll center 164px transparent !important;
}

body.full-size div.illustration{
background: url("http://img201.imageshack.us/img201/4579/bgillustrationt2.png") no-repeat scroll center 164px transparent !important;
}



#footer-container h3{
color: #DAE88F !important;
}

body {
background:url("http://img178.imageshack.us/img178/7071/bgt.png") repeat-x scroll left top #261d46 !important;}

#header-container .wrapper > form {
top:90px !important;}

#header-container .wrapper > form fieldset input[type="submit"]{
width: 30px !important;
border-right: 1px solid #515151 !important;
}

#header-container .wrapper > form fieldset input[type="text"]{
border-color: white !important;
}

#header-container .wrapper > form fieldset{
padding-left: 0 !important;
border-left: 1px solid #515151 !important;
border-bottom: 1px solid #b1b1b1 !important;
border-top: 1px solid #3c3c3c !important;
}

#body-container .wrapper ul.comments-list > li blockquote .inner .inner-2nd h5.head small {
color:#ABB389 !important;
margin:0 0 0 7px !important; 
}

.tabs li a span em{
background: #c0d37c !important;
color: white !important;
font-weight: bold !important;
}

.tabs li a:hover span em{
background: #a9ba6d !important;
}

.tabs li a span em strong{
color: #4f4f4f !important;
}

.tabs li.selected a span em{
background: white !important;
color: #4f4f4f !important;
font-weight: normal !important;
}

.tabs li.selected a span em strong{
color: #F99762 !important;
font-weight: bold !important;
}

.wrapper #content ul.tabs {
background: #DAE88F !important;
-moz-border-radius: 5px 5px 0px 0px!important;
}

#body-container .wrapper #content-entry blockquote.entry-details div.entry-count > a > strong {
color:gray !important;}

#body-container .wrapper #content-entry blockquote.entry-details div.entry-count > a:hover > strong {
color:gainsboro !important;}

#footer-container .wrapper {
background-color:#1a1a1a !important;
background-position:-5px 0;
color: #FFFFFF;
}

#footer-container .wrapper .inner ul {
background:none repeat scroll 0 0 transparent !important;
}

#footer-container .wrapper {
-moz-border-radius:0 0 15px 15px !important;
margin-bottom:20px !important;
padding-bottom:20px !important;
}

#footer-container .wrapper, #footer-container .wrapper .inner {
background-image: none !important;
background-repeat: no-repeat;
}

#body-container .wrapper ul.comments-list > li.subcomment blockquote .inner .inner-2nd h5.head {
margin:0 0 !important;
-moz-border-radius: 2px;
}

#body-container .wrapper ul.comments-list.entry > li blockquote .inner .inner-2nd h5.head {
margin:0 0 !important;
-moz-border-radius: 2px;
}

#body-container .wrapper ul.comments-list > li blockquote .inner .inner-2nd h5.head {
background:none repeat scroll 0 0 #F0F5CD !important;
}

#body-container .wrapper div.add-comment.user-active fieldset div.bottom {
background:none repeat scroll 0 0 #C5C5C5 !important;
border-bottom: 1px solid #8e8e8e !important;
}

#body-container .wrapper div.add-comment.user-active fieldset div.top {
background:none repeat scroll 0 0 #C5C5C5 !important;
border-top: 1px solid #8e8e8e !important;
}

#body-container .wrapper div.add-comment.user-active fieldset div.inner {
background:none repeat scroll 0 0 #C5C5C5 !important;
border-left: 1px solid #8e8e8e !important;
border-right: 1px solid #8e8e8e !important;}

#body-container .wrapper #user-info #entry-info dl > dd {
color: #B8C37C !important;
}

#body-container .wrapper #user-info #entry-info dl > dd h6 small {
color: #9da66a !important;
}

#body-container .wrapper #content-entry blockquote.entry-details h4 a{
color: #655b84 !important;
}

#body-container .wrapper #content-entry blockquote.entry-details h4 a:hover{
color: #8C86A5 !important;
}

#header-container .wrapper ul#main-menu li#sub-list > ul > li.options div,
#header-container .wrapper ul#main-menu li#sub-list > ul > li.options,
#header-container .wrapper ul#main-menu li#sub-list > ul > li.rss div, #header-container .wrapper ul#main-menu li#sub-list > ul > li.rss {
border-right: none !important;
}

#header-container .wrapper ul#main-menu li#sub-list > ul > li {
border-right:1px solid #C4D181 !important;
}

#header-container .wrapper ul#main-menu li#sub-list > ul > li > div {
border-right:1px solid #EEF7A6 !important;
}

#body-container .wrapper ul.entry-list-blocks, #body-container .wrapper #sidebar ul.entry-list-notification{
border-top: none !important;
border-left: 1px solid gainsboro !important;
border-right: 1px solid gainsboro !important;
border-bottom: 1px solid gainsboro !important;
}

#body-container .wrapper ul.entry-list-blocks > li p big a.link{
color: #655B84 !important;
}

#body-container .wrapper ul.entry-list-blocks > li p big a:hover.link{
color: #8c86a5 !important;
}

#body-container .wrapper ul.entry-list-blocks > li p span, #body-container .wrapper #content ol.entry > li blockquote a.vote span{
color: #d75252 !important;
}

#body-container .wrapper #content-entry blockquote.entry-details div.entry-count > a {
color:#D75252 !important;
}

#sidebar .box-rounded.gradient, .box-rounded.gradient .inner{
background-image: none !important;
background-color: #414141 !important;
}


#header-container .wrapper ul#main-menu li#main-list > ul > li a:hover span em {
background-color: #313131 !important;
border-top: 1px solid #1d1d1d !important;
}

#header-container .wrapper ul#main-menu li#main-list > ul > li a.selected span em{
background-color: #DAE88F !important;
color: white;
text-shadow: 0 1px 1px #727a4a;
border-top: 1px solid white !important;
}

#header-container .wrapper ul#main-menu li#main-list > ul > li a span em {
text-shadow:0 1px 1px black;
}

ul#main-menu li#main-list ul li span.before {
color:gray !important;
}

#header-container #main-menu #sub-list a{
color: #263849 !important;
}

#header-container .wrapper ul#main-menu li#sub-list {
background-color:#DAE88F !important;
border-bottom:1px solid #dbdbdb !important;
border-top:1px solid #FFFFFF;
}

#body-container .wrapper #content ol.entry > li blockquote h4 > span{
color: #B8C37C !important;
}

ol.pager li a span em {
color:#373926 !important;
}

ol.pager li a.selected span em {
color:white !important;
}

#header-container .wrapper div.user-panel ul.slide-list > li > a span {
background-image: none !important;
}



#body-container .wrapper #content ol.entry > li blockquote h4 > a:hover > em {
color:#8C86A5 !important;
}

#body-container .wrapper #content ol.entry > li blockquote h4 > a > em {
color:#655b84 !important;
}

#body-container .wrapper #content ol.entry > li blockquote h4 > a {
color:#DAE88F !important;
}

#body-container .wrapper #content ol.entry > li blockquote h4 a:visited em {
color:#B8C37C !important;
}

#header-container .wrapper h2 a span strong, #header-container .wrapper h2 a, #header-container .wrapper h2 a span, #footer-container .wrapper .inner ul li a, .tabs li a, .tabs li a span, #header-container .wrapper ul#main-menu li#sub-list > ul > li ul.slide-list > li > a span em, .box-rounded.gradient .top, .box-rounded.gradient .base, .box-rounded.gradient .top span, .box-rounded.gradient .base span, #header-container .wrapper ul#main-menu, div.groups-browser > p.desc, #header-container .wrapper ul#main-menu li#sub-list > ul > li.options div, 
#header-container .wrapper ul#main-menu li#main-list > ul > li.slide-list a span em, #body-container .wrapper #content ol.entry > li blockquote > div > ul.options > li.comments a,
#header-container .wrapper ul#main-menu li#main-list, #header-container .wrapper ul#main-menu li#main-list > ul, #header-container .wrapper > form fieldset,
#header-container .wrapper ul#main-menu li#main-list > ul > li a, #header-container .wrapper ul#main-menu li#main-list > ul > li a span, a.rss-icon,
ol.pager li a, ol.pager li a span, ol.pager li a span em{
background-image : url(http://img843.imageshack.us/img843/1002/corebuttonsnavit.png) !important;
}


#header-container .wrapper > form fieldset input[type="submit"], #body-container .wrapper ul.comments-list > li blockquote div.top span:first-child, #body-container .wrapper ul.comments-list > li blockquote div.bottom span:first-child, .wizard .top span:first-child, #body-container .wrapper div.add-comment.user-active.small fieldset span, #body-container .wrapper ul.entry-list-blocks > li p span, div.entry-count a.jvote, ol.entry li blockquote a.vote span.votecount {background-image: url(http://img826.imageshack.us/img826/9905/coret.png) !important;}

}
